INSERT INTO `dx` VALUES (1, '系统公告', '<P>&nbsp;&nbsp;&nbsp; 欢迎大家登陆我站，我站主要是为广大朋友精心制作的一个系统，希望大家能够在我站获得一个好心情，谢谢！</P><P>&nbsp;&nbsp;&nbsp; 自强不息，海纳百川，努力学习！</P>');
INSERT INTO `dx` VALUES (2, '系统简介', '<p>企业宗旨：诚信为本，稳健经营</p>');
INSERT INTO `dx` VALUES (3, '关于我们', '&nbsp; &nbsp; 忠信仁义，以人为本。 <br />  忠：即忠诚;信：即诚信，做企业要立足根本道德、信义，要诚实、讲信用。 <br />  ');
INSERT INTO `dx` VALUES (4, '联系方式', '联系人：xxxxxxxx<br />  电话：0000-0000000<br />  手机：010000000000<br />  邮件：xxxxxxxx@163.com<br />  网址：http://www.xxxx.com<br />');
INSERT INTO `dx` VALUES (5, '公告信息', '<p style=\"text-align:left;\">\r\n	<span style=\"font-size:24px;\"> 当前，国内、省内多地出现新冠肺炎本土确诊病例和无症状感染者，奥密克戎毒株传染性更强，留给我们流调的时间更短，全市疫情防控形势异常严峻。为切实降低新冠疫情传播风险，巩固我市防疫成果，现通告如下：</span><span style=\"font-size:24px;\"></span> \r\n</p>\r\n<p class=\"p\" style=\"margin-left:0.0000pt;text-indent:27.0000pt;\">\r\n	<span style=\"font-size:24px;\"><span style=\"font-size:24px;\">一、强化重点管控</span><strong><span style=\"font-size:18px;\">。</span></strong></span><span style=\"font-size:24px;\">在疫情防控工作中，各镇街道和各相关单位要围绕重点群体和重点人员，做好稳控工作，落实防控措施。对拒不执行人民政府在紧急状态下发布的决定、命令者、寻衅滋事者、扰乱防疫秩序者，请立即移交公安机关，依法依规严厉打击。</span> \r\n</p>\r\n<p class=\"p\" style=\"margin-left:0.0000pt;text-indent:27.0000pt;\">\r\n	<br />\r\n</p>\r\n<p class=\"p\" style=\"margin-left:0.0000pt;text-indent:27.0000pt;\">\r\n	<span style=\"font-size:24px;\"> </span> \r\n</p>\r\n<p class=\"p\" style=\"margin-left:0.0000pt;text-indent:27.0000pt;\">\r\n	<span style=\"font-size:24px;\">二、严守入禹返禹报备制度。对于入省返省前</span><span style=\"font-size:24px;\">3</span><span style=\"font-size:24px;\">日内，不主动报备、隐瞒活动轨迹、不配合排查、不落实防疫措施，造成疫情传播风险，以及造谣传谣，引起社会恐慌的，公安机关将严肃追究其法律责任。</span> \r\n</p>\r\n<p class=\"p\" style=\"margin-left:0.0000pt;text-indent:27.0000pt;\">\r\n	<br />\r\n</p>\r\n<p class=\"p\" style=\"margin-left:0.0000pt;text-indent:27.0000pt;\">\r\n	<br />\r\n</p>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"p\" style=\"margin-left:0.0000pt;text-indent:27.0000pt;\">\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>');
