INSERT INTO `xinwentongzhi` VALUES (90, '感染性腹泻症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>感染性腹泻症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">腹泻，大便每日</span><span style=\"font-size:18px;\">≥</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">次，粪便的性状异常，可为稀便、水样便， 亦可为粘液便、脓血便可伴有恶心、呕吐、食欲不振、发热及全身不适等，病情严重者，大量丢失水分引起脱水、电解质紊乱甚至休克</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">分泌性腹泻</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">指病原体或其产物作用于肠上皮细胞，引起肠液分泌增多和</span><span style=\"font-size:18px;\">/</span><span style=\"font-size:18px;\">或吸收障碍而导致的腹泻</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">病人多不伴有发热，粪便性状为稀便或水样便，粪便的显微镜检查多无细胞，或可见少许红、白细胞</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">属于此类腹泻的除霍乱外，还有肠产毒性大肠杆菌肠炎、致泻性弧菌肠炎、非</span><span style=\"font-size:18px;\">O1/</span><span style=\"font-size:18px;\">非</span><span style=\"font-size:18px;\">O139</span><span style=\"font-size:18px;\">霍乱弧菌肠炎、轮状病毒肠炎、隐孢子虫肠炎，以及常以食物中毒形式出现的腊样芽胞杆菌腹泻，金黄色葡萄球菌腹泻等</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">炎症性腹泻</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">病原体侵袭上皮细胞，引起炎症而致的腹泻</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">常伴有发热，粪便多为粘液便或粘液血便，粪便的显微镜检查见有较多的红、白细胞</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">属于此类感染性腹泻的除细菌性痢疾外，还有侵袭性大肠杆菌肠炎、肠出血性大肠杆菌肠炎、弯曲菌肠炎、小肠结肠炎耶尔森氏菌肠炎等</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>感染性腹泻预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">健康教育</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">加强以预防肠道传染病为重点的卫生宣传教育，搞好环境卫生，提倡喝开水，不吃生的半生的食品。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">改变习惯</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">改变有些农村人畜共舍的生活习惯。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">3.</span><span style=\"font-size:18px;\">免疫接种</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">本组传染病病原体种类多，尚没有理想的免疫制品。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">4.</span><span style=\"font-size:18px;\">加强饮用水卫生</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">要加快城乡自来水建设及自来水卫生监督管理，在一时达不到要求的地区，必需保护水源，改善饮用水卫生，实行饮用水消毒。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">5.</span><span style=\"font-size:18px;\">抓好饮食卫生</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">加强宣传和严格执行《中华人民共和国食品安全法》，特别要加强对饮食行业、农贸集市、集体食堂等的食品卫生管理。</span> \r\n</p>', 'hsg', 'upload/1650938474802.jpg', 5, '', '2022-04-26 10:01:16');
INSERT INTO `xinwentongzhi` VALUES (91, '手足口病症状的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<strong><span style=\"font-size:24px;\">手足口病症状</span></strong> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">典型症状：</span><span> </span><span style=\"font-size:18px;\">发热，口腔黏膜出现散在疱疹，手、足和臀部出现斑丘疹、疱疹，疱疹周围有炎性红晕，疱内液体较少。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span> </span><span style=\"font-size:18px;\">口唇疱疹</span><span> </span><span style=\"font-size:18px;\">口痛</span><span> </span><span style=\"font-size:18px;\">水泡</span><span> </span><span style=\"font-size:18px;\">丘疹</span><span> </span><span style=\"font-size:18px;\">低烧</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">一、症状</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 手足口病一年四季均可见到，以夏秋季较多，它的潜伏期是</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">—</span><span style=\"font-size:18px;\">8</span><span style=\"font-size:18px;\">日。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">发病初期先有发热、咳嗽流涕和流口水等像上呼吸道感染一样，有的孩子可能有恶心、呕吐等症状以及后手、足的指及趾背部出现椭圆形或梭形的水泡，疱的周围有红晕水疱的液体清亮，水疱的长轴与皮纹是一致的。然后水疱的中心凹陷变黄、干燥、脱掉</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">脱屑</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">另外指、趾端有散在的比较坚硬的淡红色丘疹或者疱疹。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">同时在口腔里，如嘴唇、舌口腔粘膜、齿龈上也有散在的水疱，但口腔里的水疱很快破溃而形成灰白色的小点或灰白色的一层膜其周围有红晕，在灰白色的膜下可以见到点状或片状的糜烂面。手足口病是由肠道感染引起的感染源为疱疹液、咽喉分泌物、粪便污染的手玩具、食具等。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">一般症状表现</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1)</span><span style=\"font-size:18px;\">急性起病，潜伏期</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">天，有低热、全身不适、腹痛等前驱症。口腔粘膜出现散在疼痛性粟粒至绿豆大小水疱，手、足出现斑丘疹、疱疹，初起为斑丘疹，后转变为疱疹，圆形或椭圆形，约</span><span style=\"font-size:18px;\">3-7mm</span><span style=\"font-size:18px;\">如米粒大小，较水痘皮疹为小，质地较硬，周围有红晕，疱内液体较少，在灰白色的膜下可以见到点状或片状的糜烂面。皮疹消退后不留瘢痕或色素沉着，如有继发感染常使皮肤损害加重。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2)</span><span style=\"font-size:18px;\">除手足口外，亦可见于臀部及肛门附近，偶可见于躯干及四肢，数天后干涸、消退，皮疹无瘙痒，无疼痛感。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3)</span><span style=\"font-size:18px;\">个别儿童可出现泛发性丘疹、水疱，伴发无菌性脑膜炎、脑炎、心肌炎等。可伴有咳嗽、流涕、食欲不振、恶心、呕吐、头痛等症状。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4)</span><span style=\"font-size:18px;\">部分病例仅表现为皮疹或疱疹性咽峡炎。全病程约</span><span style=\"font-size:18px;\">5-10</span><span style=\"font-size:18px;\">天，多数可自愈，预后良好，无后遗症。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">重症病例表现</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 少数病例</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">尤其是小于</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">岁者</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">可出现脑炎、脑脊髓炎、脑膜炎、肺水肿、循环衰竭等。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1)</span><span style=\"font-size:18px;\">呼吸系统表现为：呼吸浅促、困难，呼吸节律改变，口唇紫绀，口吐白色、粉红色或血性泡沫液</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">痰</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">，肺部可闻及痰鸣音或湿罗音</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2)</span><span style=\"font-size:18px;\">神经系统表现为：精神差、嗜睡、头痛、呕吐、易惊、肢体抖动、无力或瘫痪</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">查体可见脑膜刺激症、腱反射减弱或消失</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">危重病例可表现为频繁抽搐、昏迷、脑水肿、脑疝</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3)</span><span style=\"font-size:18px;\">循环系统表现为：面色苍白，心率增快或缓慢，脉搏浅速、减弱甚至消失，四肢发凉，指</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">趾</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">发绀，血压升高或下降。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">二、分类</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span> <span> </span><span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">柯萨奇病毒</span><span style=\"font-size:18px;\">A</span><span style=\"font-size:18px;\">型</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">感染儿童多见，成人感染占</span><span style=\"font-size:18px;\">21.7%</span><span style=\"font-size:18px;\">。临床表现除上述外，主要特点为急性发烧、皮疹。脑膜脑炎伴有急性病毒性心肌病。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">柯萨奇病毒</span><span style=\"font-size:18px;\">B</span><span style=\"font-size:18px;\">型</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">发热是婴幼儿</span><span style=\"font-size:18px;\">EV71</span><span style=\"font-size:18px;\">感染的常见临床症状，患者绝大多数是小于</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">个月婴儿。急性呼吸道疾病是</span><span style=\"font-size:18px;\">EV71</span><span style=\"font-size:18px;\">感染的又一常见临床症状，它包括一些常见呼吸道症状，如咽炎、哮喘、细支气管炎和肺炎，发病年龄一般为</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">岁，需要住院治疗。</span><span style=\"font-size:18px;\">EV71</span><span style=\"font-size:18px;\">累及神经系统多发生于</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">岁以下儿童，</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">岁幼儿发病率最高。主要表现为无菌性脑膜炎、脑炎及瘫痪性疾病，多发生于</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">岁以下幼儿，</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">岁以下婴儿发病率最高。临床表现变化多样，病情轻重不一，一般表现为阵挛、呕吐、共济失调、意向性震颤、眼球震颤及情感淡漠等。头颅</span><span style=\"font-size:18px;\">MRI</span><span style=\"font-size:18px;\">及脑电图检查有助于明确疾病的严重性。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">三、诊断</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">根据典型皮疹表现，普通手足口病诊断并不困难。普通病例出现神经系统受累、呼吸和循环功能障碍等表现即可诊断为重症病例。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">对重症病例一定要提高警惕。在流行区域，普通病例具有以下特征时可能在短期内迅速发展为危重病例，应密切观察病情变化，进行必要的辅助检查：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">①年龄</span><span style=\"font-size:18px;\">&lt;3</span><span style=\"font-size:18px;\">岁；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">②持续高热不退；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">③精神差、呕吐、抽搐、肢体抖动或无力；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">④外周血白细胞计数明显增高；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">⑤高血糖。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">有少数患儿皮疹并不明显，但可出现神经系统症状并迅速恶化，诊断困难，确诊靠病原学检查。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<strong><span style=\"font-size:24px;\">手足口病预防</span></strong> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">）父母应该注意</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1. </span><span style=\"font-size:18px;\">饭前便后、外出后要用肥皂或洗手液等给婴幼儿洗手，不要让婴幼儿喝生水、吃生冷食物，避免接触患病婴幼儿</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2. </span><span style=\"font-size:18px;\">接触儿童前、替幼童更换尿布、处理粪便后均要洗手，并妥善处理污物</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3. </span><span style=\"font-size:18px;\">婴幼儿使用的奶瓶、奶嘴使用前后应充分清洗</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4. </span><span style=\"font-size:18px;\">本病流行期间不宜带婴幼儿到人群聚集、空气流通差的公共场所，注意保持家庭环境卫生，居室要经常通风，勤晒衣被</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">5. </span><span style=\"font-size:18px;\">婴幼儿出现相关症状要及时到医疗机构就诊。父母要及时对患儿的衣物进行晾晒或消毒，对患儿粪便及时进行消毒处理</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">轻症患儿不必住院，宜居家治疗、休息，以减少交叉感染。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">）幼儿园及小学等机构的预防措施</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(1)</span><span style=\"font-size:18px;\">流行季节，教室和宿舍等场所要保持良好通风</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(2)</span><span style=\"font-size:18px;\">每日对玩具、个人卫生用具、餐具等物品进行清洗消毒</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(3)</span><span style=\"font-size:18px;\">进行清扫或消毒工作</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">尤其清扫厕所</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">时，工作人员应戴手套。清洗工作结束后应立即洗手</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(4)</span><span style=\"font-size:18px;\">消毒的必备品：碘酊、消毒棉球等。</span> \r\n</p>', 'hsg', 'upload/1650938457744.jpg', 4, '', '2022-04-26 10:00:59');
INSERT INTO `xinwentongzhi` VALUES (92, '包虫病的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong> </strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;font-family:;\"><strong>包虫病症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;font-family:;\">典型症状：</span><span style=\"font-size:18px;font-family:;\"> </span><span style=\"font-size:18px;font-family:;\">包虫病可在人体内数年至数十年不等。临床表现视其寄生部位、囊肿大小以及有无并发症而异。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;font-family:;\">相关症状：</span><span style=\"font-size:18px;font-family:;\"> </span><span style=\"font-size:18px;font-family:;\">头痛</span><span style=\"font-size:18px;font-family:;\"> </span><span style=\"font-size:18px;font-family:;\">肝区痛</span><span style=\"font-size:18px;font-family:;\"> </span><span style=\"font-size:18px;font-family:;\">胸痛</span><span style=\"font-size:18px;font-family:;\"> </span><span style=\"font-size:18px;font-family:;\">疼痛</span><span style=\"font-size:18px;font-family:;\"> </span><span style=\"font-size:18px;font-family:;\">消瘦</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;font-family:;\">一、症状</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;font-family:;\">棘球蚴不断生长，对寄生的器官及邻近组织器官产生挤压，引起组织细胞萎缩、坏死。受累部位有轻微疼痛和坠胀感。如寄生在肝脏可有肝区痛，在肺可有呼吸急促、胸痛等呼吸道刺激症状，累及颅脑可引起癫痫及头痛，呕吐等颅内压升高症状，寄生于骨胳易造成骨折。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;font-family:;\"><strong>包虫病预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;font-family:;\">包虫病为人兽共患疾病，中间宿主包括家畜和野生动物，其预防不仅是生物学范畴内的一个复杂问题，而且也是一个严重的社会问题，应采取综合措施，包括：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:Arial Black;\"> （一）加强流行区犬的处理和管制：牛为预防人体包虫感染的关键性一环，在包虫流行区野犬应一律灭绝，家犬严加限制，对必用的牧羊犬，猎犬或警犬等必须挂牌登记，定期驱绦虫和药物监测应</span><span style=\"font-family:Arial Black;\"></span><span style=\"font-family:Arial Black;\"></span><span style=\"font-family:Arial Black;\">列为常规制度，据新西兰报告重度流行区规定每隔</span></span><span style=\"font-size:18px;font-family:;\">6</span><span style=\"font-size:18px;font-family:;\">周投药驱绦一次，轻度流行区改为</span><span style=\"font-size:18px;font-family:;\">3</span><span style=\"font-size:18px;font-family:;\">个月投药一次。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;font-family:;\">（二）大力开展卫生宣教：宣教方式可多样化，内容要简单通俗易懂，讲求实效，并要充分发动群众，做到家喻户晓，人人皆知。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"></span><span style=\"font-size:18px;\"></span><span style=\"font-size:18px;\"></span><span style=\"font-size:18px;\"></span><span style=\"font-size:18px;\"></span><span style=\"font-size:18px;\"></span><span style=\"font-size:18px;\"></span> \r\n</p>', 'hsg', 'upload/1650938438030.jpg', 2, '', '2022-04-26 10:00:39');
INSERT INTO `xinwentongzhi` VALUES (93, '黑热病的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>黑热病症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">典型症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">不规则的发热，消瘦，肝脾肿大、</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">脾肿大</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">丘疹</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">嗜酸性粒细胞增多</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">牙龈出血</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">一、临床表现</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 潜伏期长短不一，平均</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">月</span><span style=\"font-size:18px;\">(10</span><span style=\"font-size:18px;\">日至</span><span style=\"font-size:18px;\">9</span><span style=\"font-size:18px;\">年</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 早期发热为主要症状，起病缓慢，症状轻而不典型，长期不规则发热，约</span><span style=\"font-size:18px;\">1/2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">1/3</span><span style=\"font-size:18px;\">病例呈双峰热型，即</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">日内有</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">次体温升高</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">升降幅度超过</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">℃</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">。其它热型可类似伤寒、结核、疟疾或波浪热</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">即布鲁氏杆菌病</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">。发热早期多持续</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">周后消退，数周后可再度升高，如此复发与间歇相交替，可持续一年以上。发热时可伴畏寒、盗汗食欲不振、乏力，头昏等症状。发热虽持续较久，但仍能坚持一般劳动，是其特征。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 病后</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">月典型症状逐渐明显，长期不规则发热，乏力、纳差、消瘦和咳嗽等。可因贫血出血心悸、气短及贫血貌，重症可出现心脏扩大和心力衰竭。因小板减少等因素可有鼻衄、牙龈出血等出血倾向。脾脏呈进行性肿大，自</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">病周即可触及，质地柔软，以后随病期延长脾肿逐渐明显且变硬，半年可平脐，年余可达盆腔，若脾内栓寒或出血，则可引起脾区疼痛和压痛，有时可闻及摩擦音。肝脏肿大稍晚，较脾肿轻，偶见黄疸和腹水，淋巴结呈轻、中度肿大，无明显压痛。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 晚期患者</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">发病</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">年后</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">可因长期发热营养不良，极度消瘦，致使患儿发育障碍。病情加重后皮肤有色素沉着，偶至肝硬化。亦可因脾功亢进，抵抗力降低，常并发肺炎、粒细胞缺乏症、败血症等。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">此外，尚有皮肤型和淋巴结型黑热病等，均可于病变部位找到利杜体。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">二、诊断</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">免疫诊断法：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1)</span><span style=\"font-size:18px;\">检测血清抗体：如酶联免疫吸附试验</span><span style=\"font-size:18px;\">(ELISA)</span><span style=\"font-size:18px;\">、对流免疫电泳</span><span style=\"font-size:18px;\">(CIE)</span><span style=\"font-size:18px;\">、间接荧光试验</span><span style=\"font-size:18px;\">(IF)</span><span style=\"font-size:18px;\">、间接血凝试验</span><span style=\"font-size:18px;\">(IHA)</span><span style=\"font-size:18px;\">、直接凝集试验等，阳性率高，假阳性率也较高。近年来，用分子生物学方法获得纯抗原，降低了假阳性率。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2)</span><span style=\"font-size:18px;\">检测血清循环抗原：单克隆抗体抗原斑点试验</span><span style=\"font-size:18px;\">(McAb-AST)</span><span style=\"font-size:18px;\">用于诊断黑热病，阳性率高，敏感性、特异性、重复性均较好，仅需微量血清即可，还可用于疗效评价。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 分子生物学方法：</span> <span style=\"font-size:18px;\">近年来，用聚合酶链反应</span><span style=\"font-size:18px;\">(PCR)</span><span style=\"font-size:18px;\">及</span><span style=\"font-size:18px;\">DNA</span><span style=\"font-size:18px;\">探针技术检测黑热病取得较好的效果，敏感性、特异性高，但操作较复杂，目前未能普遍推广。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">实验室检查</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1)</span><span style=\"font-size:18px;\">全血细胞减少，白细胞多在</span><span style=\"font-size:18px;\">1.5</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3.0</span><span style=\"font-size:18px;\">×</span><span style=\"font-size:18px;\">108/L</span><span style=\"font-size:18px;\">间，甚至中性粒细胞缺乏</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">贫血呈中度，血小板减少。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2)</span><span style=\"font-size:18px;\">血浆球蛋白显著增高，球蛋白沉淀试验</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">水试验</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">、醛凝试验、锑剂试验多呈阳性</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">白蛋白减少，</span><span style=\"font-size:18px;\">A/G</span><span style=\"font-size:18px;\">可倒置。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3)</span><span style=\"font-size:18px;\">血清特异性抗原抗体检测阳性有助诊断。骨髓、淋巴结或脾、肝组织穿针涂片，找到利杜体或穿刺物培养查见前鞭毛体可确诊。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4)</span><span style=\"font-size:18px;\">治疗性诊断：可用葡萄糖酸锑钠试验治疗，若疗效显著有助于本病诊断。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>黑热病预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">预防</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 主要预防措施是治疗患者和捕杀病犬。同时在白蛉活动季节喷洒</span><span style=\"font-size:18px;\">DDV</span><span style=\"font-size:18px;\">，敌百虫等药物以杀灭白蛉、防止其孳生。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(1)</span><span style=\"font-size:18px;\">在疫区白蛉季节来临之前，普查普治所有患者，并对患者定期追访一年，力求根治。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(2)</span><span style=\"font-size:18px;\">在黑热病及犬内脏利什曼病皆存在的地区，尤其是在山丘地区，仅治疗病人难以保证黑热病的根除，必须对犬类定期登记检查，发现病犬应扑杀掩埋。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(3)</span><span style=\"font-size:18px;\">疫区每年</span><span style=\"font-size:18px;\">5-9</span><span style=\"font-size:18px;\">月白蛉活动季节，要在住室、圈厕等附采取灭蛉措施。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(4)</span><span style=\"font-size:18px;\">流行季节搞好住室卫生，保持通风透光和地面干燥，防止成龄躲藏和幼龄孽生，野外工作人员裸露部分涂擦驱避剂，夜间睡眠可用细纱蚊帐防止白蛉叮咬，做好个人防护。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">其他注意事项：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(1)</span><span style=\"font-size:18px;\">继发细菌性感染 易并发肺部炎症、细菌性痢疾、齿龈溃烂、走马疳等。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(2)</span><span style=\"font-size:18px;\">急性粒细胞缺乏症 表现为高热、极度衰竭、口咽部溃疡、坏死、局部淋巴结肿胀以及外周血象中粒细胞显著减少，甚至消失。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(3)</span><span style=\"font-size:18px;\">由于血小板减少，患者常发生鼻衄、牙龈出血等症状。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(4)</span><span style=\"font-size:18px;\">可能由于患者发生肾小球淀粉样变性以及肾小球内有免疫复合物的沉积所致，也可出现蛋白尿及血尿。</span> \r\n</p>', 'hsg', 'upload/1650938421021.jpg', 2, '', '2022-04-26 10:00:22');
INSERT INTO `xinwentongzhi` VALUES (94, '麻风病的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>麻风病症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">早期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">皮损为浅色、浅黄色或淡红色斑，边界模糊，广泛而对称分布于四肢伸侧、面部和躯干等。浅感觉正常或稍迟钝，有蚁行感。鼻黏膜可充血、肿胀或糜烂。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">晚期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">皮损呈深在性、弥漫性浸润，常伴暗红色结节，面部结节或斑块可融合成大片凹凸不平的损害，双唇肥厚，耳垂肿大，形如狮面</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">眉毛脱落，头发部分或大部分脱落。伴明显浅感觉及出汗障碍，周围神经受累导致面瘫、手足运动障碍和畸型、骨质疏松和足底溃疡等。淋巴结、睾丸、眼和内脏器官受累严重，睾丸可萎缩，常引起阳萎、乳房胀大、不育等。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">红斑样皮疹</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">发烧</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">斑疹</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">淋巴结肿大</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">怕冷</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">脓疱</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">色素性皮损</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">面部皮肤有结节状增生</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>麻风病预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">预防</span><span style=\"font-size:18px;\">:</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 要控制和消灭麻风病，必须坚持</span><span style=\"font-size:18px;\">“预防为主”的方针，贯彻“积极防治，控制传染”的原则，执行“边调查、边隔离、边治疗”的做法。发现和控制传染病源，切断传染途径，给予规则的药物治疗，同时提高周围自然人群的免疫力，才能有效的控制传染、消灭麻风病。鉴于目前对麻风病的预防，缺少有效的预防疫苗和理想的预防药物。因此，在防治方法上要应用各种方法早期发现病人，对发现的病人，应及时给予规则的联合化学药物治疗。对流行地区的儿童、患者家属以及麻风菌素及结核菌素反应均为阴性的密切接触者，可给予卡介苗接种，或给予有效的化学药物进行预防性治疗。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2,</span><span style=\"font-size:18px;\">其他注意事项</span><span style=\"font-size:18px;\">:</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 怀疑自己患了麻风病怎么办</span><span style=\"font-size:18px;\">?</span><span style=\"font-size:18px;\">现在麻风的分类是：查到有菌或未查到菌，但皮损超过</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">块的属于多菌型</span><span style=\"font-size:18px;\">(MB);</span><span style=\"font-size:18px;\">未查到菌，皮损少于</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">块的归属少菌型</span><span style=\"font-size:18px;\">(PB)</span><span style=\"font-size:18px;\">。如果你怀疑自己的症状象麻风，主要及早去当地的皮防站</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">所</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">或防疫站就诊，千万不要讳疾忌医，以免贻误病情，失去早诊断、早治疗、早康复的机会。麻风病一般不致命，但它会损害神经而且是不可逆的。如果耽误了治疗会给自己留下终身的残疾</span><span style=\"font-size:18px;\">!</span> \r\n</p>', 'hsg', 'upload/1650938400278.jpg', 2, '', '2022-04-26 10:00:02');
INSERT INTO `xinwentongzhi` VALUES (95, '流行性出血性结膜炎的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>流行性出血性结膜炎症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">典型症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">出现畏光、流泪、眼红、异物感和剧烈眼疼等。眼睑及结膜充血、水肿，睑结膜滤泡明显增生。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">水肿</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">淋巴结肿大</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">畏光</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">眼痛</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">一、症状</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 此病潜伏期一般为</span><span style=\"font-size:18px;\">24h</span><span style=\"font-size:18px;\">，最长可达</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">天。其症状主要有以下特点：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">、起病急骤，迅速出现结膜充血、眼睑水肿、流泪和眼球痛。两个眼睛一般不会同时一起发生，一般是先发生于一个眼睛，过几个小进之另一只眼睛也跟着发生。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">、在起病</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">天后出现特征性的表现</span><span style=\"font-size:18px;\">-</span><span style=\"font-size:18px;\">眼球结膜下出血，约</span><span style=\"font-size:18px;\">70%</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">90%</span><span style=\"font-size:18px;\">病例会出现这种情况。从细小的出血点至整个球结膜下出血程度不等，可见短暂而无后遗症的上皮性角膜炎。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">、起病后</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">天约</span><span style=\"font-size:18px;\">90%</span><span style=\"font-size:18px;\">病例在睑结膜上出现细小滤泡。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">、约</span><span style=\"font-size:18px;\">20%</span><span style=\"font-size:18px;\">病例出现全身症状如发热、头痛、全身不适等。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">、刚发病时，常常发生角膜上皮点状剥脱，约</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">周后痊愈，而且耳前淋巴结肿大常见。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>流行性出血性结膜炎预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">一、预防</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">、隔离治疗所有患者。要求所有患者在家或定点隔离治疗，待所有症状消失后</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">一般</span><span style=\"font-size:18px;\">7-10</span><span style=\"font-size:18px;\">天</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">方可解除隔离。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">、疫情暴发单位做好晨检，及时发现疑似病例，及时隔离治疗。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">、开展消毒工作。病人洗脸应使用独立的毛巾、脸盆，并定期对毛巾和脸盆煮沸消毒。对病人接触过的物品应用</span><span style=\"font-size:18px;\">75%</span><span style=\"font-size:18px;\">酒精或含氯消毒液擦拭或煮沸消毒。对于发生疫情的集体单位，对可能被污染的日常用品、生产用具、玩具、食具或公共场所等，应使用含氯消毒剂擦拭消毒。医务人员检治病人后可用</span><span style=\"font-size:18px;\">75%</span><span style=\"font-size:18px;\">酒精消毒双手及使用过的仪器、物品等。</span> \r\n</p>', 'hsg', 'upload/1650938378568.jpg', 2, '', '2022-04-26 09:59:40');
INSERT INTO `xinwentongzhi` VALUES (96, '风疹的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>风疹症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">典型症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">低热或中度发热、头痛、食欲减退、疲倦、乏力、咳嗽、喷嚏、流涕、咽痛、结合膜充血等轻微上呼吸道炎症。偶伴呕吐、腹泻、鼻出血、牙龈肿胀等。部分患者软腭及咽部可见玫瑰色或出血性斑疹，但颊黏膜光滑，无充血及科氏斑。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">发烧</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">皮疹</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">颈部淋巴结肿大</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">浅表淋巴结肿大</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">一、症状：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">依感染方式的不同，可分为自然感染性风疹及先天风疹，其表现有不同。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">获得性风疹</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">或自然感染的风疹</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">潜伏期平均</span><span style=\"font-size:18px;\">18</span><span style=\"font-size:18px;\">天</span><span style=\"font-size:18px;\">(14</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">21</span><span style=\"font-size:18px;\">天</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(1)</span><span style=\"font-size:18px;\">前驱期：较短暂，约</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">天，症状亦较轻微。低热或中度发热、头痛、食欲减退、疲倦、乏力、咳嗽、喷嚏、流涕、咽痛、结合膜充血等轻微上呼吸道炎症。偶伴呕吐、腹泻、鼻出血、牙龈肿胀等。部分患者软腭及咽部可见玫瑰色或出血性斑疹，但颊黏膜光滑，无充血及科氏斑。一般说来，婴幼儿患者前驱期症状常较轻微，或无前驱期症状。而年长儿及成人患者则较显著，并可持续</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">天。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(2)</span><span style=\"font-size:18px;\">出疹期：通常于发热</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">天后即出现皮疹，皮疹初见于面颈部，迅速向下蔓延，</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">天内布满躯干和四肢，但手掌、足底大多无疹。皮疹初起呈细点状淡红色斑疹、斑丘疹或丘疹，直径为</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3mm</span><span style=\"font-size:18px;\">。面部、四肢远端皮疹较稀疏，部分融合类似麻疹。躯干尤其背部皮疹密集，融合成片，又类似猩红热。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 皮疹一般持续</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">天</span><span style=\"font-size:18px;\">(1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">天</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">消退，亦有人称为“三日麻疹”。面部有疹为风疹之特征，少数患者出疹呈出血性，同时全身伴出血倾向，出疹期常伴低热，轻度上呼吸道炎，脾肿大及全身浅表淋巴结肿大，其中尤以耳后、枕部、颈后淋巴结肿大最明显，肿大淋巴结轻度压痛，不融合，不化脓。有时风疹患者脾脏及淋巴结肿大可在出疹前</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">天就已发生。疹退时体温下降，上呼吸道症状消退，肿大的淋巴结亦逐渐恢复，但完全恢复正常常需数周以后。皮疹消退后一般不留色素沉着，亦不脱屑。仅少数重症患者可有细小糠麸样脱屑，大块脱皮则极少见。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 无皮疹性风疹：风疹患者可以只有发热、上呼吸道炎、淋巴结肿痛，而不出皮疹。也可以在感染风疹病毒后没有任何症状、体征，血清学检查风疹抗体为阳性，即所谓隐性感染患者。在不同地区的流行病学调查中发现显性感染和无皮疹的或隐性感染患者的比例为</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">∶</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">∶</span><span style=\"font-size:18px;\">9</span><span style=\"font-size:18px;\">。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">先天性风疹综合征</span><span style=\"font-size:18px;\">(CRS) </span><span style=\"font-size:18px;\">胎儿被感染后，重者可导致死胎、流产、早产，轻者可致胎儿发育迟缓，出生体重、身长、头围、胸围等均比正常新生儿低，此差距至</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">岁时往往还不能纠正。此类患婴易有多种畸形，有谓新生儿先天畸形中</span><span style=\"font-size:18px;\">5%</span><span style=\"font-size:18px;\">以上由于先天性风疹所致。先天畸形或疾病常见者有白内障、视网膜病、青光眼、虹膜睫状体炎、神经性耳聋、前庭损害、中耳炎、先天性心脏病、心肌坏死、高血压、间质肺炎、巨细胞肺炎、肝脾肿大、淋巴结肿大、肾小球硬化、血小板减少性紫癜、溶血性贫血、再生障碍性贫血、脑炎、脑膜炎、小头畸形、智力障碍等。从先天性风疹患儿的咽部、血、尿、脑脊液内可分离出风疹病毒，阳性率以</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">岁内为高。也有报告经先天感染后，风疹病毒于脑组织内持续存在在达</span><span style=\"font-size:18px;\">12</span><span style=\"font-size:18px;\">年，而引起进行性风疹全脑炎。多数先天性风疹患儿于出生时即具临时症状，也可于生后数月到数年才出现进行性症状和新的畸形。</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">岁以后出现的畸形可有耳聋、精神动作异常、语言障碍、骨骼畸形等。因此对有先天性风疹和可能的小儿自出生后需随访至</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">年或</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">年。国外有报道在一次风疹大流行期中出生的</span><span style=\"font-size:18px;\">4005</span><span style=\"font-size:18px;\">例新生儿经病毒分离或血清检查证明先天性风疹</span><span style=\"font-size:18px;\">&gt;2%(</span><span style=\"font-size:18px;\">当地平时新生儿中只</span><span style=\"font-size:18px;\">0.1%</span><span style=\"font-size:18px;\">为先天性风疹</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">。此</span><span style=\"font-size:18px;\">4005</span><span style=\"font-size:18px;\">例中</span><span style=\"font-size:18px;\">68%</span><span style=\"font-size:18px;\">为亚临床型，在新生儿时期无畸形或缺陷症状，但其中</span><span style=\"font-size:18px;\">71%</span><span style=\"font-size:18px;\">在生后头</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">年内的随访中，在不同时期陆续出现上述各种不同的先天性风疹病症。可见先天性风疹综合征是风疹病毒感染的严重后果。我国近年也有报道在</span><span style=\"font-size:18px;\">825</span><span style=\"font-size:18px;\">例早孕妇女中，查出风疹</span><span style=\"font-size:18px;\">IgM</span><span style=\"font-size:18px;\">抗体阳性率占</span><span style=\"font-size:18px;\">1.44%</span><span style=\"font-size:18px;\">，其胎儿血风疹</span><span style=\"font-size:18px;\">IgM</span><span style=\"font-size:18px;\">阳性率占孕妇感染的</span><span style=\"font-size:18px;\">62.5%</span><span style=\"font-size:18px;\">。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">二、诊断标准：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 典型的风疹病人的诊断，主要依据流行病学史和临床表现，如前驱期短，开始在面、颈部有淡红色斑疹或斑丘疹，</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">天后发展到躯干和四肢，</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">天或更少时间皮疹消退，上呼吸道炎症，低热、特殊斑丘疹，耳后、腋窝、腹股沟枕部淋巴结肿痛等，但在流行期间不典型病人和隐型感染患者远较典型病人为多，对这类病人必须做病毒分离或血清抗体测定，方可以确定诊断。特异性</span><span style=\"font-size:18px;\">IgM</span><span style=\"font-size:18px;\">抗体有诊断价值。此</span><span style=\"font-size:18px;\">IgM</span><span style=\"font-size:18px;\">抗体于发病</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">8</span><span style=\"font-size:18px;\">周后消失，只留有</span><span style=\"font-size:18px;\">IgG</span><span style=\"font-size:18px;\">抗体。妊娠期怀疑感染风疹的妇女所生婴儿，不论有无症状、体征，均应作风疹病毒分离和测定</span><span style=\"font-size:18px;\">IgM</span><span style=\"font-size:18px;\">抗体，阳性者即可诊断为先天性风疹。先天性风疹时特异性</span><span style=\"font-size:18px;\">IgM</span><span style=\"font-size:18px;\">抗体与自然感染者不同，胎儿</span><span style=\"font-size:18px;\">16</span><span style=\"font-size:18px;\">周龄时，即有他自己的特异的</span><span style=\"font-size:18px;\">IgM</span><span style=\"font-size:18px;\">，出生后</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">个月内持续升高，此后渐下降，但一岁内均可测得。自母体来的</span><span style=\"font-size:18px;\">IgG</span><span style=\"font-size:18px;\">抗体生后数月时即下降而婴儿自身的</span><span style=\"font-size:18px;\">IgG</span><span style=\"font-size:18px;\">风疹抗体同时持续上升。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">风疹视网膜炎往往为诊断先天性风疹的重要甚至唯一的体征。视网膜上常出现棕褐或黑褐色的大小不一的点状或斑纹状色素斑点，重症患者除斑点相大外并伴有黄色晶状体。视网膜血管常较正常窄细。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>风疹预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">【预防】</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">因本病症状多轻，一般预后良好，故似不需要特别预防，但先天性风疹危害大，可造成死胎、早产或多种先天畸形，因此预防应着重在先天性风疹。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">一</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">隔离检疫　病人应隔离至出疹后</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">天。但本病症状轻微，隐性感染者多，故易被忽略，不易做到全部隔离。一般接触者可不进行检疫，但妊娠期、特别妊娠早期的妇女在风疹流行期间应尽量避免接触风疹病人。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">二</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">自动免疫　国际上经过十余年来广泛应用风疹减毒疫苗，均证明为安全有效，接种后抗体阳转率在</span><span style=\"font-size:18px;\">95%</span><span style=\"font-size:18px;\">以上，接种后仅个别有短期发热、皮疹、淋巴结肿大及关节肿痛等反应，免疫后抗体持久性大多可维持在</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">年以上。接种对象方面不同国家尚不统一，例如美国主张</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">岁至青春期的青少年，特别是幼儿园和小学中的儿童为主要免疫对象，因为小儿风疹发病率最多，且可传播给孕妇等成人。青春期及成年妇女也应接种，先天性风疹已明显减少。尽管目前关于风疹疫苗病毒株对人体、胎儿的影响了解得不够，但活疫苗的弱病毒确能通过胎盘感染胎儿导致胎儿畸形，因此孕妇不宜接受此类活疫苗。风疹早已与麻疹、腮腺炎疫苗联合使用。取得了良好的效果。目前我国也已制成风疹减毒活疫苗，有的地方已开始使用并将逐步纳入计划免疫执行，重点免疫对象中包括婚前育龄妇女，含高中、初中毕业班女生。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">三</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">患儿卧床休息，避免直接吹风，防止受凉后复感新邪，加重病情。发热期间，多饮水。饮食宜清淡和容易消化，不吃煎炸与油腻之物。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">四</span><span style=\"font-size:18px;\">) </span><span style=\"font-size:18px;\">防止播搔破皮肤，引起感染。</span> \r\n</p>', 'hsg', 'upload/1650938364172.jpg', 2, '', '2022-04-26 09:59:25');
INSERT INTO `xinwentongzhi` VALUES (97, '流感的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>流感症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">早期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">高热畏寒、高热、头痛、头晕、全身酸痛、乏力等中毒症状。可伴有咽痛、流涕、流泪、咳嗽等呼吸道症状。少数病例有食欲减退，伴有腹痛、腹胀、呕吐和腹泻等消化道症状。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">晚期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">婴儿可见高热惊厥</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">部分患儿表现为喉气管支气管炎，严重者出现气道梗阻现象</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">新生儿流感虽少见，一旦发生常呈脓毒症表现，如嗜睡、拒奶、呼吸暂停等，常伴有肺炎，病死率高。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">怕冷</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">头痛</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">浑身酸痛</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">咽喉疼痛</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">发烧</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">上呼吸道卡他症状</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">一、症状</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 突然快速而至的高热畏寒、高热、头痛、头晕、全身酸痛、乏力等中毒症状。可伴有咽痛、流涕、流泪、咳嗽等呼吸道症状。少数病例有食欲减退，伴有腹痛、腹胀、呕吐和腹泻等消化道症状。婴儿流感的临床症状往往不典型，可见高热惊厥</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">部分患儿表现为喉气管支气管炎，严重者出现气道梗阻现象</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">新生儿流感虽少见，一旦发生常呈脓毒症表现，如嗜睡、拒奶、呼吸暂停等，常伴有肺炎，病死率高。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">B</span><span style=\"font-size:18px;\">型流感的病童会有小腿痛、肚子痛等不典型的流感症状，</span><span style=\"font-size:18px;\">A</span><span style=\"font-size:18px;\">型流感和</span><span style=\"font-size:18px;\">B</span><span style=\"font-size:18px;\">型流感的发烧、全身痠痛、咳嗽、流鼻水的症状大同小异，只是</span><span style=\"font-size:18px;\">B</span><span style=\"font-size:18px;\">型流感更容易引起肌肉发炎，常有小病人因小腿痛不想走路，被家长抱着进诊间。此外，大约</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">到</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">成病童会嚷着肚子痛，这是疼痛转移的现象。如果发烧超过</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">天，喘不过气、心跳加速或意识不清楚时，当心病情恶化。</span><span style=\"font-size:18px;\">B</span><span style=\"font-size:18px;\">型流感症状缓解速度较慢，即使在发病</span><span style=\"font-size:18px;\">48</span><span style=\"font-size:18px;\">小时内服用「克流感」抗病毒药，仍然可能</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">星期才完全煺烧。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">二、分类</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">轻型流感</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 起病急、病情较轻，全身症状</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">全身酸痛、软弱无力</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">与呼吸道症状</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">喉咙痛、流鼻涕、咳嗽、鼻塞、流眼泪</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">均很轻。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">典型流感</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 开始可表现为畏寒、发热，体温可高达</span><span style=\"font-size:18px;\">39-40</span><span style=\"font-size:18px;\">℃，同时患者感到头痛、全身酸痛、软弱无力，也伴随着眼眼睛干涩、喉咙干燥、轻微的喉咙痛。除了上述症状，部分患者可能会出现打喷嚏、流鼻涕、鼻塞，有时也会伴随有胃肠道症状，加恶心、呕吐、腹泻等。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 发热与上述此状一般于</span><span style=\"font-size:18px;\">1-2</span><span style=\"font-size:18px;\">天达高峰，</span><span style=\"font-size:18px;\">3-4</span><span style=\"font-size:18px;\">日内热退，症状随之消失。乏力与咳嗽可持续</span><span style=\"font-size:18px;\">l-2</span><span style=\"font-size:18px;\">周。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3.</span><span style=\"font-size:18px;\">流感病毒肺炎</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 即流感病毒性肺炎，</span><span style=\"font-size:18px;\">24</span><span style=\"font-size:18px;\">小时内病情迅速加重，表现为高热、乏力、烦躁、剧咳、呼吸困难、发绀，咳有血痰，双肺密布湿性罗音和喘鸣，脉快细弱，病死率较高。此类病人较少见，主要发生于原有心脏病、慢性肺病患者或妊娠妇女、主要常见于小儿、老人或体弱多病、免疫力低下者。起病时与单纯型流感相似，但在发病</span><span style=\"font-size:18px;\">24</span><span style=\"font-size:18px;\">小时内，出现高热不退、剧烈咳嗽、呼吸困难、咯血、紫绀等症状。病程可延长</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">周。血常规检查：白细胞减少、中性粒细胞减少。</span><span style=\"font-size:18px;\">X</span><span style=\"font-size:18px;\">线检查：双肺呈散在絮状阴影。磺胺、抗菌素对本病无效。少数病人可因心力衰竭或周围循环衰竭而死亡。小儿病死率较高。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4.</span><span style=\"font-size:18px;\">中毒性流感</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">以中枢神经系统及心血管系统损害为特征。表现为高热不退，血压下降，谵妄</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">谵妄是一种急性脑病状态，特点是伴有意识和认知功能障碍，在疾病伴发躯体功能紊乱的时候会造成上述情况。</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">、惊厥、脑膜刺激征等脑炎脑膜炎症状。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">5.</span><span style=\"font-size:18px;\">脑炎型流感</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 患者起病骤急，一开始就非常严重，常表现为高热、神志不清，颈项强直</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">指由于支配颈部肌群的神经受到刺激后，引起的颈部肌肉发生痉挛性收缩和疼痛，颈部僵直，活动受限，被动屈曲颈部时有阻抗感，下颌不能贴近胸部。</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">、抽搐等脑炎的症状。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 流感嗜血杆菌脑膜炎：绝大部分是由</span><span style=\"font-size:18px;\">b</span><span style=\"font-size:18px;\">型流感杆菌所致。流感杆菌仅侵犯人类，发病年龄以</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">个月至</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">岁婴儿为主，因为</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">个月以内的婴儿体内有来自母体的杀菌抗体，故很少患病，其后随着年龄增长，来自母体的特异性杀菌抗体下降，而自身尚未能产生足量抗体，故易患此病。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">6.</span><span style=\"font-size:18px;\">胃肠炎型流感</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">少见，以腹泻、腹痛、呕吐为主要临床表现。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">7.</span><span style=\"font-size:18px;\">其他类型</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 老年人流行性感冒</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">：患者易并发肺炎，高龄、体弱或合并有心肺疾病者预后常较严重。</span><span style=\"font-size:18px;\">60</span><span style=\"font-size:18px;\">岁以上老人发病率较高，病情较重，老年人因感染此病毒而死亡的情况比其它年龄高。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">小儿流行性感冒：是小儿严重的呼吸道传染性疾病。特别是婴幼儿。容易并发肺炎和脑膜炎。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 副流行性感冒：副流感病毒为</span><span style=\"font-size:18px;\">RNA</span><span style=\"font-size:18px;\">病毒，属副粘液病毒科。在成人中常引起轻型上呼吸道感染，而</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">岁以下儿童主要为下呼吸道感染，且发病率最高。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 高致病性禽流感病毒感染：是由禽甲型流感病毒某些亚型的毒株引起的人的急性呼吸道传染病。病情轻重不一，严重者可致败血症、休克、多脏器功能衰竭，以及</span><span style=\"font-size:18px;\">Reye</span><span style=\"font-size:18px;\">综合征等多种并发症而致人死亡。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>流感预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">流感重在预防，遵循传染病防治的一般规律。由于人群对流感病毒普遍易感，与年龄、性别、职业等无关，对这样一个高度易感的疾病，要预防必须隔离传染源、切断传播途径，对易感人群进行必要的教育并采取有效的措施。下面从三个方面介绍一下流感的预防。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">一般预防</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 早期发现，迅速确诊，加强监控力度。对病人做好隔离，病人是主要的传染源，自潜伏期末即有传染性，</span> <span style=\"font-size:18px;\">病初</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">—</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">天传染性最强。病毒存在于病人的鼻涕、口涎、痰液，并随咳嗽、喷嚏排出体外，同时还应注意由于部分免疫，有些人感染后可不发病，成为隐性感染，成为人群的隐形传染源。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">药物预防</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">金刚烷胺或金刚乙胺预防甲型流感有一定效果。在流行早期，必须及时确定流行株的型别。对无保护的人群可以进行药物预防。但是由于金刚烷胺具有中枢神经系统的副作用，老年及有血管硬化者慎用，孕妇及有癫痫史者禁用。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 人们也可试用中药预防。将板蓝根、大青叶各</span><span style=\"font-size:18px;\">50</span><span style=\"font-size:18px;\">克，野菊花、金银花各</span><span style=\"font-size:18px;\">30</span><span style=\"font-size:18px;\">克，四味中药同放入大茶缸中，用热开水冲泡，片刻后饮用。或者用贯众、板蓝根各</span><span style=\"font-size:18px;\">30</span><span style=\"font-size:18px;\">克，蒲公英</span><span style=\"font-size:18px;\">15</span><span style=\"font-size:18px;\">克，青茶</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">克，三味用开水冲泡后代茶饮。以上两方清热解毒功效良好，具有较强的抗病毒功效，适用于流行感冒。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3.</span><span style=\"font-size:18px;\">疫苗预防</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 目前，接种流感疫苗是预防流感最有效的一种手段。流感疫苗是根据病毒的变化来制造的，联合国国际卫生组织在世界各地建有</span><span style=\"font-size:18px;\">110</span><span style=\"font-size:18px;\">个流感监测点，其中有</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">个建在我国，负责监测和预测流感爆发和流感病毒的变异。每年</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">月，联合国国际卫生组织主持召开各个国家流感监测专家会议，汇总、分析可能爆发和流行的毒株，疫苗厂商据此生产疫苗，故流感疫苗应一年接种一次。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 预防流行性感冒在日常生活中需要注意的</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">个小细节</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">）勤洗手</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 研究结果表明，感冒病毒在手上能存活</span><span style=\"font-size:18px;\">70</span><span style=\"font-size:18px;\">个小时。感冒患者在擤鼻涕、挖鼻孔时会将病毒沾在手上，健康人若与患者握手或在公共场所接触了患者触摸过的物品，手上就会带有感冒病毒</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">所以勤洗手十分必要。 日常生活饮食需要注意，经常锻炼身体提高免疫力。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">）勤换牙刷。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 人们每天都要使用牙刷，若上面带有病毒，则容易反复感染</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">另外，牙刷常处于潮湿状态，病原体易滋生繁殖，对身体健康极为不利。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">）脚部保暖。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 脚对温度比较敏感，如果脚部受凉，会反射性地引起鼻黏膜血管收缩，使人容易受感冒病毒侵扰</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">同时提倡冷水洗脸。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">）饮食清淡。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">“三高”饮食会降低人体免疫力，饮食过咸，会使唾液分泌及口腔内的溶菌酶减少，并降低干扰素等抗病因子的分泌，使感冒病毒易进入呼吸道黏膜而诱发感冒。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">）精神愉快。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 医学家通过观察发现，精神紧张、忧郁的人，体内抗病毒物质明显减少</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">局部免疫力下降，易患感冒。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">）合理睡眠。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">经研究表明，人在睡眠时体内会产生一种有提高免疫力作用的物质。因此感冒病人保证充足睡眠十分重要。</span> \r\n</p>', 'hsg', 'upload/1650938325801.jpg', 2, '', '2022-04-26 09:58:47');
INSERT INTO `xinwentongzhi` VALUES (98, '疟疾的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>疟疾症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">典型症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">发烧 ，乏力 ，头痛 ，口唇发绀 ，脾肿大， 急性贫血。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">发烧</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">乏力</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">头痛</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">口唇发绀</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">脾肿大</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">急性贫血</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 一、按照发病过程分类：四种人体疟疾典型的临床发作大体相似，可分为潜伏期、发冷</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">寒战</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">期、发热期、出汗期和间歇期。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">、潜伏期：从人体感染疟原虫到发病</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">口腔温度超过</span><span style=\"font-size:18px;\">37.8</span><span style=\"font-size:18px;\">℃</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">，称潜伏期。潜伏期包括整个红外期和红内期的第一个繁殖周期。一般间日疟、卵形疟</span><span style=\"font-size:18px;\">14</span><span style=\"font-size:18px;\">天，恶性疟</span><span style=\"font-size:18px;\">12</span><span style=\"font-size:18px;\">天，三日疟</span><span style=\"font-size:18px;\">30</span><span style=\"font-size:18px;\">天。感染原虫量、株的不一，人体免疫力的差异，感染方式的不同均可造成不同的潜伏期。温带地区有所谓长潜伏期虫株，可长达</span><span style=\"font-size:18px;\">8</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">14</span><span style=\"font-size:18px;\">个月。输血感染潜伏期</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">天。胎传疟疾，潜伏期就更短。有一定免疫力的人或服过预防药的人，潜伏期可延长。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 间日疟</span><span style=\"font-size:18px;\">(tertian malaria)</span><span style=\"font-size:18px;\">多急起，复发者尤然。初次感染者常有前驱症状，如乏力、倦怠、打呵欠</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">头痛，四肢酸痛</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">食欲不振，腹部不适或腹泻</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">不规则低热。一般持续</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">天，长者一周。随后转为典型发作。分为三期。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">、发冷期或寒战期：骤感畏寒，先为四肢末端发凉，迅觉背部、全身发冷。皮肤起鸡皮疙瘩，口唇，指甲发绀，颜面苍白，全身肌肉关节酸痛。进而全身发抖，牙齿打颤，有的人盖几床被子不能制止，持续约</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">分钟，乃至一小时许，寒战自然停止，体温上升。此期患者常有重病感。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">、发热期：冷感消失以后，面色转红，紫绀消失，体温迅速上升，通常发冷越显著，则体温就愈高，可达</span><span style=\"font-size:18px;\">40</span><span style=\"font-size:18px;\">℃以上。高热患者痛苦难忍。有的辗转不安，呻呤不止</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">有的谵妄，撮空，甚至抽搐或不省人事</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">有的剧烈头痛</span><span style=\"font-size:18px;\">.</span><span style=\"font-size:18px;\">顽固呕吐。患者面赤</span><span style=\"font-size:18px;\">.</span><span style=\"font-size:18px;\">气促</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">结膜充血</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">皮灼热而干燥</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">脉洪而速</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">尿短而色深。多诉说心悸，口渴，欲冷饮。持续</span><span style=\"font-size:18px;\">2-6</span><span style=\"font-size:18px;\">小时，个别达</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">余小时。发作数次后唇鼻常见疱疹。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">、出汗期：高热后期，颜面手心微汗，随后遍及全身，大汗淋漓，衣服湿透，约</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">小时体温降低，常至</span><span style=\"font-size:18px;\">35.5</span><span style=\"font-size:18px;\">℃。患者感觉舒适，但十分困倦，常安然入睡。一觉醒来，精神轻快，食欲恢复，又可照常工作。此刻进入间歇期。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">、间歇期：系指前后两次发作的间隔时间。时间长短取决于虫种和免疫力。就典型者的间歇期而言，恶性疟病例很不规则，短仅数小时，长达</span><span style=\"font-size:18px;\">24</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">48h</span><span style=\"font-size:18px;\">，间日疟和卵形疟约为</span><span style=\"font-size:18px;\">48h</span><span style=\"font-size:18px;\">，三日疟为</span><span style=\"font-size:18px;\">72h</span><span style=\"font-size:18px;\">。镜检所见原虫除恶性疟外，以大滋养体为主。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">、潜隐期和复发：间日疟和卵形疟还有潜隐期和复发</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">恶性疟和三日疟只有复燃，没有复发。初发与复发以及前后两次复发间隔的时间，分别称为第</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">和第</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">潜隐期。未经足量高效血内裂殖体杀灭药治疗后所见的类似情况，统称复燃。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">二、按照疾病严重程度分类：凶险型疟疾主要见于恶性疟。其他三种疟疾极少见到凶险型。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">、间日疟：间日疟多有前驱期。临床急性发作以体温超过</span><span style=\"font-size:18px;\">38</span><span style=\"font-size:18px;\">℃为准，发热始于中午前后和晚上</span><span style=\"font-size:18px;\">9</span><span style=\"font-size:18px;\">点以前，偶见于深夜。开始一、二次症状较轻，热度较低，随后日益加重。间日疟发作中常见单纯疱疹，多见于口唇周围，也可延及鼻和两耳，偶见于肛门周围和外阴部。间日疟预后良好，早年有良性疟之称。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">、卵形症：临床症状与间日疟相似。多数在下午</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">点以后或晚上发作。症状较轻，无明显寒战，发作次数一般在</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">次以内，易自愈，远期复发少，常见无症状的带虫者。半数以上病例一开始就呈典型的间日热型，热度较低。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">、三日疟：潜伏期</span><span style=\"font-size:18px;\">18</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">35</span><span style=\"font-size:18px;\">天，平均</span><span style=\"font-size:18px;\">28</span><span style=\"font-size:18px;\">天。通常无前驱期，发作前</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">天有疲倦、肌肉关节酸痛、寒冷感及头痛，往往不易觉察而被忽视。多在午后发病，亦可见于中午时分。发冷、发热和出汗三个时期较明显，因退热太快，有虚脱的可能。由于三日疟原虫的发育同步性较强，每</span><span style=\"font-size:18px;\">72h</span><span style=\"font-size:18px;\">为一发作周期亦较规则。三日疟无复发，足量氯喹和奎宁均能予以根治。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">、恶性疟：潜伏期</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">27</span><span style=\"font-size:18px;\">天，平均</span><span style=\"font-size:18px;\">11</span><span style=\"font-size:18px;\">天，多突然发病，无寒战，仅有畏寒感。高热者多见，常伴有头痛、全身酸痛、恶心、呕吐、贫血等。出汗期不明显。热型复杂，有的像间日疟，隔天发作</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">次，与</span><span style=\"font-size:18px;\">48h</span><span style=\"font-size:18px;\">为一恶性疟红内期裂体增殖周期相符</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">有的每天发热，热型呈间歇型、弛张型或不规则型</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">有的持续高热，发热期往往长达</span><span style=\"font-size:18px;\">20</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">36h;</span><span style=\"font-size:18px;\">前后两次发作的间歇期极短，致使体温曲线呈“</span><span style=\"font-size:18px;\">M</span><span style=\"font-size:18px;\">”型。恶性疟用高效裂殖体杀灭药足量治疗后，即可根治。若能及时治疗，多数病例预后良好。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(1)</span><span style=\"font-size:18px;\">脑型：多见于无免疫力而又未及时治疗者。临床上分为嗜睡、昏睡和昏迷三级。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(2)</span><span style=\"font-size:18px;\">超高热型：以起病较急、体温迅速上升至</span><span style=\"font-size:18px;\">41</span><span style=\"font-size:18px;\">℃以上并持续不退为特点。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(3)</span><span style=\"font-size:18px;\">厥冷型：病人软弱无力，皮肤湿冷、苍白或轻度发绀，可有阵发性上腹剧痛，常伴有顽固性呕吐或水样便，很快虚脱以至昏迷，多因循环衰竭而死亡。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(4)</span><span style=\"font-size:18px;\">胃肠型：有明显腹痛、腹泻和里急后重感。本型是凶险型中预后较好、病死率较低一型。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">三、诊断</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">人体四种疟疾在临床表现、病程经过、对药物反应等方面有许多共同性，又各有一定的特殊性。因此，在诊断上应明确患者的疟疾种类。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">、临床诊断要点 ①多数病例在发热前有时间长短不一的寒战或畏寒</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">②体温在短时间内迅速上升，持续数小时，然后很快下降，继而有不同程度的出汗。每隔</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">4h</span><span style=\"font-size:18px;\">测量体温</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">次，分析体温曲线，则可发现夜间的体温往往降至正常或在常温以下</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">③发作有定时性，发热期与无热期交叠出现，且有一定的规律性</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">④病人在发作间歇期除疲劳、无力和略感不适外，一般感觉良好</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">⑤发病多见于中午前后和下午，夜间开始发作者较少</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">⑥临床症状一次比一次严重，经多次发作后，又渐次减轻，有“自愈”的趋势</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">⑦有溶血性贫血的临床表现，其程度与发作次数相符</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">⑧脾大，其程度与病程相关，部分病例同时见肝大。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">婴幼儿、恶性疟以及新感染病例发作的起初一、二次，临床症状常不典型。此外，一些有较高免疫力的病人，血中带有大量原虫，临床症状却不明显或完全没有，尤须参照体检和实验室检查方能确定诊断。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">、实验室诊断见检查标签页</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>疟疾预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">，预防</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">一</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">预防用药</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 根据</span><span style=\"font-size:18px;\">WHO</span><span style=\"font-size:18px;\">最新的建议</span><span style=\"font-size:18px;\">[29]</span><span style=\"font-size:18px;\">，疟疾的预防用药在过去</span><span style=\"font-size:18px;\">20</span><span style=\"font-size:18px;\">来已逐渐减少其重要性，目前建议使用的仅限於短期前往高危险疫区的观光客，以及在高流行区工作的军人，警察或劳工等、对於预防用药有以下几点要注意：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">、没有任何一种预防用药有百分之百的保护作用。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">、预防用药必须因不同的地区的抗药性而使用不同的药物。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">、每周服用一次的药如</span><span style=\"font-size:18px;\">chloroquine</span><span style=\"font-size:18px;\">或</span><span style=\"font-size:18px;\">mefloquine</span><span style=\"font-size:18px;\">必须在出发前一周开始服用，</span><span style=\"font-size:18px;\">mefloquine</span><span style=\"font-size:18px;\">更建议两周前开始服用，除了可以达到更高的血中药物浓度之外，更可以提早发现可能的副作用。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">、抗疟疾药物最好跟食物一起服用，而且要喝大量的水。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">、服药必须规律，尤其是每周一次的药，最好在每周的同一天服用，使用时间也很重要，除了</span><span style=\"font-size:18px;\">Malarone</span><span style=\"font-size:18px;\">以外，其他的药都必须在离开疫区之后继续服用</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">周以确保预防的效果。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">、每种抗疟预防用药皆有其不同族群使用的禁忌，使用前必须详加考虑。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">、曾经前往疫区的民众必须了解，不管有无使用预防用药，返国后</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">个月内若有不明原因的发烧，应尽早就医，并主动告知医师相关的旅游史。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">二</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">特殊族群预防用药的注意事项：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">、怀孕妇女：怀孕时感染疟疾会增加母亲死亡及流产的风险，更可能导致死产或是婴儿体重不足，所以应该建议怀孕妇女尽量不要到有疟疾风险的地区旅游。如果真的必要，建议要做好完善的预防措施，包括避免蚊子叮咬</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">使用防蚊液及蚊帐</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">，以及正确使用预防药物、药物选择除了要考虑依不同地区可能的抗药性之外，还要特别注意药物的安全性，像是</span><span style=\"font-size:18px;\">mefloquine</span><span style=\"font-size:18px;\">就不建议在第一产期使用</span><span style=\"font-size:18px;\">(2nd</span><span style=\"font-size:18px;\">，</span><span style=\"font-size:18px;\">3rdtrimester</span><span style=\"font-size:18px;\">可以使用</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">，而</span><span style=\"font-size:18px;\">doxycycline</span><span style=\"font-size:18px;\">则是整个怀孕期间都不能用，另外</span><span style=\"font-size:18px;\">Malarone</span><span style=\"font-size:18px;\">则是还没有足够的使用经验。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">、小孩：小孩得到疟疾很可能会有严重并发症甚至急速恶化死亡，尤其是恶性疟流行区，最好不要带婴儿或年纪小的孩童前往，如无法避免，一定要做好万全的准备来避免蚊子叮咬、药物方面，必须依体重来调整剂量，</span><span style=\"font-size:18px;\">mefloquine</span><span style=\"font-size:18px;\">只可以用在体重大於</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">公斤的小孩，</span><span style=\"font-size:18px;\">doxycycline</span><span style=\"font-size:18px;\">不能用在</span><span style=\"font-size:18px;\">8</span><span style=\"font-size:18px;\">岁以下的小孩，</span><span style=\"font-size:18px;\">Malarone</span><span style=\"font-size:18px;\">则是不用在小於</span><span style=\"font-size:18px;\">11</span><span style=\"font-size:18px;\">公斤的小孩、表六为</span><span style=\"font-size:18px;\">WHO</span><span style=\"font-size:18px;\">预防用药的建议。</span> \r\n</p>', 'hsg', 'upload/1650938307947.jpg', 1, '', '2022-04-26 09:58:30');
INSERT INTO `xinwentongzhi` VALUES (99, '血吸虫的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>血吸虫病症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">早期症状：</span><span style=\"font-size:18px;\"> 1</span><span style=\"font-size:18px;\">）发热：为本期主要的症状，发热的高低，期限和热型视感染轻重而异。 </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">）胃肠道症状：常呈痢疾样大便，可带血和粘液。 </span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">）肝脾肿大。 </span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">）肺部症状 ：咳嗽相当多见，可有胸痛，血痰等症状。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">晚期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">病人极度消瘦，出现腹水、巨脾，腹壁静脉怒张等晚期严重症状。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">消瘦</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">胸痛</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">痰中带血丝</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">胃肠道症状</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">一、症状</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">、侵袭期</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">患者可有咳嗽、胸痛、偶见痰中带血丝等。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">、急性期</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">临床上常有如下特点：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">）发热：为本期主要的症状，发热的高低，期限和热型视感染轻重而异。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">）胃肠道症状：常呈痢疾样大便，可带血和粘液。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">）肝脾肿大。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">）肺部症状 ：咳嗽相当多见，可有胸痛，血痰等症状。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">、慢性期：多因急性期未曾发现，未治疗或治疗不彻底，或多次少量重复感染等原因，逐渐发展成慢性。本期一般可持续</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">20</span><span style=\"font-size:18px;\">年，因其病程漫长，症状轻重可有很大差异。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">、晚期：病人极度消瘦，出现腹水、巨脾，腹壁静脉怒张等晚期严重症状。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">二、检查</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 大便沉淀孵化试验</span> <span style=\"font-size:18px;\">沉孵法是目前最主要的诊断方法。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">肠镜检查及肠粘膜活组织检查：疑似血吸虫的而反复大便检查虫卵阴性者适用肠镜检查。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">血象：急性期白细胞总数及嗜酸性粒细胞显著增多。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>血吸虫病预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">①不在有钉螺分布的湖水、河塘、水渠里游泳、戏水。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">②因生产生活不可避免接触疫水者，可在接触疫水前涂抹防护油膏，预防血吸虫感染。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">③接触疫水后，要及时到当地血防部门进行必要的检查和早期治疗。</span> \r\n</p>', 'hsg', 'upload/1650938283478.jpg', 1, '', '2022-04-26 09:58:05');
INSERT INTO `xinwentongzhi` VALUES (100, '钩端螺旋体病的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>钩端螺旋体病症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">早期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">早期有高热，全身酸痛、软弱无力、结膜充血、腓肠肌压痛、表浅淋巴结肿大等钩体毒血症状</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">晚期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">晚期多数病例恢复，少数病例可出现后发热、眼葡萄膜炎以及脑动脉闭塞性炎症等多种与感染后的变态反应有关的后发症。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">出血倾向</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">脑膜刺激症状</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">弛张热</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">烦躁不安</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">怕冷</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>钩端螺旋体病预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">钩端螺旋体病预防措施</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">一</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">消灭和管理传染源</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">开展灭鼠保粮灭鼠防病群众运动。疫区内应灭鼠，管理好猪、犬、羊、牛等家畜，加强动物宿主的检疫工作。发现病人及时隔离进行检查治疗，并对排泄物如尿、痰和病人的血、脑脊液等进行消毒。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">二</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">切断传染途径</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 保护水源和食物，防止鼠和病畜尿污染。应对流行区的水稻田、池溏、沟溪、积水坑及准备开荒的地区进行调查，因地制宜地结合水利建设对疫源地进行改造</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">加强疫水管理、粪便管理、修建厕所和改良猪圈、不让畜粪、畜尿进行附近池溏、稻田和积水中</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">对污染的水源、积水可用漂白粉及其他有效药物进行喷洒消毒</span><span style=\"font-size:18px;\">; </span><span style=\"font-size:18px;\">收割水稻前放干田水，或放农药处理</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">管理好饮食，防止带菌鼠的排泄物污染食品。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">三</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">保护易感人群</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">在流行地区和流行季节避免在疫水中游泳、嬉水、涉水。加强个人防护、皮肤涂布防护药。尽量穿长统靴和戴胶皮手套，并防止皮肤破损、减少感染机会。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">四</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">药物预防</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">疫区居民、部队及参加收割、防洪、排涝可能与疫水接触的人员，尽可能提前</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">个月接种与本地区流行菌型相同的钩体多价菌苗。每年</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">次，间隔</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">天。剂量成人第</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">次</span><span style=\"font-size:18px;\">1ml</span><span style=\"font-size:18px;\">，第二次</span><span style=\"font-size:18px;\">2ml</span><span style=\"font-size:18px;\">。全程注射后人体产生的免疫力可持续</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">年左右。以后每年仍需同样注射。有心、肾疾患、结核病及发热患者不予注射。</span> \r\n</p>', 'hsg', 'upload/1650938260991.jpg', 1, '', '2022-04-26 09:57:42');
INSERT INTO `xinwentongzhi` VALUES (101, '流行性出血热的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>流行性出血热症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">早期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">起病急，有发热、三痛以及恶心、呕吐、胸闷、腹痛、腹泻、全身关节痛等症状，皮肤黏膜红，眼结膜充血。口腔黏膜、胸背、腋下出现大小不等的出血点或淤斑。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">晚期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">随着肾功能的逐渐恢复，尿量减至</span><span style=\"font-size:18px;\">3000ml</span><span style=\"font-size:18px;\">以下时，即进入恢复期。尿量、症状逐渐恢复正常，复原需数月。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">眼眶疼痛</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">食欲减退</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">面颊及上胸部充血</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">头昏</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>流行性出血热预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">一、预防</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">流行性出血热是由汉坦病毒引起，以鼠类为主要传染源，通过接触、呼吸道、消化道等多种途径传播的急性传染病。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 流行性出血热病毒对对紫外线、乙醇和碘酒敏感</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">对氯仿、丙酮等脂溶剂和去氧胆盐敏感。</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">20</span><span style=\"font-size:18px;\">℃温度下相对稳定，高于</span><span style=\"font-size:18px;\">37</span><span style=\"font-size:18px;\">℃及</span><span style=\"font-size:18px;\">pH5.0</span><span style=\"font-size:18px;\">以下易灭活</span><span style=\"font-size:18px;\">;56</span><span style=\"font-size:18px;\">℃ </span><span style=\"font-size:18px;\">30min</span><span style=\"font-size:18px;\">或</span><span style=\"font-size:18px;\">100</span><span style=\"font-size:18px;\">℃ </span><span style=\"font-size:18px;\">1min</span><span style=\"font-size:18px;\">可灭活。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 流行性出血热的早期症状是发热，</span><span style=\"font-size:18px;\">“三痛”</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">头痛、腰痛、眼眶痛</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">，“三红”</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">颜面、颈、上胸部潮红</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">，皮肤黏膜出血及肾脏损害等。该病病毒可以侵犯人的多个器官和系统，目前没有特效的治疗方法。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">出现上述症状应及时到医院就诊，确诊后立即进行隔离治疗。对病人用过、接触过的物品进行消毒。与病人有过接触者，发现不适，应立即去医院就诊。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">专家提示：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(1)</span><span style=\"font-size:18px;\">强调早发现、早休息、早治疗和就近治疗</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(2)</span><span style=\"font-size:18px;\">发现有死老鼠应深埋或焚烧，接触死老鼠时应戴手套或使用器具</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(3)</span><span style=\"font-size:18px;\">家中食物不要裸露摆放，以防老鼠的分泌物将食物污染</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(4)</span><span style=\"font-size:18px;\">野外作业时要注意灭鼠，避免与鼠类及其排泄物、分泌物接触</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(5)</span><span style=\"font-size:18px;\">接种流行性出血热疫苗。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">二、预后</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 本病病死率与病型轻重、治疗迟早及措施是否正确有关。近年来通过早期诊断和治疗措施的改进，病死率由</span><span style=\"font-size:18px;\">10%</span><span style=\"font-size:18px;\">下降为</span><span style=\"font-size:18px;\">5%</span><span style=\"font-size:18px;\">以下。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 在我国一般认为汉坦型病毒感染病死率高于汉城型病毒感染。重型病人的病死率仍较高。主要死亡原因是休克、尿毒症、肺水肿、出血</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">主要是脑出血和肺出血等</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">。由于治疗措施得当，因休克、尿毒症、肺水肿等而死亡的病例逐渐减少，而死于出血的病例相对增多。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">本病患者恢复期后仍可出现慢性肾功能损害、高血压或腺垂体功能减退，说明预后不良。</span> \r\n</p>', 'hsg', 'upload/1650938244531.jpg', 1, '', '2022-04-26 09:57:26');
INSERT INTO `xinwentongzhi` VALUES (102, '淋病的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>淋病症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">典型症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">尿道局部肿胀 尿道口痒 尿道口有白膜形成 白色黏液状尿 脓尿</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">尿道局部肿胀</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">尿道口痒</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">尿道口有白膜形成</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">白色黏液状尿</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">脓尿</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>淋病预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span> <span style=\"font-size:18px;\">1. </span><span style=\"font-size:18px;\">宣传性传播疾病知识，提倡高尚的道德情操，严禁嫖娼卖淫。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2. </span><span style=\"font-size:18px;\">使用安全套，可降低淋球菌感染发病率。可在性交前后各服用氟哌酸或阿莫西林，可有效的预防性病的感染。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3. 30</span><span style=\"font-size:18px;\">天内接触过患者的性伴侣应做淋菌检查，并进行预防性治疗。性伴同时治疗。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4. </span><span style=\"font-size:18px;\">淋病的预防还应注意应禁止淋病病人与儿童，特别是女孩同床、共浴或公用浴盆、浴巾等。在患病率较高的地区，应对所有新生儿用硝酸银溶液或其他有效的抗生素滴眼液点眼，防止发生淋菌性眼炎。</span> \r\n</p>', 'hsg', 'upload/1650938216228.jpg', 1, '', '2022-04-26 09:56:58');
INSERT INTO `xinwentongzhi` VALUES (103, '布鲁氏菌病的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>布鲁氏菌病症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">典型症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">局部脓肿，或很复杂而表现为几个脏器和系统同时受累。少数患者有至数日的前驱症状，如无力、失眠、低热、食欲症、上呼吸道炎等。急性期的主要临床表现为发热、多汗、乏力、关节炎、睾丸炎等。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">布鲁氏菌病症状诊断</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 布鲁氏菌病临床表现复杂多变、症状各异，轻重不一，呈多器官病变或局限某一局部。根据</span><span style=\"font-size:18px;\">1977</span><span style=\"font-size:18px;\">年九月我国北方防治地方病领导小组办公室颁发的“人布鲁氏菌病的诊断和治疗效果判定试行标准”，临床上分型为：多性期；慢性活动型；慢性期相对稳定型。国外按鲁德涅夫</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">руд</span><span style=\"font-size:18px;\">HEB)</span><span style=\"font-size:18px;\">分期法分为：急性期，指患病</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">个月以内；亚急性期，</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">个月到</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">年；慢性期，</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">年以上。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">一、潜伏期</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 为</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">60</span><span style=\"font-size:18px;\">天，平均两周。少数患者可长达数月或</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">年以上。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">二、急性期</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">80%</span><span style=\"font-size:18px;\">起病缓慢，常出现前驱症状，其表现颇似重感冒。全身不适，疲乏无力，食纳减少，头痛肌痛、烦躁或抑郁等。持续</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">天。</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">27%</span><span style=\"font-size:18px;\">患者急骤起病，以寒战高热，多汗，游走性关节痛为主要表现。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">、布鲁氏菌病发热</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">76.8%</span><span style=\"font-size:18px;\">以上有发热</span><span style=\"font-size:18px;\">.</span><span style=\"font-size:18px;\">典型病例热型呈波浪状，初起体温逐日升高，达高峰后缓慢下降，热程约</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">周，间歇数日至</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">周，发热再起，反复数次。但据</span><span style=\"font-size:18px;\">729</span><span style=\"font-size:18px;\">例热型分析，目前呈典型波状热仅占</span><span style=\"font-size:18px;\">15.78%</span><span style=\"font-size:18px;\">，低热占</span><span style=\"font-size:18px;\">42.11%</span><span style=\"font-size:18px;\">，不规则热占</span><span style=\"font-size:18px;\">15.36%</span><span style=\"font-size:18px;\">，间歇热为</span><span style=\"font-size:18px;\">12.76%</span><span style=\"font-size:18px;\">，其它尚有弛张热、稽留热型等。热前多伴寒战畏寒。高热患者意识清晰，部分还可以下床活动，而热退后反感症状恶化，抑郁寡欢，软弱无力。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">、多汗</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">本病的突出症状之一，每于夜间或凌晨退热时大汗淋漓。也有患者发热不高或处于发热间歇期仍多汗。汗味酸臭。盛汗后多数感软弱无力，甚至可因大汗虚脱。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">、关节痛</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">76.09%</span><span style=\"font-size:18px;\">以上有关节痛，与发热并行。疼痛呈锥刺样或钝痛，痛剧者似风湿，辗转呻吟。但关节疼痛程度与病理改变并不平行。病变主要累及大关节，如髋、肩、膝等，单个或多个，非对称性，局部红肿。也可表现为滑膜炎，腱鞘炎、关节周围炎。少数表现为化脓性关节炎。急性期患者疼痛多呈游走性，慢性期病变已定局，疼痛固定某些关节。肌肉也痛，尤其下肢肌及殿肌，重者呈痉挛性痛。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">、泌尿生殖系病症</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">因睾丸炎及附睾炎引起睾丸肿瘤是男性患者常见症状之一，多为单侧。个别病例可有鞘膜积液、肾盂肾炎。女性患者可有卵巢炎、子宫内膜炎及乳房肿痛。但人类引起流产者少。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">、其它</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">坐骨神经、腰神经、肋间神经、三叉神经等均可因神经根受累而疼痛。脑膜、脑脊膜受累可发生剧烈头痛和脑膜刺激症。其次还可出现肝脾肿大，淋巴结肿大以及皮疹。部分患者还可出现顽固性咳嗽，咳白色泡沫痰、鼻衄、便血等。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">三、慢性期</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">由急性期发展而来，也可缺乏急性病史由无症状感染者或轻症者逐渐变为慢性。慢性期症状多不明显，也有典型，呈多样表现。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 慢性期活动型者</span> <span style=\"font-size:18px;\">具有急性期的表现，也可长期低热或无热，疲乏无力，头痛，反应迟钝，精神抑郁，神经痛，关节痛，一般局限某一部位，但重者关节强直，变形。一部分患者自述症状很多，缺乏体征，类似神经官能症；另一部分患者表现多器官和系统损害，如骨骼肌肉持续不定的钝痛，反反复复，迁延不愈，晚期有的发展成为关节强直，肌肉挛缩，畸形，瘫痪。神经系统表现为神经炎、神经根炎，脑脊髓膜炎。泌尿生殖系统，可有睾丸炎、附睾炎、卵巢炎、子宫内膜炎等。心血管系统可有支气管炎或支气管肺炎。另外尚有肝脾肿大，淋巴结肿大；视网膜血栓性静脉炎，视神经炎；乳突炎及听神经损伤等。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">慢性期相对稳定型者，症状、体征较固定，功能障碍仅因气候变化，劳累过度才加重。但久病后体力衰竭、营养不良、贫血。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">牛种型病例易表现为慢性，羊种型和猪种型病例病情较重，并发症较多。近年来本病有逐渐轻化的趋势，可能与预防接种及抗生素的普遍应用有关。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 患病后复发率</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">10%</span><span style=\"font-size:18px;\">，常在</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">个月以内发生。可能是细菌为细胞内寄生，不易为抗生素杀灭或者与疗程不够有关。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>布鲁氏菌病预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">治疗前的注意事项：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">、早治疗</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">诊断一经确立，立即给予治疗，以防疾病向慢性发展。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">、联合用药，剂量足，疗程够</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">一般联合两种抗菌药，连用</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">个疗程。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">、中医结合</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">中医包括蒙医、藏医和汉医。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">、综合治疗</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">以药为主，佐以支持疗法，以提高患者抵抗力</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">增强战胜疾病的信心。</span> \r\n</p>', 'hsg', 'upload/1650938182689.jpg', 2, '', '2022-04-26 09:56:24');
INSERT INTO `xinwentongzhi` VALUES (104, '猩红热的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>猩红热症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">早期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">突然高热、头痛、咽痛、恶心、呕吐等。若细菌是从咽部侵入的，则扁桃体红肿，可有灰白色易被擦去的渗出性膜，软腭粘膜充血，有点状红斑及散至性瘀点，出疹之前即可见舌乳头红肿肥大，突出于白色舌苔之中，称为“白色杨梅舌”。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">晚期症状：</span><span style=\"font-size:18px;\"> 3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">天后，白色舌苔脱落，舌色鲜红，舌乳头红肿突出，状似杨梅，称“红色杨梅舌”，同时伴有颌下淋巴结肿大。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">高热</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">杨梅样舌</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">猩红热样皮疹</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">咽痛</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">头痛</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">全身皮肤充血</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">咽喉炎</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">淋巴结肿大</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">发烧</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">斑疹</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">口腔粘膜弥漫充血</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>猩红热预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">预防</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 目前此病没有自动免疫制剂，预防着重于控制感染的散播。隔离患儿，至咽培养连续两次阴性后解除隔离。对体弱及免疫功能低下的密切接触者，应服复方新诺明或注射青霉素预防。带菌者应接受</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">天青霉素治疗。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">一</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">管理传染源病人及带菌者隔离</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">天。有人主张用青霉素治疗</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">天，可使</span><span style=\"font-size:18px;\">95%</span><span style=\"font-size:18px;\">左右的患者咽试子培养阴转，届时即可出院。当儿童机构或新兵单位发现病人后，应予检疫至最后一个病人发病满</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">周为止。咽试子培养持续阳性者应延长隔离期。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">二</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">切断传播途径流行期间 ，小儿应避免到公共场所，住房应注意通风。对可疑猩红热、咽峡炎患者及带菌者，都应给予隔离治疗。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">三</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">保护易感者对儿童机构、部队或其它有必要的集体，可酌情采用药物预防。如用苄星青霉素</span><span style=\"font-size:18px;\">(Penicillin G Benzathine)</span><span style=\"font-size:18px;\">，儿童</span><span style=\"font-size:18px;\">60</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">90</span><span style=\"font-size:18px;\">万μ，成人</span><span style=\"font-size:18px;\">120</span><span style=\"font-size:18px;\">万μ，可保护</span><span style=\"font-size:18px;\">30</span><span style=\"font-size:18px;\">天。或磺胺嘧啶每天</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">克或周效碘胺</span><span style=\"font-size:18px;\">(Sulfadoxinum)</span><span style=\"font-size:18px;\">每周</span><span style=\"font-size:18px;\">0.5</span><span style=\"font-size:18px;\">克。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">四</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">宝宝易感猩红热，居家防护对策</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">少带宝宝去公共场所，多去空气清新的花园玩耍或散步。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 幼儿园发生流行时，可每天采用盐水漱口，对患病的宝宝应马上进行隔离治疗，一般治疗</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">天后方可解除隔离，以免传染给别的宝宝。无论是幼儿还是大人，只要与猩红热患者有过密切接触，都应在医生的指导下赶快服药预防，如服用复方新诺明片、注射青霉素等，尤其是曾经患过肾炎或风湿热的宝宝。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">其他注意事项</span><span style=\"font-size:18px;\">:</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">一</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">化脓性并发症 可由本病病原菌或其他细菌直接侵袭附近组织器官所引起。常见的如中耳炎、乳突炎、鼻旁窦炎、颈部软组织炎、蜂窝织炎、肺炎等。由于早期应用抗菌疗法，此类并发症已少见。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">二</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">中毒性并发症 由细菌各种生物因子引起，多见于第</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">周。如中毒性心肌炎、心包炎等。病变多为一过性，且预后良好。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">三</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">变态反应性并发症 一般见于恢复期，可出现风湿性关节炎、心肌炎、心内膜炎、心包炎及急性肾小球肾炎。并发急性肾炎时一般病性轻，多能自愈，很少转为慢性。</span> \r\n</p>', 'hsg', 'upload/1650938154220.jpg', 1, '', '2022-04-26 09:55:56');
INSERT INTO `xinwentongzhi` VALUES (105, '白喉的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>白喉症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">典型症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">颌下淋巴结肿大，压痛扁桃体，充血发烧，咽痛，紫绀，疲乏，食欲不振，乏力，厌食。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">白喉假膜形成</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">扁桃体上有灰色膜</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">扁桃体充血</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">干咳</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">一、症状</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 临床表现</span> <span style=\"font-size:18px;\">潜伏期</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">天，一般为</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">天。根据假膜所在部位及中毒症状轻重，分为下列类型：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">、咽白喉最常见</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">占发病人数的</span><span style=\"font-size:18px;\">80%</span><span style=\"font-size:18px;\">左右。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(1).</span><span style=\"font-size:18px;\">无假膜的咽白喉</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">白喉流行时，部分患者仅有上呼吸道症状如咽痛，全身中毒症状较轻。咽部只有轻度炎症。扁桃体可肿大，但无假膜形成，或仅有少量纤维蛋白性渗出物，细菌培养阳性。此类患者易被误诊和漏诊。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(2).</span><span style=\"font-size:18px;\">局限性咽白喉</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">①咽门白喉</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">假膜局限于腭弓、悬雍垂等处，症状较轻。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">②扁桃体白喉</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">假膜局限于一侧或双侧扁桃体。起病徐缓，自觉症状轻和中度发热，全身不适，疲乏，食欲不振及轻度咽痛。扁桃体充血、稍肿胀，假膜初呈点状后融合成片。颌下淋巴结可肿大，微痛。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(3).</span><span style=\"font-size:18px;\">中毒型咽白喉</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">主要由局限型及播散型转变而成。大多伴有混合感染。如不及时治疗，病死率极高。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 此型表现症状：假膜多因出血而呈黑色，扁桃体及咽部高度肿胀、阻塞咽门，或有坏死而形成溃疡，具特殊腐败臭气。颈淋巴肿大，周围软组织水肿，以致颈部增粗</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">牛颈</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">。全身中毒症状严重，有高热、气促、唇紫绀、脉细而快、心律失常等。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(4).</span><span style=\"font-size:18px;\">播散咽白喉</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">假膜由扁桃体扩展到悬雍垂、软腭、咽后壁、鼻咽部或喉头。假膜色灰白或黄白，边界清楚，周围组织红肿较重。双侧扁桃体肿大，甚至充塞咽门，导致呼吸困难。颈部淋巴结肿大、周围有水肿。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 此型表现症状：全身中毒症状重，有高热、乏力、厌食、咽痛等</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">重症病例可引起循环衰竭。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">、鼻白喉</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">此型较为少见，多见于婴幼儿。全身症状轻微或无，有鼻塞、流浆液血性鼻涕，鼻孔周围可见表皮剥脱或浅溃汤，鼻前庭可见白色假膜。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">、喉白喉</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">喉白喉约占</span><span style=\"font-size:18px;\">20%</span><span style=\"font-size:18px;\">左右。少数为原发性，约</span><span style=\"font-size:18px;\">3/4</span><span style=\"font-size:18px;\">为咽白喉向下蔓延而成。原发性喉白喉由于毒素吸收少，全身中毒症状并不严重。继发性喉白喉常发生在咽白喉基础上，伴有喉白喉的临床表现，全身中毒症状严重。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">但少数由于假膜延及气管、支气管，可造成程度不等的硬阻现象，症状表现为：粗糙的干咳，声音嘶哑、甚至失声，呼吸急促。严重者可出现紫绀，刀可因窒息而死亡。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">、其他部位的白喉白喉杆菌</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">可从侵入眼结合膜、耳、外阴部、新生儿脐带及皮肤损伤处，产生假膜及化脓性分泌物。眼、耳及外阴部白喉多为继发性。皮肤白喉在热带地区较多见，病程长，皮损往往经久不愈，愈合后可有黑色素沉着。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">此型表现症状：全身中毒症状，但可发生末梢神经麻痹。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>白喉预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">一）控制传染源</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">、早期发现</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">及时隔离治疗病人，直至连续</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">次咽拭子白喉杆菌培养阴性，可解除隔离。如无培养条件，起病后隔离</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">周。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">对密切接触者</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">观察</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">天。对没有接受白喉类毒素全程免疫的幼儿，最好给予白喉类毒素与抗毒毒同时注射。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3.</span><span style=\"font-size:18px;\">带菌者</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">予青霉素或红霉素治疗</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">天，细菌培养</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">次阴性始能解除隔离。如用药无效者可考虑扁桃体摘除。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">二</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">切断传播途径呼吸道隔离</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">病人接触过的物品及分泌物，必须煮沸或加倍量的</span><span style=\"font-size:18px;\">10%</span><span style=\"font-size:18px;\">漂白粉乳剂或</span><span style=\"font-size:18px;\">5%</span><span style=\"font-size:18px;\">石炭酸溶液浸泡</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">小时。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">三</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">提高机体免疫力</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">对学龄前儿童应预防接种百、白、破三联疫苗，可产生良好免疫力。</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">月龄的幼儿即可开始免疫，皮下注射</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">次</span><span style=\"font-size:18px;\">(0.5</span><span style=\"font-size:18px;\">、</span><span style=\"font-size:18px;\">1.0</span><span style=\"font-size:18px;\">、</span><span style=\"font-size:18px;\">1.0ml)</span><span style=\"font-size:18px;\">，每次间隔</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">周，</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">年后和入学前各加强注射</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">次。</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">岁以上儿童首次免疫注射，应以白喉和破伤风类毒素开始。对白喉易感者或体弱多病者可用抗毒素作被动免疫，成人</span><span style=\"font-size:18px;\">1000</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">2000</span><span style=\"font-size:18px;\">μ肌注，儿童</span><span style=\"font-size:18px;\">1000</span><span style=\"font-size:18px;\">μ，有效期仅</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">周。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 儿童应定期接受白喉计划免疫。白喉类毒素一般是与破伤风类毒素和百日咳菌苗混合，作为百日咳</span><span style=\"font-size:18px;\">—白喉—破伤风三联疫苗而使用。如果已经接种了白喉免疫的人与已受感染的人接触，应加强注射一次疫苗以增加保护性。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 预后：白喉预后好坏与病人年龄、病型、有无并发症和治疗的早晚有关。婴幼儿的病死率较年长儿童和成人为高。重型咽白喉可产生严重中毒性症状，多在病后第</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">周出现中毒性心肌炎，是白喉的主要死因。喉白喉可引起窒息。</span> \r\n</p>', 'hsg', 'upload/1650938135092.jpg', 1, '', '2022-04-26 09:55:37');
INSERT INTO `xinwentongzhi` VALUES (106, '百日咳的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>百日咳症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">早期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">有咳嗽、打喷嚏、流涕、流泪，有低热或中度发热，</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">天后症状消失，热退，但咳嗽逐渐加重，尤以夜间为重。此期传染性最强，可持续</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">天，若及时治疗，能有效地控制本病的发展。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">晚期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">未能控制，就会出现阵发性痉挛性咳嗽，一次比一次加剧，直至咳出大量黏稠痰液和呕吐胃内容物而止。痉咳发作前有诱因，发作时常有喉痒、胸闷等不适预兆。。婴幼儿由于声门较小可因声带痉挛和黏稠分泌物的堵塞而发生呼吸暂停，因缺氧而出现发绀，甚至于抽搐，亦可因窒息而死亡。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">咳嗽</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">喉部痉挛</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">流鼻涕</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">打喷嚏</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">口唇发绀</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">流泪</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">一、症状：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">根据接触史及典型的痉咳期表现，如无典型痉咳者可结合典型血象改变，均可作出临床诊断。病原学诊断有赖于细菌培养和特异的血清学检查。对各年龄组不明原因的持续性咳嗽，特别有痉咳症状者，均需考虑本病的可能，作进一步的检测。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 潜伏期</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">21</span><span style=\"font-size:18px;\">天，平均</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">天，典型临床经过分</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">期。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">卡他期或称痉咳前期 起病时有咳嗽、打喷嚏、流涕、流泪，有低热或中度发热，类似感冒症状。</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">天后症状消失，热退，但咳嗽逐渐加重，尤以夜间为重。此期传染性最强，可持续</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">天，若及时治疗，能有效地控制本病的发展。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">痉咳期 卡他期未能控制，患者出现阵发性痉挛性咳嗽，其特点是频繁不间断的短咳</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">余声，如呼气状态，最后深长呼气，此时由于咳嗽而造成胸腔内负压，加之吸气时，声带仍处于紧张状态，空气气流快速地通过狭窄的声门而发出一种鸡鸣样高音调的吸气声，接着又是一连串阵咳。如此反复发作，一次比一次加剧，直至咳出大量黏稠痰液和呕吐胃内容物而止。痉咳发作前有诱因，发作时常有喉痒、胸闷等不适预兆。患儿预感痉咳来临时，表现恐惧，痉咳发作时表情是痛苦的。痉咳时由于胸腔内压力增加，上腔静脉回流受阻，颈静脉怒张，眼睑及颜面充血水肿，口唇发绀，眼结膜充血，如毛细血管破裂可引起球结膜下出血及鼻出血。有的患者舌向齿外伸，与门齿摩擦，常见有舌系带溃疡。有的患者因阵咳，腹压增高使大小便失禁及出现疝症。此期如无并发症发生，一般持续</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">周，也有长达</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">个月或以上。婴幼儿和新生儿百日咳症状比较特殊，无典型痉咳，由于声门较小可因声带痉挛和黏稠分泌物的堵塞而发生呼吸暂停，因缺氧而出现发绀，甚至于抽搐，亦可因窒息而死亡。成人或年长儿童，百日咳症状轻，而且不典型，主要表现为干咳，无阵发性痉咳，白细胞和淋巴细胞增加不明显，大多被误诊为支气管炎或上呼吸道感染。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3.</span><span style=\"font-size:18px;\">恢复期 阵发性痉咳次数逐渐减少至消失，持续</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">周好转痊愈。若有并发肺炎，肺不张等常迁延不愈，可长达数周之久。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">支气管肺炎是常见的并发症，多发生在痉咳期。还可并发百日咳脑病，患者意识障碍、惊厥，但脑脊液无变化。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">二、诊断标准：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 诊断依据</span> <span style=\"font-size:18px;\">根据当地流行情况，有无百日咳患者接触史。若患儿曾有发热，但热退后咳嗽症状反而加重，特别在晚间咳嗽剧烈，且无明显肺部阳性体征，应作为疑似诊断。若有明显痉咳，外周血计数白细胞及淋巴细胞分类均明显增高则根据这些特点可作出百日咳临床诊断。加之细菌培养阳性或血清学免疫学、</span><span style=\"font-size:18px;\">PCR</span><span style=\"font-size:18px;\">检查阳性可以确诊百日咳。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>百日咳预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">预防：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">控制传染源 在流行季节，凡确诊的患者应立即隔离至病后</span><span style=\"font-size:18px;\">40</span><span style=\"font-size:18px;\">天，或隔离至痉咳后</span><span style=\"font-size:18px;\">30</span><span style=\"font-size:18px;\">天。对接触者应密切观察至少</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">周，若有前驱症状应及早抗生素治疗。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">切断传播途径 由于百日咳杆菌对外界抵抗力较弱，无需消毒处理，但应保持室内通风，衣物在阳光下曝晒，对痰液及口鼻分泌物则应进行消毒处理。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3.</span><span style=\"font-size:18px;\">提高人群免疫力 目前已用于预防接种的百日咳菌苗有全细胞菌苗和无细胞菌苗，全细胞菌苗为常规菌苗</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">即百日咳、白喉、破伤风</span><span style=\"font-size:18px;\">(DTP)</span><span style=\"font-size:18px;\">制剂，用量每</span><span style=\"font-size:18px;\">0.5ml</span><span style=\"font-size:18px;\">内含百日咳</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">个保护单位。</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">个月婴儿进行基础免疫，皮下</span><span style=\"font-size:18px;\">0.5</span><span style=\"font-size:18px;\">，</span><span style=\"font-size:18px;\">1.0</span><span style=\"font-size:18px;\">，</span><span style=\"font-size:18px;\">1.0ml</span><span style=\"font-size:18px;\">，共</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">次，每次间隔</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">周。流行期时</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">个月婴儿即可接受疫苗，</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">岁时再加强肌内注射。</span><span style=\"font-size:18px;\">DTP</span><span style=\"font-size:18px;\">菌苗亦有人提倡正常婴儿和儿童</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">月龄进行第</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">次，</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">月龄第</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">次，</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">月龄第</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">次，</span><span style=\"font-size:18px;\">15</span><span style=\"font-size:18px;\">个月第</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">次，</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">岁第</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">次。由于年长儿或成人免疫力降低仍可感染百日咳，</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">岁以后每</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">年进行</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">次。该菌苗对出生时有外伤史、过敏史、家族中有精神神经病史、本人有惊厥史、进行性神经系统疾病及存在急性感染时禁忌接种百日咳菌苗。一般接种后在局部注射处有疼痛，轻度或中等度发热等症状，极少者在接种后数日至数周后出现惊厥等脑部症状。国内外近年来一直在研制百日咳新菌苗，现已报道无细胞菌苗含有淋巴细胞增多促进因子</span><span style=\"font-size:18px;\">(LPF)</span><span style=\"font-size:18px;\">与丝状血凝素</span><span style=\"font-size:18px;\">(FHA)</span><span style=\"font-size:18px;\">中单一种抗原成分或全部，其效力似乎与</span><span style=\"font-size:18px;\">DTP</span><span style=\"font-size:18px;\">制剂相当，已在日本、瑞士进行了现场试验，认为该疫苗安全、有效，但须深入系列研究证实。除此外，意大利研制出新型</span><span style=\"font-size:18px;\">DNA</span><span style=\"font-size:18px;\">重组百日咳菌苗，目前正进行Ⅱ期试验，认为可能为百日咳提供安全、有效菌苗。我国于</span><span style=\"font-size:18px;\">1985</span><span style=\"font-size:18px;\">年后对无细胞百日咳菌苗进行了系统试验，已获得有效的百日咳菌苗制剂。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4.</span><span style=\"font-size:18px;\">药物预防 对婴幼儿及体弱小儿，未经预防接种而与百日咳患者密切接触者，可选用百日咳免疫球蛋白</span><span style=\"font-size:18px;\">2.5ml</span><span style=\"font-size:18px;\">肌内注射，或恢复期血清</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">20ml</span><span style=\"font-size:18px;\">肌内注射，</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">天重复注射</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">次，连续</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">次，可使其暂不发病。近来证实红霉素对百日咳接触者进行预防可降低百日咳的感染率，剂量</span><span style=\"font-size:18px;\">30</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">50mg/kg</span><span style=\"font-size:18px;\">体重，分</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">次口服，连服</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">天，有助于控制百日咳传播。亦有人选用复方磺胺甲噁唑，半岁内婴儿可选用该药乳剂</span><span style=\"font-size:18px;\">5ml(</span><span style=\"font-size:18px;\">含</span><span style=\"font-size:18px;\">TMP40mg</span><span style=\"font-size:18px;\">，</span><span style=\"font-size:18px;\">SMZ200mg)</span><span style=\"font-size:18px;\">，</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">次</span><span style=\"font-size:18px;\">/d</span><span style=\"font-size:18px;\">，连续</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">天。</span> \r\n</p>', 'hsg', 'upload/1650938108539.jpg', 1, '', '2022-04-26 09:55:11');
INSERT INTO `xinwentongzhi` VALUES (107, '伤寒与副伤寒的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>伤寒与副伤寒症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">早期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">全身不适、乏力、食欲减退、咽痛与咳嗽</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">晚期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">高热持续不退，呈稽留热型，少数呈弛张热型或不规则热型，持续约</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">14</span><span style=\"font-size:18px;\">天。消化系统症状，食欲不振较前更为明显，舌尖与舌缘的舌质红苔厚腻</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">稽留热</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">不规则热</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">肠出血</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">腹部不适</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">反应迟钝</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">呆滞</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">表情淡漠</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">一、症状</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">）典型的伤寒自然病程为时约</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">周，可分为</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">期</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">一</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">初期相当于病程第</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">周</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">起病大多缓慢</span><span style=\"font-size:18px;\">(75%</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">90%)</span><span style=\"font-size:18px;\">，发热是最早出现的症状，常伴有全身不适，乏力，食欲减退，咽痛与咳嗽等，病情逐渐加重，本温呈阶梯形上升，于</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">天内达</span><span style=\"font-size:18px;\">39</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">40</span><span style=\"font-size:18px;\">℃，发热前可有畏寒而少寒战，退热时出汗不显著。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">二</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">极期相当于病程第</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">周</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">常有伤寒的典型表现，有助于诊断。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span> <span style=\"font-size:18px;\"> </span><span> </span><span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">高热高热持续不退</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">多数</span><span style=\"font-size:18px;\">(50%</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">75%)</span><span style=\"font-size:18px;\">呈稽留热型，少数呈弛张热型或不规则热型，持续约</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">14</span><span style=\"font-size:18px;\">天。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">消化系统症状食欲不振较前更为明显</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">舌尖与舌缘的舌质红，苔厚腻</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">即所谓伤寒舌</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">，腹部不适，腹胀，多有便秘，少数则以腹泻为主，由于肠道病多在回肠末段与回盲部，右下腹可有轻度压痛。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3.</span><span style=\"font-size:18px;\">神经系统症状与疾病的严重程度成正比</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">是由于伤寒杆菌内毒素作用中枢神经系统所致，患者精神恍惚，表情淡漠，呆滞，反应迟钝，听力减退，重者可有谵妄，昏迷或出现脑膜刺激征</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">虚性脑膜炎</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">，此等神经系统症状多随体温下降至逐渐恢复。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4.</span><span style=\"font-size:18px;\">循环系统症状常有相对缓脉</span><span style=\"font-size:18px;\">(20%</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">73%)</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">或有时出现重脉是本病的临床特征之一，但并发中毒性心肌炎时，相对缓脉不明显。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">5.</span><span style=\"font-size:18px;\">脾肿大病程第</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">天开始</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">在左季肋下常可触及腺肿大</span><span style=\"font-size:18px;\">(60%</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">80%)</span><span style=\"font-size:18px;\">，质软或伴压痛，少数患者肝脏亦可肿大</span><span style=\"font-size:18px;\">(30%</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">40%)</span><span style=\"font-size:18px;\">，质软或伴压痛，重者出现黄疸，肝功能有明显异常者，提示中毒性肝炎存在。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">6.</span><span style=\"font-size:18px;\">皮疹病程</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">13</span><span style=\"font-size:18px;\">天</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">部分患者</span><span style=\"font-size:18px;\">(20%</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">40%)</span><span style=\"font-size:18px;\">的皮肤出现淡红色小斑丘疹</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">玫瑰疹</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">，直径约</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">4mm</span><span style=\"font-size:18px;\">，压之退色，为数在</span><span style=\"font-size:18px;\">12</span><span style=\"font-size:18px;\">个以下，分批出现，主要分布于胸，腹，也可见于背部及四肢，多在</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">天内消失，水晶形汗疹</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">或称白痱</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">也不少见，多发生于出汗较多者。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">三</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">缓解期相当于病程第</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">周</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">人体对伤寒杆菌的抵抗力逐渐增强，体温出现波动并开始下降，食欲逐渐好转，腹胀逐渐消失，脾肿开始回缩，但本期内有发生肠出血或肠穿孔的危险，需特别提高警惕。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">四</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">恢复期相当于病程第</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">周末开始</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">体温恢复正常，食欲好转，一般在</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">个月左右完全恢复健康。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">）其它临床类型</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">除上述典型伤寒外，根据发病年龄，人体免疫状态，致病菌的毒力与数量，病程初期不规则应用抗菌药物以及有无加杂症等因素，伤寒又可分为下列各种类型。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">一</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">轻型全身毒血症状轻</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">病程短，</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">周内痊愈，多见于发病前曾接受伤寒菌苗注射或发病初期已应用过有效抗菌药物治疗者，在儿童病例中亦非少见，由于病情轻，症状不典型，易致漏诊或误诊。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">二</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">暴发型起病急</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">毒血症状严重，有畏寒，高热，腹痛，腹泻，中毒性脑病，心肌炎，肝炎，肠麻痹，休克等表现，常有显著皮疹，也可并发</span><span style=\"font-size:18px;\">DIC</span><span style=\"font-size:18px;\">。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">三</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">迁延型起病与典型伤寒相似</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">由于人体免疫功能低下，发热持续不退，可达</span><span style=\"font-size:18px;\">45</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">60</span><span style=\"font-size:18px;\">天之久，伴有慢性血吸虫病的伤寒患者常属此型。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 伤寒合并血吸虫病时，起病多急，发热常为弛张型或间歇型，肝脾肿大较著，嗜酸粒细胞在发热期及极期显著减少或消失，热退后回升，常超过正常值，热程可迁延</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">月。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">四</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">小儿伤寒一般年龄越大</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">临床表现越似于成人，年龄越小，症状越不典型。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 学龄期儿童症状与成人相似，但多属轻型，常急性起病，有持续发热，食欲不振，腹痛，便秘，表情淡漠，嗜睡，烦躁鼻衄，舌苔厚，腹胀及肝脾肿大等，而缓脉及玫瑰疹少见，白细胞计数常不减少，病程较短，有时仅</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">周即自然痊愈，由于肠道病变轻，故肠出血，肠穿孔等并发症也较少。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">婴幼儿伤寒常不典型，病程亦轻重，起病急，伴有呕吐，惊厥，不规则高热，脉快，腹胀，腹泻等，玫瑰疹少见，白细胞计数常增多，并发支气管炎或肺炎颇为常见。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">五</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">老年伤寒体温多不高</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">症状多不典型，虚弱现象明显</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">易并发支气管肺炎与心功能不全，常有持续的肠功能紊乱和记忆力减退，病程迁延，恢复不易，病死率较高。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 复发与再燃：症状消失后</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">周，临床表现与初次发作相似，血培养阳转为复发，复发的症状较轻，病程较短，与胆囊或网状内皮系统中潜伏的病菌大量繁殖，再度侵入血循环有关</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">疗程不足，机体抵抗力低下时易见，偶可复发</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">次，再燃是指病程中，体温于逐渐下降的过程中又重升高，</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">天后方正常，血培养常阳性，机理与初发相似。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>伤寒与副伤寒预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 本病预防重点是加强饮水、饮食卫生和粪便管理，切断传播途径。注射死菌菌苗的保护作用不满意。近年推崇口服减毒菌苗</span><span style=\"font-size:18px;\">(Ty2la)</span><span style=\"font-size:18px;\">，已在中东、拉美一些国家试用，但效果大多有限。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">、不要到卫生条件差的摊点、餐馆就餐。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">、不生吃或半生吃毛蚶、牡蛎、蛏子等海产品。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">、养成良好的卫生习惯，不喝生水，饭前便后要洗手。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">、凡有不明原因持续发热病人，要及时到医院诊断治疗，以免延误病情。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">、家中以及周围有伤寒病人时，更要注意自我保护。对可能污染的物品可选用煮沸、消毒药浸泡等方式消毒。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">、应急性预防服药，可用复方新诺明</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">片，每天两次，服用</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">—</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">天。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">、应急接种：对疫情爆发地区及毗临地区的重点人群进行伤寒菌苗的预防接。</span> \r\n</p>', 'hsg', 'upload/1650938093154.jpg', 2, '', '2022-05-14 21:37:18');
INSERT INTO `xinwentongzhi` VALUES (108, '肺结核的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>肺结核症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">典型症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">表现为午后低热、乏力、食欲减退、消瘦、盗汗等。若肺部病灶进展播散，常呈不规则高热。妇女可有月经失调或闭经。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">低烧</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">食欲减退</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">乏力</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">咳嗽</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">颜面潮红</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">消瘦</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">咳痰</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 典型肺结核起病缓慢，病程较长，有低热、倦怠、食欲不振、咳嗽及少量咯血。但多数患者病灶轻微，无显著症状，经</span><span style=\"font-size:18px;\">X</span><span style=\"font-size:18px;\">线健康检查时偶被发现。亦有以空然咯血才被确诊，追溯其病史可有轻微的全身症状。少数患者因突然起病及突出的毒性症状与呼吸道症状，而经</span><span style=\"font-size:18px;\">X</span><span style=\"font-size:18px;\">线检查确认为急性粟粒型肺结核或干酪样肺炎。老年肺结核患者，易被长年慢性支气管炎的症状所掩盖。偶见未被发现的重症肺结核，因继发感染而有高热，甚至已发展至败血症或呼吸衰竭才去就医。鉴于肺结核的临床表现常呈多样化，在结核病疫情已基本得到控制、发病率低的地区，医务人员在日常诊疗工作中尤应认识其不典型表现。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">一</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">全身症状</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">表现为午后低热、乏力、食欲减退、消瘦、盗汗等。若肺部病灶进展播散，常呈不规则高热。妇女可有月经失调或闭经。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">二</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">呼吸系统症状</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 通常为干咳或带少量粘液痰，继发感染时，痰呈粘液脓性。约</span><span style=\"font-size:18px;\">1/3</span><span style=\"font-size:18px;\">患者有不同程度咯血，痰中带血多因炎性病灶的毛细血管扩张所致</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">中等量以上咯血，则与小血管损伤或来自空洞的因管瘤破裂有关。咯血后常有低热，可能因小支气管内残留血块吸收或阻塞支气管引起的感染</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">若发热持续不退，则应考虑结核病灶播散。有时硬结钙化的结核病灶可因机械性损伤血管，或合并支气管扩张而咯血。大咯血时可发生失血性休克</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">偶因血块阻塞大气道引起窒息。此时患者极度烦躁、心情紧张、挣扎坐起、胸闷气促、发绀，应立即进行抢救。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">病灶炎症累及壁层及胸膜时，相应胸壁有刺痛，一般多不剧烈，随呼吸及咳嗽而加重。慢性重症肺结核时，呼吸功能减退，常出现渐进性呼吸困难，甚至缺氧发绀。若并发气胸或大量胸腔积液，其呼吸困难症状尤为严重。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>肺结核预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">抗结核化学药物治疗对控制结核病起决定性作用，合理化疗可使病灶内细菌消灭，最终达到痊愈。休息与营养疗法仅起辅助作用。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1).</span><span style=\"font-size:18px;\">消灭传染源</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">建立结核病监测网络，及时发现病人，积极彻底治疗，可迅速控制传染源。有效的化疗虽然需数月才能使痰菌阴转，但</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">周内可使患者的传染性降低到几乎消失。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2).</span><span style=\"font-size:18px;\">保护易感人群</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">接种卡介苗是预防结核病最有效的办法，新生儿出生时即接种，以后每</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">年补种，直至</span><span style=\"font-size:18px;\">15</span><span style=\"font-size:18px;\">岁。接种卡介苗有划痕法和皮内注射法两种。在青少年中，对结素试验阳性者，可采用</span><span style=\"font-size:18px;\">INH</span><span style=\"font-size:18px;\">化学性预防，每日</span><span style=\"font-size:18px;\">300mg</span><span style=\"font-size:18px;\">，持续半年到</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">年。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3).</span><span style=\"font-size:18px;\">切断传播途径</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">活动期病人带口罩，不随地吐痰，防止大笑和情绪激昂的讲话：保持室内通风，空气清洁，紫外线照射消毒等，都是切断传播途径的有效手段。</span> \r\n</p>', 'hsg', 'upload/1650938077311.jpg', 1, '', '2022-04-26 09:54:38');
INSERT INTO `xinwentongzhi` VALUES (109, '乙脑的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>乙脑症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">早期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">起病急，体温急剧上升至</span><span style=\"font-size:18px;\">39</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">40</span><span style=\"font-size:18px;\">℃，伴头痛、恶心和呕吐，部分病人有嗜睡或精神倦怠，并有颈项轻度强直，病程</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">天。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">晚期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">严重患者可因脑实质类（尤其是脑干病变）、缺氧、脑水肿、脑疝、颅内高压、低血钠性脑病等病变而出现中枢性呼吸衰竭，表现为呼吸节律不规则</span><span style=\"font-size:18px;\">.</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">表情淡漠</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">吞咽困难</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">烦躁不安</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">伤寒面容</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">食欲不振</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">不自主运动</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">脑膜刺激症状</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">反应迟钝</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">中枢性发热</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>乙脑预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1)</span><span style=\"font-size:18px;\">早期发现病人，及时隔离病人至体温正常为止。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2)</span><span style=\"font-size:18px;\">注意病情变化，观察体温、脉搏、呼吸、血压、瞳孔大不不等、呼吸节律失常等征象。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3)</span><span style=\"font-size:18px;\">消灭蚊虫孳生地，抓好防蚊，灭蚊措施，切断传播途径。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4)</span><span style=\"font-size:18px;\">提高人群免疫力，对易感者，尤其是</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">岁以下儿童，定期作乙脑疫苗接种，一般在流行季节前</span><span style=\"font-size:18px;\">1-2</span><span style=\"font-size:18px;\">月进行。如有瘫痪可能给予按摩等，以促进肢体功能的恢复。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 据介绍，乙脑疫苗是预防流行性乙脑最有效的方式。目前主要应用的菌苗为死疫苗，全程接种</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">周后，血清中出现中和抗体、补体结合抗体和血凝抑制抗体，</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">个月左右达高峰。其中一定量的中和抗体和血凝抑制抗体能中和进入人体的乙脑病毒，从而阻止乙脑病毒侵犯中枢神经系统，达到预防乙脑的作用。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">刚出生的婴儿并不会传染乙脑，这是因为乙脑的中和抗体和血凝抑制抗体还可以通过胎盘传递给胎儿，使新生获得来自母体的被动免疫保护。婴儿出生后，这种抗体随着婴儿月龄的增长而逐渐消失。因此，婴儿出生八个月时就要开始接种乙脑疫苗。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">由于乙脑主要经过蚊子吸食携带乙脑病毒幼猪的血后，再次叮咬易感人群而引起感染，为此日常生活要注意防蚊。</span> \r\n</p>', 'hsg', 'upload/1650938052744.jpg', 1, '', '2022-04-26 09:54:14');
INSERT INTO `xinwentongzhi` VALUES (110, '狂犬病的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>狂犬病症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">典型症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">在兴奋状态出现前大多数患者有低热、嗜睡、食欲缺乏，少数有恶心、呕吐、头痛</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">多在枕部</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">、背腰痛、周身不适等</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">对痛、声、光、风等刺激开始敏感，并有咽喉紧缩感。并对水声、光、风等刺激非常敏感，引起发作性咽肌痉挛、呼吸困难等。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">低烧</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">三怕</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">水声、光、风</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">现象</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">恐水症</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">躁狂与昏睡交替</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">喉头痉挛</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">四肢抽搐</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">呼吸异常</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">流涎</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">一、症状</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 狂犬病患者典型三恐</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">恐风、恐水、恐光</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">病症占</span><span style=\"font-size:18px;\">60%</span><span style=\"font-size:18px;\">，另</span><span style=\"font-size:18px;\">40%</span><span style=\"font-size:18px;\">为不典型症状。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 潜伏期因为个体差异导致时间差异大。大多数在</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">个月以内发病，超过半年者占</span><span style=\"font-size:18px;\">4%</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">10%</span><span style=\"font-size:18px;\">，超过</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">年以上者约</span><span style=\"font-size:18px;\">1%</span><span style=\"font-size:18px;\">，文献记载最长</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">例达</span><span style=\"font-size:18px;\">19</span><span style=\"font-size:18px;\">年。平均</span><span style=\"font-size:18px;\">20-90</span><span style=\"font-size:18px;\">天。 </span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">影响潜伏期长短的因素为年龄</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">儿童较短</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">、伤口部位</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">头、面部发病较早</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">、伤口深浅</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">深者发病早</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">、病毒入侵数量及毒株的毒力、受伤后是否进行了正规的扩创处理和接种狂犬病疫苗预防等。其他如外伤、受寒、过度劳累等均可能促使提前发病。由吸血蝙蝠啮咬而引起的狂犬病，绝大多数病例不出现兴奋期，也无咽肌痉挛和恐水现象，而以上行性瘫痪为主要临床表现。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 临床表现可分为狂躁型</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">脑炎型</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">及麻痹型</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">静型</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">两型。狂躁型狂犬病的病变主要在脑干、颈神经或更高部位中枢神经系统，麻痹型狂犬病的病变则局限于脊髓和延髓，因而造成临床症状的差异。两者都可分为下列三期：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span> <span style=\"font-size:18px;\"> </span><span> </span><span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">前驱期</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">两型的前驱期相似。在兴奋状态出现前大多数患者有低热、嗜睡、食欲缺乏，少数有恶心、呕吐、头痛</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">多在枕部</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">、背腰痛、周身不适等</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">对痛、声、光、风等刺激开始敏感，并有咽喉紧缩感。具有重大诊断意义的早期症候，是已愈合的伤口部位及神经通路上，有麻木、发痒、刺痛或虫爬、蚁走等感觉异常，约发生于</span><span style=\"font-size:18px;\">80%</span><span style=\"font-size:18px;\">的病例。这是由于病毒繁殖刺激神经元，特别是感觉神经元而引起，此症状可维持数小时至数天。本期持续</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">天，很少超过</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">天以上。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">兴奋期或痉挛期</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">可分两型，两型的表现不同。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(1)</span><span style=\"font-size:18px;\">躁狂型狂犬病：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">国内最多见，国外约占</span><span style=\"font-size:18px;\">2/3</span><span style=\"font-size:18px;\">。患者逐渐进入高度兴奋状态，其突出表现为极度恐怖，有大难临头的预兆感，并对水声、光、风等刺激非常敏感，引起发作性咽肌痉挛、呼吸困难等。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">临床症状：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">A) </span><span style=\"font-size:18px;\">恐水：是本病的特殊性症状，但不一定每例均有，更不一定在早期出现。典型者饮水、见水、闻流水声，或仅提及饮水时，均可引起严重咽喉肌痉挛。因此，患者渴极而不敢饮，即使饮也无法下咽，满口流涎，沾污床褥或向四周胡乱喷吐。由于声带痉挛，故吐字不清，声音嘶哑，甚至失音。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">B)</span><span style=\"font-size:18px;\">怕风亦是本病特有的症状，微风、吹风、穿堂风等都可导致咽肌痉挛。其他如音响、光亮、触动等，也可引起同样发作。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">C)</span><span style=\"font-size:18px;\">咽肌痉挛发作使患者极度痛苦，不仅无法饮水和进食，而且常伴有辅助呼吸肌痉挛，导致呼吸困难和缺氧，甚或全身进入疼痛性抽搐状态，每次发作后患者仍烦躁不安，并有大量出汗及脱水现象。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">D)</span><span style=\"font-size:18px;\">自主神经功能亢进，患者出现大汗、流涎、体温升高达</span><span style=\"font-size:18px;\">38</span><span style=\"font-size:18px;\">℃以上，心率加快，血压升高，瞳孔扩大。患者表情痛苦、焦急，但神志大多清楚，极少有侵人行为。随着兴奋状态的增长，部分患者可出现精神失常、谵妄、幻视幻听、冲撞嚎叫等症状。病程进展很快，多在发作中死于呼吸衰竭或循环衰竭。本期持续</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3 </span><span style=\"font-size:18px;\">天。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(2)</span><span style=\"font-size:18px;\">麻痹型狂犬病</span><span style=\"font-size:18px;\">(paralytic &nbsp;rabies)</span><span style=\"font-size:18px;\">：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">狂躁型平均</span><span style=\"font-size:18px;\">8</span><span style=\"font-size:18px;\">天。印度及泰国较常见，约占总数</span><span style=\"font-size:18px;\">1/3</span><span style=\"font-size:18px;\">，国内报道不到</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">例。临床上无兴奋期，无恐水症状和吞咽困难，而以高热、头痛、呕吐、咬伤处疼痛开始，继则出现肢体软弱、腹胀、共济失调、部分或全部肌肉瘫痪、尿潴留或大小便失禁等，呈现横断性脊髓炎或上升性脊髓麻痹表现。早期用叩诊锤叩击胸肌，可见被叩击隆起，数秒钟后平复。早期仅在叩诊处出现肌水肿与毛发竖立。病程持续</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">天。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3.</span><span style=\"font-size:18px;\">昏迷期或麻痹期</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">麻痹型一般为</span><span style=\"font-size:18px;\">13</span><span style=\"font-size:18px;\">天左右。两型狂犬病不易区别。痉挛停止，患者暂趋安静，有时尚可勉强饮水吞食，反应减弱或消失，转为弛缓性瘫痪，其中以肢体软瘫最为多见。眼肌、颜面部及咀嚼肌瘫痪，表现为斜视、眼球运动失调、下颌下坠、口不能闭合和面部缺少表情。此外，尚有失音、感觉减退、反射消失、瞳孔散大等。在本期中患者的呼吸逐渐变为微弱或不规则，并可出现潮式呼吸、脉搏细速、血压下降、心音低钝、四肢厥冷，可迅速因呼吸和循环衰竭而死亡。临终前患者多进入昏迷状态。本期持续</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">18h</span><span style=\"font-size:18px;\">。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">二、体征</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 狂犬病病毒进入人体后的路径是：首先侵染肌细胞，在肌细胞中渡过潜伏期，后通过肌细胞和神经细胞之间的乙酰胆碱受体进入神经细胞，其在周围神经组织里的平均移动速率是</span><span style=\"font-size:18px;\">3mm/h</span><span style=\"font-size:18px;\">，上行到中枢神经组织</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">脑</span><span style=\"font-size:18px;\">-</span><span style=\"font-size:18px;\">脊髓</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">后可在一天内繁殖扩散到整个中枢神经组织内。然后沿着相同的通路进入脊髓，进而入脑，并不延血液扩散。病毒在脑内感染海马区、小脑、脑干乃至整个中枢神经系统，并在灰质大量复制，延神经下行到达唾液腺、角膜、鼻粘膜、肺、皮肤等部位。 </span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">狂犬病病毒对宿主主要的损害来自内基小体，即为其废弃的蛋白质外壳在细胞内聚集形成的嗜酸性颗粒，内基小体广泛分布在患者的中枢神经细胞中，也是本疾病实验室诊断的一个指标。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>狂犬病预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 目前的医学手段尚不足以对狂犬病实行有效治疗手段，所以采取有效措施防止动物、人类染病，更加重要。应加强预防措施以控制疾病的蔓延。预防接种对防止发病有肯定价值。还应严格管理饲养动物</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">特变是猫犬等</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">的条例。降低发病率。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(1)</span><span style=\"font-size:18px;\">控制传染源</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">对野犬野猫通过投喂含口服狂犬疫苗的诱饵实现控制。对饲养的猫狗、警犬及实验用猫狗等温血动物，应进行登记，并强制性接种狂犬疫苗。狗</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">猫</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">，只有狂犬病发作才带毒。已经发作狂犬症的温血动物咬人，才有可能通过唾液将狂犬病毒传播到人身上，此时需要尽快打狂犬病预苗预防。发现已染病的温血动物，应立即击毙，以免伤人。对咬人的可疑的狗或猫需要隔离观察</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">天，在此期间如果动物不发病死亡，被咬伤的人可以不要或中止免疫预防。动物一般在出现神经症状前唾液开始排毒，至死亡只有短短数日。自然感染狂犬病的狗、猫在潜伏期内唾液中没有病毒，狗通常在神经症状出现前</span><span style=\"font-size:18px;\">1-5</span><span style=\"font-size:18px;\">天唾液才有病毒</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">出现症状后迅速死亡的狗，它的脑和唾液的病毒浓度，比活得较长一些的狗反而低些。实验感染的猫一般在发病前</span><span style=\"font-size:18px;\">1-2</span><span style=\"font-size:18px;\">天至发病后</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">天唾液排毒。 </span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">仍存活的动物可确定为非患狂犬病者可解除隔离。对死亡动物应取其脑组织进行检查，并将其焚毁或深埋，切不可剥皮或进食。对于器官移植方面，也应确保器官捐献者无染病。</span><span style=\"font-size:18px;\">2004</span><span style=\"font-size:18px;\">年在美国一个未诊断为狂犬病的患者过世之后捐献内脏，获得捐献的三个人因狂犬病身亡。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(2)</span><span style=\"font-size:18px;\">伤口处理</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">早期的伤口处理很重要。人被咬伤后应及时以</span><span style=\"font-size:18px;\">20%</span><span style=\"font-size:18px;\">肥皂水充分地清洗伤口，并不断擦拭。伤口较深者尚需用导管伸入，以肥皂水作持续灌注清洗。如有免疫血清，作皮试阴性后，可注入伤口底部和四周，伤口不宜缝合或包扎。被流浪动物或者是不能辨明其健康与否的动物咬伤或抓伤后，应立即用肥皂水反复冲洗伤口，再用碘伏消毒</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">不是碘酒，碘酒即碘酊，会刺激皮肤黏膜</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">。伤口不要包扎，要保持暴露，因为狂犬病菌属于厌氧菌，包扎导致伤口缺氧反而会加速病菌繁殖</span><span style=\"font-size:18px;\">.</span><span style=\"font-size:18px;\">在被咬</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">抓</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">伤</span><span style=\"font-size:18px;\">24</span><span style=\"font-size:18px;\">小时内到防疫站，疾控中心接种疫苗。</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">医院不提供疫苗接种</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(3)</span><span style=\"font-size:18px;\">预防接种</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">接种对象为：</span><span style=\"font-size:18px;\">①被狼、狐等野兽所咬者</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">②被发病随后死亡</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">包括观察期内</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">或下落不明的犬、猫所咬者</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">③为已被击毙和脑组织已腐败的动物所咬者</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">④皮肤伤口为狂犬唾液沾污者</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">⑤伤口在头、颈处，或伤口较大而深者，如咬人动物</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">指非流行区而言</span><span style=\"font-size:18px;\">)5</span><span style=\"font-size:18px;\">日后仍安危无恙，注射即可中止</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">⑥医务人员的皮肤破损处为狂犬病病人沾污者等。近年来，国内已发现一些被咬伤发生狂犬病而死亡的病列，而犬却安然无恙，经证实该犬的唾液内带毒，故流行区被犬咬伤者均应接种。表</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">示狂犬病预防接种的适应证。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 我国广泛使用田鼠肾细胞疫苗，</span><span style=\"font-size:18px;\">1979</span><span style=\"font-size:18px;\">年起，由武汉生物制品所通过鉴定，投产。轻度咬伤者于第</span><span style=\"font-size:18px;\">0</span><span style=\"font-size:18px;\">、</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">、</span><span style=\"font-size:18px;\">14</span><span style=\"font-size:18px;\">天各肌注</span><span style=\"font-size:18px;\">2ml</span><span style=\"font-size:18px;\">，重度咬伤及头、面、颈部咬伤于第</span><span style=\"font-size:18px;\">0</span><span style=\"font-size:18px;\">、</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">、</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">、</span><span style=\"font-size:18px;\">14</span><span style=\"font-size:18px;\">及</span><span style=\"font-size:18px;\">30</span><span style=\"font-size:18px;\">天各肌注</span><span style=\"font-size:18px;\">2ml</span><span style=\"font-size:18px;\">。该疫苗的效果，各方报告不一致，有的认为其效果仅及人二倍体疫苗的</span><span style=\"font-size:18px;\">1/6</span><span style=\"font-size:18px;\">，因此，需寻找新一代疫苗。免疫血清有抗狂犬病马血清与人体抗狂犬病球蛋白两种。我国目前生产的是前者，每支</span><span style=\"font-size:18px;\">10ml</span><span style=\"font-size:18px;\">，含</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">，</span><span style=\"font-size:18px;\">000IU;</span><span style=\"font-size:18px;\">成人剂量为</span><span style=\"font-size:18px;\">20ml;</span><span style=\"font-size:18px;\">儿童为</span><span style=\"font-size:18px;\">40IU/kg</span><span style=\"font-size:18px;\">。需皮试阴性后方可应用，以一半剂量作局部伤口处注射，另一半剂量肌注。人抗狂犬病球蛋白的一次注射量为</span><span style=\"font-size:18px;\">20IU/kg</span><span style=\"font-size:18px;\">。免疫血清可干扰宿主的主动免疫而影响抗体生成，因此必须在接种结束后</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">、</span><span style=\"font-size:18px;\">20</span><span style=\"font-size:18px;\">和</span><span style=\"font-size:18px;\">90</span><span style=\"font-size:18px;\">日再给予激发量疫苗，以触发回忆反应而产生较大量的相应抗体。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(4)</span><span style=\"font-size:18px;\">其他</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">按需要给予破伤风抗毒素或类毒素，以及适宜的抗菌药物。预防接种后并发神经系统反应者可给予肾上腺皮质激素。干扰素及干扰素诱导剂对动物实验感染有保护作用，用于人的预防是否有效，有待进一步临床实践。</span> \r\n</p>', 'hsg', 'upload/1650938036602.jpg', 2, '', '2022-04-26 09:53:58');
INSERT INTO `xinwentongzhi` VALUES (111, '梅毒的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>梅毒症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">典型症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">会阴溃疡 硬下疳 肉芽肿 斑疹 小阴唇及前庭也有散在的白色圆形及环形丘疹 梅毒性脱发 梅毒性白斑 无痛性硬实结节</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">会阴溃疡</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">硬下疳</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">肉芽肿</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">斑疹</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">小阴唇及前庭也有散在的白色圆形及环形丘疹</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">梅毒性脱发</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">梅毒性白斑</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">无痛性硬实结节</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">梅毒症状诊断</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">一、症状：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">梅毒临床分期：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">潜伏梅毒</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 潜伏梅毒是指已被确诊为梅毒患者，在某一时期，皮肤、粘膜以及任何器官系统和脑脊液检查均无异常发现，物理检查，胸部</span><span style=\"font-size:18px;\">X</span><span style=\"font-size:18px;\">线均缺乏梅毒临床表现，脑脊液检查正常，而仅梅毒血清反应阳性者，或有明确的梅毒感染史，从未发生任何临床表现者。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2. </span><span style=\"font-size:18px;\">一期梅毒</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 潜伏期平均</span><span style=\"font-size:18px;\">3-4</span><span style=\"font-size:18px;\">周，典型损害为硬下疳</span><span style=\"font-size:18px;\">(Hard Chancre</span><span style=\"font-size:18px;\">，</span><span style=\"font-size:18px;\">Ulcus Durum)</span><span style=\"font-size:18px;\">，硬下疳由于性交感染，所以损害多发生在外阴部及性接触部位。硬下疳常合并包皮水肿。有的病人可在阴茎背部出现淋巴管炎，呈较硬的线状损害。女性硬下疳多见于大小阴唇、阴蒂、尿道口、阴阜，尤多见于宫颈，易于漏诊。硬下疳出现一周后，附近淋巴结肿大，其特点为不痛，皮表不红肿，不与周围组织粘连，不破溃，称为无痛性横痃</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">无痛性淋巴结炎</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">。硬下疳如不治疗，经</span><span style=\"font-size:18px;\">3-4</span><span style=\"font-size:18px;\">周可以自愈。经有效治疗后可迅速愈合，遗留浅在性萎缩瘢痕。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 硬下疳发生</span><span style=\"font-size:18px;\">2-3</span><span style=\"font-size:18px;\">周后，梅毒血清反应开始呈阳性。一期梅毒除发生硬下疳外，少数患者尚可在大阴唇、包皮或阴囊等处出现硬韧的水肿。犹如象皮，称为硬性浮肿</span><span style=\"font-size:18px;\">(Edema Induratum)</span><span style=\"font-size:18px;\">。如患者同时感染由杜克雷氏嗜血杆菌引起的软下疳，或由性病淋巴肉芽肿引起的崩蚀性溃疡，则称为混合下疳。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3.</span><span style=\"font-size:18px;\">二期梅毒</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 为梅毒的泛发期。自硬下疳消失至二期梅毒疹出现前的时期，称为第二潜伏期。二期梅毒疹一般发生在硬下疳消退后</span><span style=\"font-size:18px;\">3-4</span><span style=\"font-size:18px;\">周，相当于感染后</span><span style=\"font-size:18px;\">9-12</span><span style=\"font-size:18px;\">周。二期梅毒是梅毒螺旋体经淋巴结进入血行引起全身广泛性损害。丘疹及斑丘疹，临床亦常见，约占二期梅毒</span><span style=\"font-size:18px;\">40%</span><span style=\"font-size:18px;\">左右。发生时间较斑疹稍迟。依其症状及临床经过，可分为大型丘疹及小型丘疹。除引起皮肤损害外，尚可侵犯内脏及神经系统。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 二期梅毒在发疹前可有流感样综合征</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">头痛，低热，四肢酸困</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">，这些前驱症，约持续</span><span style=\"font-size:18px;\">3-5</span><span style=\"font-size:18px;\">日，皮疹出后即消退。本期梅毒血清反应呈强阳性。</span><span style=\"font-size:18px;\">PCR</span><span style=\"font-size:18px;\">检测梅毒螺旋体</span><span style=\"font-size:18px;\">DNA</span><span style=\"font-size:18px;\">呈阳性反应。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 梅毒性脱发：约</span><span style=\"font-size:18px;\">10%</span><span style=\"font-size:18px;\">二期梅毒病人发生。这是毛囊受梅毒性浸润所致，毛发区微细血管阻塞，供血不良引起。表现为梅毒性斑秃或弥漫性脱发，前者为</span><span style=\"font-size:18px;\">0.5cm</span><span style=\"font-size:18px;\">左右的秃发斑，呈虫蛀状。弥漫性脱发，面积较大，稀疏，头发长短不齐。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 梅毒性白斑，多见于妇女患者。一般发于感染后</span><span style=\"font-size:18px;\">4-5</span><span style=\"font-size:18px;\">个月或</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">年，好发于颈项两侧，亦可见于胸、背、乳房、四肢、腋窝、外阴、肛周等部。患部色素完全脱失，周围色素增加，类似白癜风。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">梅毒相关性肾病：主要发生于后天性二期梅毒，其发生率较低。后天性梅毒肾损害的临床表现是多种多样的，如急性肾病综合征、膜性肾小球肾病、急性进行性肾小球肾炎等</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4.</span><span style=\"font-size:18px;\">三期梅毒</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">晚期梅毒</span><span style=\"font-size:18px;\">)</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 发生时间一般在发病后</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">年，但也可更长时间达</span><span style=\"font-size:18px;\">3-5</span><span style=\"font-size:18px;\">年者。好发于</span><span style=\"font-size:18px;\">40-50</span><span style=\"font-size:18px;\">岁之间。主要是由于未经抗梅毒治疗或治疗时间不足，用药量不够。机体内外环境失调亦有一定关系。过度饮酒，吸咽，身体衰弱及患者有结核等慢性病者预后不良。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">三期梅毒的特征如下：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">①发生时间晚</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">感染后</span><span style=\"font-size:18px;\">2-15</span><span style=\"font-size:18px;\">年</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">，病程长，如不治疗，可长达</span><span style=\"font-size:18px;\">10-20-30</span><span style=\"font-size:18px;\">年，甚至终生</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">②症状复杂，可累及任何组织器官，包括皮肤、粘膜、骨、关节以及各内脏，较易侵犯神经系统，易与其它疾病混淆，诊断困难</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">③体内及皮损中梅毒螺旋体少，传染力弱，但破坏组织力强，常造成组织缺损，器官破坏，可致残废，甚至危及生命</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">④抗梅治疗虽有疗效，但对已破坏的组织器官则无法修复。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">⑤梅毒血清反应不稳定，阴性率可达</span><span style=\"font-size:18px;\">30%</span><span style=\"font-size:18px;\">以上，脑脊液常有改变。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1)</span><span style=\"font-size:18px;\">结节性梅毒疹</span><span style=\"font-size:18px;\">(nodular Syphilid)</span><span style=\"font-size:18px;\">：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">多发生于感染后</span><span style=\"font-size:18px;\">3-4</span><span style=\"font-size:18px;\">年内，损害好发于头部、肩部、背部及四肢伸侧。为一群直径约为</span><span style=\"font-size:18px;\">0.3-1.0cm</span><span style=\"font-size:18px;\">大小的浸润性结节，呈铜红色，表面光滑或附有薄鳞屑，质硬，患者无自觉症状，结节的演变可能有两种结局，一是结节变平吸收，留下小的萎缩斑，长期留有深褐色色素沉着。另一结局是中心坏死，形成小脓肿，破溃后形成溃疡，形成结节性溃疡性梅毒疹，愈后留下浅瘢痕。瘢痕周围有色素沉着，萎缩处光滑而薄，在边缘可出现新损害。这是本症的特征。新旧皮疹此起彼伏，新的又发生，可迁延数年。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2)</span><span style=\"font-size:18px;\">树胶肿</span><span style=\"font-size:18px;\">(gumma)</span><span style=\"font-size:18px;\">在三期梅毒中多见，约占三期梅毒</span><span style=\"font-size:18px;\">61%</span><span style=\"font-size:18px;\">。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">为深达皮之下硬结。初发如豌豆大小，渐增大如蚕豆乃李子大或更大，坚硬，触之可活动，数目多少不定。开始颜色为正常皮色，随结节增大，颜色逐渐变为淡红、暗红乃至紫红。结节容易坏死，可逐渐软化，破溃，流出树胶样分泌物，可形成特异的园形、椭园形、马蹄形溃疡，境界清楚，边缘整齐隆起如堤状，周围有褐红或暗红浸润，触之有硬感。常一端愈合，另一端仍蔓延如蛇行状。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3)</span><span style=\"font-size:18px;\">梅毒性心血管病</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">是梅毒螺旋体侵入人体后于晚期</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">第三期</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">累及心血管系统引起的心血管病变，包括梅毒性主动脉炎、梅毒性主动脉瓣关闭不全、梅毒性主动脉瘤、冠状动脉口狭窄和心肌树胶样肿。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">妊娠相关梅毒感染：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1)</span><span style=\"font-size:18px;\">妊娠合并梅毒：梅毒与妊娠可相互影响。妊娠梅毒可通过胎盘传染胎儿，由于妊娠梅毒的胎盘血管梗阻，影响胎儿营养，易发生流产，早产或死产，虽可足月分娩，但约有</span><span style=\"font-size:18px;\">64.5%</span><span style=\"font-size:18px;\">胎儿已感染梅毒，发生先天梅毒，其中有</span><span style=\"font-size:18px;\">15%~20%</span><span style=\"font-size:18px;\">为早发性先天梅毒。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2)</span><span style=\"font-size:18px;\">先天性梅毒：先天梅毒在胎期由梅毒孕妇借血行通过胎盘传染于胎儿，故亦称胎传梅毒。通常约在怀孕四个月经胎盘传染，胎儿可死亡或流产。如孕妇感染梅毒五年以上，胎儿在子宫内传染就不大可能。</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">岁以内为早期先天梅毒，超过</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">岁为晚期先天梅毒，特点是不发生硬下疳，早期病变较后天梅毒为重，晚期较轻，心血管受累少，骨骼，感管系统如眼、鼻受累多见。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>梅毒预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">同预防其他传染病一样，首先应加强卫生宣传教育，反对不正当的性行为，其次应采取以下预防措施：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(1)</span><span style=\"font-size:18px;\">对可疑病人均应进行预防检查，做梅毒血清试验，以便早期发现新病人并及时治疗</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(2)</span><span style=\"font-size:18px;\">发现梅毒病人必须强迫进行隔离治疗，病人的衣物及用品，如：毛巾、衣服、剃刀、餐具、被褥等，要在医务人员指导下进行严格消毒，以杜绝传染源</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(3)</span><span style=\"font-size:18px;\">追踪病人的性伴侣，包括病人自报及医务人员访问的，查找病人所有性接触者，进行预防检查，追踪观察并进行必要的治疗，未治愈前配偶绝对禁止有性生活</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(4)</span><span style=\"font-size:18px;\">对可疑患梅毒的孕妇，应及时给予预防性治疗，以防止将梅毒感染给胎儿</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">未婚男女病人，未经治愈不能结婚。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">【预防方法】</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 跟其它性病不同，只用眼睛侦察很难被发现，但最低限度在梅毒蔓延时，会让我们察觉它存在。如你察觉身体有损伤，请暂时停止性行为。随着</span><span style=\"font-size:18px;\">50-75% </span><span style=\"font-size:18px;\">的男性与女性毫无症状出现，且一些损伤无法被察觉又怎样？使用避孕套是最佳的保护方法。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">【预防常识】</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 梅毒是一种十分严重的性病，可造成传染和流行，贻害家庭和后代及社会。治疗青酶素有特效，应到正规医院性病专科诊治，梅毒的治愈不是轻而易举的，有的经治疗后复发，或潜伏，或转入晚期梅毒。此外，国外有用非青酶素治疗早期梅毒后，发生晚期梅毒的报告，因此要在治疗完成后认真、严格随访</span><span style=\"font-size:18px;\">1-3</span><span style=\"font-size:18px;\">年，并在医生指导下复查，何时终止随访由医师决定，有复发者应即复治，以免转入晚期梅毒，造成心血管神经系统损害。</span> \r\n</p>', 'hsg', 'upload/1650938020204.jpg', 1, '', '2022-04-26 09:53:42');
INSERT INTO `xinwentongzhi` VALUES (112, '麻疹的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>麻疹症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">典型症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">有发热、咳嗽、流鼻涕、流眼泪、畏光等，伴有不同程度的全身不适。有时在腭垂</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">又称悬雍垂</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">、扁桃体、咽后壁、软腭处可发现棕红色斑点，</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">发烧</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">上呼吸道卡他症状</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">红斑样皮疹</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">斑丘疹</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">麻疹面容</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">脾肿大</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">一、症状：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">典型麻疹</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 潜伏期：一般为</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">天±</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">天</span><span style=\"font-size:18px;\">(6</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">21</span><span style=\"font-size:18px;\">天</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">，感染严重或经输血获得感染者潜伏期可短至</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">天，接受过免疫制剂</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">全血、血清、免疫球蛋白等</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">或曾接种过麻疹疫苗而发病时，则潜伏期可延长至</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">周。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 此期临床上主要表现：在潜伏期末</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">天已可从上呼吸道分泌物中排出麻疹病毒。有些患者于接触麻疹患者数小时后，可出现暂时性轻度上呼吸道症状及低热，甚至有一过性皮疹，但甚罕见。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">麻疹典型病程可划分为三个阶段：前驱期、出疹期和恢复期。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1)</span><span style=\"font-size:18px;\">前驱期：一般持续</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">天，体弱及重症可延长至</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">8</span><span style=\"font-size:18px;\">天，而曾接种过麻疹疫苗或有被动免疫力者则可短至</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">天。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 此期临床上主要表现：体格检查可见口腔及咽部黏膜充血明显，上呼吸道</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">包括眼结合膜</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">炎症的卡他症状，常伴胃纳减退，甚至呕吐、腹泻等胃肠道症状。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 有发热、咳嗽、流鼻涕、流眼泪、畏光等，伴有不同程度的全身不适。发热常日低夜高，逐日升高，可达</span><span style=\"font-size:18px;\">39</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">40</span><span style=\"font-size:18px;\">℃，婴幼儿可发生高热惊厥，年长儿或成人常诉头痛、头昏、乏力、嗜睡。咳嗽渐加重，多半为干咳，因上呼吸道黏膜炎症常下延至喉部、气管、支气管，咳嗽往往带嘶哑声，年幼儿甚至出现呼吸急促和困难。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 发病后</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">天可在第一磨牙对面的颊黏膜上出现科氏斑，为麻疹前驱期的特征性体征，有麻疹早期诊断价值。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 此种细小口腔内疹，呈白色，为</span><span style=\"font-size:18px;\">0.5</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">1mm</span><span style=\"font-size:18px;\">针尖大小，散在于鲜红湿润的颊黏膜上。初起时仅几个，很快增多，且可融合，扩散至整个颊黏膜，以及口唇内侧、牙龈等处，也偶见于眼睑结合膜上，极少发生于硬、软腭。斑点数目少时易在日光下见到细小白点，周围红晕，数目众多时可融合成片，仅见充血的颊黏膜上有细盐样突起颗粒。科氏斑一般维持</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">天，迅速消失，有时在出疹后</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">天还可见到。个别患者在前驱期开始时见到颈、胸、腹部出现风疹样或猩红热样或荨麻疹样皮疹，数小时内就消退，称为前驱疹。有时在腭垂</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">又称悬雍垂</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">、扁桃体、咽后壁、软腭处可发现棕红色斑点，出疹期初迅速隐去。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2) </span><span style=\"font-size:18px;\">出疹期：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 皮疹：起病后</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">天当呼吸道卡他症状及发热达高峰时开始出现皮疹，常在见到科氏斑后</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">天。首先从耳后发际出现淡红色斑丘疹，渐及头部前额、脸面、颈部，自上而下扩展至胸、腹、背，最后达四肢，直至手心脚底，</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">天就波及全身。皮疹以斑丘疹为主，开始时颜色鲜红，压之退色，大小不等，平均直径</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">5mm</span><span style=\"font-size:18px;\">，分布稀疏分明，至出疹高峰时皮疹数目增多，聚集融合成片，色泽也渐转暗，但疹间皮肤仍属正常，偶见小疱疹或细小出血性皮疹。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 其他器官病变症状：病情严重时，尤其伴有心肺衰竭时，皮疹颜色可突然转暗，并快速隐退。随出疹达到高峰全身中毒症状加重，体温进一步升高，可达</span><span style=\"font-size:18px;\">40</span><span style=\"font-size:18px;\">℃以上，精神委靡、嗜睡倦怠，或终日烦躁不安，咳嗽加重有痰、唇舌干燥、咽极度充血、眼睑浮肿，分泌物多。颈部淋巴结及肝脾肿大，肺部常闻干、湿啰音。胸部</span><span style=\"font-size:18px;\">X</span><span style=\"font-size:18px;\">线检查，可见纵隔淋巴结增大，肺纹理增粗。普种疫苗前年代成人较少患野毒株麻疹病毒引起的自然感染，出疹期中毒症状成人常比小儿为重，皮疹也都密集，但并发细菌感染似较婴幼儿为少。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3)</span><span style=\"font-size:18px;\">恢复期：若无并发症，单纯麻疹自起病至疹退一般病程为</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">14</span><span style=\"font-size:18px;\">天。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 此期临床上主要表现：在单纯麻疹患者，当皮疹和中毒症状发展到高峰后，体温常于</span><span style=\"font-size:18px;\">12</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">24h</span><span style=\"font-size:18px;\">内较快下降，随之患者精神好转，呼吸道症状减轻，但咳嗽常可延续较久，食欲大大好转。一般体温下降后</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">天皮疹按出疹顺序依次消退，留下浅棕色色素沉着斑，伴糠麸样细小脱屑，以躯干为多，</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">周内退尽。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">非典型麻疹 根据麻疹病毒基因差异、毒力强弱，进入人体数量多少，以及患者年龄大小、健康状况、营养优劣、免疫力的高低等，麻疹的临床发展过程除大多为典型麻疹外，在部分病例尚可呈现以下非典型表现。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1)</span><span style=\"font-size:18px;\">轻型麻疹：大多因体内对麻疹病毒有一定的免疫力所致，如</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">个月前婴儿尚留有来自母体的被动免疫抗体，或近期注射被动免疫制剂，或以往曾接种过麻疹疫苗，以及第二次感染发病者，都可表现为轻症。轻型麻疹潜伏期可延长至</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">周。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">此型临床上主要表现：发病轻，前驱期短而不明显，呼吸道卡他症状较轻，科氏斑不典型或不出现，全身症状轻微，不发热或仅有低中度热。皮疹稀疏色淡，病程较短，很少并发症，但病后所获免疫力，特异抗体上升滴度与患典型麻疹者基本相同。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">现已证实麻疹也有不少隐性感染或无皮疹型麻疹，只能依据病后血清特异抗体增加来证实。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2)</span><span style=\"font-size:18px;\">重型麻疹：大多由于患者体质弱，有其他疾病，营养不良、免疫力低下或伴有继发性细菌感染等，使麻疹病情加重，如中毒性麻疹，因麻疹病毒感染严重。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 此型临床上主要表现：起病不久即出现高热</span><span style=\"font-size:18px;\">40</span><span style=\"font-size:18px;\">℃以上，伴有严重中毒症状，往往神志不清，呼吸急促，唇指发绀，反复惊厥，脉搏细速，呈暗红色，皮疹密集，融合成片。皮疹可呈出血性，形成紫斑，甚至伴发内脏出血、呕血、咯血、便血等</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">出血性麻疹</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">，有时皮疹呈疱疹样可融合成大疱</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">疱疹样麻疹</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">。有些年幼体弱小儿麻疹皮疹疏淡，未能出透，未及手足心，或皮疹突然隐没，体温下降低于常温，面色苍白或青灰色</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">中医称白面痧</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">，四肢厥冷，大多因心功能不全或循环衰竭引起</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">休克性麻疹</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">，心率快速、脉搏细弱，呼吸不规则或困难。并发重症细菌性</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">金黄色葡萄球菌</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">肺炎或其他病毒性肺炎</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">腺病毒性肺炎</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">等也常属重症，常发生心力衰竭，病情重危，病死率高。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3)</span><span style=\"font-size:18px;\">异型麻疹：主要发生在以往接种过麻疹灭活疫苗者，当接种</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">年后再接触麻疹急性期患者，就可引起异型麻疹。潜伏期为</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">14</span><span style=\"font-size:18px;\">天。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 此型临床上主要表现：多数患者无典型科氏斑。前驱期可突发高热，达</span><span style=\"font-size:18px;\">39</span><span style=\"font-size:18px;\">℃以上，伴头痛、肌痛、腹痛、乏力等，而上呼吸道卡他症状不明显，可有干咳，多半无流鼻涕、眼泪、眼结合膜炎等。起病后</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">天出现皮疹，从四肢远端腕部、踝部开始，向心性扩散到达四肢近端及躯干，以下身为多，很少扩散到乳头线以上部位，偶见于头面部。皮疹一般呈黄红色斑丘疹，有时呈</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3mm</span><span style=\"font-size:18px;\">大小的小疱疹，有痒感，消退时不结痂，皮疹偶呈瘀点、瘀斑或荨麻疹样，常伴四肢水肿。呼吸道症状虽不严重，但肺部有时可闻啰音。</span><span style=\"font-size:18px;\">X</span><span style=\"font-size:18px;\">线检查可见肺门淋巴结肿大及肺部片状阴影。此种肺炎可反复延续</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">年。有些患者可表现为肝、脾肿大、肢体麻木、无力和瘫痪，也可临床上不出现明显皮疹，而有其他脏器病变症状。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4)</span><span style=\"font-size:18px;\">免疫低下者患麻疹：无论患先天性免疫缺陷或继发性免疫低下</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">如肿瘤患者、肾上腺皮质激素治疗者、营养不良免疫力衰弱等</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">，若发生麻疹常患重症，病死率也较高，有报告肿瘤患者的麻疹常不出现皮疹，而一半以上可发生麻疹巨细胞肺炎，并容易并发脑炎。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">5)</span><span style=\"font-size:18px;\">孕妇和新生儿麻疹：易感孕妇患麻疹病情相对较重，有报告</span><span style=\"font-size:18px;\">54%</span><span style=\"font-size:18px;\">因原发麻疹肺炎及其他呼吸道并发症住院。孕妇患麻疹虽不像患风疹易使胎儿发生畸变，但常在妊娠早期引起死胎、稍晚可引起自然流产或死产和早产。患麻疹的孕妇分娩前可经胎盘将病毒传给胎儿，使刚出生的新生儿也可发生麻疹，病情轻重不等，但往往无明显前驱症状而发疹较多。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>麻疹预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">【预防】</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">一</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">隔离传染源：可嘱病人到传染病医院进行住院治疗，在家隔离治疗时应尽量减少与他人接触。病人隔离至出疹后</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">天，伴有呼吸道并发症者隔离期延长至出疹后</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">天。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">二</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">切断传播途径：对室内环境进行消毒，室内湿式扫除，开窗通风使病毒迅速排出室外。麻疹潜伏期为</span><span style=\"font-size:18px;\">7-21</span><span style=\"font-size:18px;\">天</span><span style=\"font-size:18px;\">(10-11</span><span style=\"font-size:18px;\">天为常见</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">，集体单位应在最后一例病人发病后</span><span style=\"font-size:18px;\">21</span><span style=\"font-size:18px;\">天内观察有无新的可疑病例，一旦出现，立即与当地卫生防疫部门联系，同时医学观察期从新病例发生时间顺延</span><span style=\"font-size:18px;\">21</span><span style=\"font-size:18px;\">天。集体单位发生病例后医学观察期内停止集体活动，减少病毒的传播范围。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">三</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">保护易感人群：接种疫苗是预防麻疹最有效的措施，因此对</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">年内未接种过麻疹疫苗者，</span><span style=\"font-size:18px;\">45</span><span style=\"font-size:18px;\">岁以下无麻疹患病史者，均应尽快应急接种。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">提高人群免疫力是预防麻疹的关键，故对易感人群实施计划免疫十分重要。如发现麻疹病人，则应采取综合措施防止传播和流行。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(1)</span><span style=\"font-size:18px;\">自动免疫　易感者都应接种麻疹减毒活疫苗。初种年龄不宜小于</span><span style=\"font-size:18px;\">8</span><span style=\"font-size:18px;\">个月，因恐来自母体的抗体中和疫苗病毒，使之失效。我国目前定于</span><span style=\"font-size:18px;\">8</span><span style=\"font-size:18px;\">个月时初种，</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">岁时加强一次。国外主张在</span><span style=\"font-size:18px;\">15</span><span style=\"font-size:18px;\">个月时初次接种更为保险，并认为在</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">岁以内接种者，应于</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">年后加强一次。疫苗应保存在</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">℃暗处，每次皮下注射</span><span style=\"font-size:18px;\">0.2ml</span><span style=\"font-size:18px;\">，</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">次即可。各年龄剂量相同，于麻疹流行季节前一个月接种最好。易感者在接触麻疹病人后</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">日内，若应急接种麻疹疫苗，仍可防止发病或减轻病情。流行时易感者</span><span style=\"font-size:18px;\">80%</span><span style=\"font-size:18px;\">接种了疫苗，可于</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">周内控制流行。接种疫苗后反应轻微，</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">14</span><span style=\"font-size:18px;\">日后可有低热数日，偶见稀疏淡红皮疹。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 注意事项：有发热和急、慢性疾病者，应暂缓自动免疫</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">有过敏体质、活动性结核、恶性肿瘤、白血病及应用免疫抑制剂或放射治疗和先天性免疫缺陷者，不应接种麻疹减毒活疫苗</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">凡</span><span style=\"font-size:18px;\">8</span><span style=\"font-size:18px;\">周内接受过输血或血制品及被动免疫制剂，以及</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">周内接受其他病毒减毒活疫苗者均应推迟接种，以免影响效果。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 接种麻疹减毒活疫苗后血清抗体都有上升，阳性率可达</span><span style=\"font-size:18px;\">95%</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">98%</span><span style=\"font-size:18px;\">，最早于</span><span style=\"font-size:18px;\">12</span><span style=\"font-size:18px;\">天血液中即可出现血凝抑制等抗体，</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">个月时达高峰，抗体效价在</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">∶</span><span style=\"font-size:18px;\">16</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">∶</span><span style=\"font-size:18px;\">128</span><span style=\"font-size:18px;\">。</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">个月后渐降，一般仍维持一定水平。部分接种者</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">年后可全部消失，故复种年龄可在</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">岁时。婴儿主动免疫覆盖率达到</span><span style=\"font-size:18px;\">90%</span><span style=\"font-size:18px;\">以上时，可形成无病区。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">有些国家将麻疹疫苗与风疹疫苗，流行性腮腺炎疫苗同时接种，不影响其免疫效果。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(2)</span><span style=\"font-size:18px;\">被动免疫　年幼体弱及患病者如接触麻疹病人，</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">天内进行被动免疫可免于发病，</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">9</span><span style=\"font-size:18px;\">天内进行则仅能减轻病情。可肌注丙种球蛋白</span><span style=\"font-size:18px;\">(10%)0.2ml/kg</span><span style=\"font-size:18px;\">，或胎盘球蛋白</span><span style=\"font-size:18px;\">0.5</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">1.0ml/kg</span><span style=\"font-size:18px;\">，或成人血浆</span><span style=\"font-size:18px;\">20</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">30ml</span><span style=\"font-size:18px;\">。被动免疫力仅能维持</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">周，</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">周后又接触麻疹患者需再注射。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">疫苗知识：何时接种麻疹疫苗最好</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 根据我国儿童免疫程序，麻疹疫苗初种时间为生后</span><span style=\"font-size:18px;\">8</span><span style=\"font-size:18px;\">个月最佳，复种为</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">岁左右最佳。这是我国医务工作者分别对不同接种时间所产生的免疫抗体进行调查后确定的。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 调查中发现：</span><span style=\"font-size:18px;\">4-5</span><span style=\"font-size:18px;\">个月婴儿接种后抗体的阳性率为</span><span style=\"font-size:18px;\">43.7%</span><span style=\"font-size:18px;\">，</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">个月</span><span style=\"font-size:18px;\">87.5%</span><span style=\"font-size:18px;\">，</span><span style=\"font-size:18px;\">8</span><span style=\"font-size:18px;\">个月为</span><span style=\"font-size:18px;\">85%</span><span style=\"font-size:18px;\">，</span><span style=\"font-size:18px;\">12</span><span style=\"font-size:18px;\">个月为</span><span style=\"font-size:18px;\">98%</span><span style=\"font-size:18px;\">，说明</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">个月以前婴儿注射麻疹疫苗后抗体的阳性率并不高。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 其主要原因是胎儿从母体中已获得了对抗麻疹的抗体，生后尚未消失，干扰了接种麻疹疫苗后抗体的产生。在</span><span style=\"font-size:18px;\">60</span><span style=\"font-size:18px;\">年代，由于当时小儿麻疹的发病率很高，所以初种年龄定为</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">个月。以后由于麻疹的发病率逐年降低，从</span><span style=\"font-size:18px;\">1977</span><span style=\"font-size:18px;\">年起，改为生后</span><span style=\"font-size:18px;\">8</span><span style=\"font-size:18px;\">个月初种，但比起美国的初种年龄</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">生后</span><span style=\"font-size:18px;\">15</span><span style=\"font-size:18px;\">个月</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">还算是较早的。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 随着时间的推移，接种过麻疹疫苗的小儿体内产生的抗体会逐年降低。初种后</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">年，抗体阴转率为</span><span style=\"font-size:18px;\">17%</span><span style=\"font-size:18px;\">，初种后</span><span style=\"font-size:18px;\">12</span><span style=\"font-size:18px;\">年达</span><span style=\"font-size:18px;\">43.6%</span><span style=\"font-size:18px;\">，所以</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">岁时应再复种一次最佳。复种后，</span><span style=\"font-size:18px;\">95%</span><span style=\"font-size:18px;\">以上小儿抗体转为阳性，又产生了较强的抗病能力。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">【预后】</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">麻疹的预后与患者免疫力强弱关系甚为密切，年幼体弱，患营养不良，佝偻病或其他疾病者，特别是细胞免疫功能低下者病情较重，常迁延不愈，易有并发症。护理不当、治疗不及时也常加重病情，而早期诊断，及早采用自动免疫或被动免疫，有助于减轻病情。</span> \r\n</p>', 'hsg', 'upload/1650937991507.jpg', 3, '', '2022-05-14 22:00:58');
INSERT INTO `xinwentongzhi` VALUES (113, '登革热的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>登革热症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">早期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">有畏寒或寒战症状，高热，</span><span style=\"font-size:18px;\">24</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">36h</span><span style=\"font-size:18px;\">内体温升高达</span><span style=\"font-size:18px;\">39</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">40</span><span style=\"font-size:18px;\">℃，多数患者表现为稽留热或弛张热</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">晚期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">为脑膜脑炎，出现剧烈头痛、呕吐、谵妄、狂躁、昏迷、抽搐、大量出汗、血压骤降、颈强直、瞳孔缩小等。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">发烧</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">出血倾向</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">红斑样皮疹</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">头痛</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">关节疼痛</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">疲乏</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">肌痛</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">淋巴结肿大</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">高热</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">【临床表现】</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 登革热临床表现主要以出血倾向和皮疹为其特点，除此之外，还有全身疼痛、发热、乏力症状。根据临床症状体征的严重程度不同，一般分为登革热、登革出血热和登革休克综合征三型。临床上将登革热分为典型、轻型与重型。登革出血热分为无休克的登革出血热</span><span style=\"font-size:18px;\">(dengue hemorrhagic fever</span><span style=\"font-size:18px;\">，</span><span style=\"font-size:18px;\">DHF)</span><span style=\"font-size:18px;\">和登革休克综合征</span><span style=\"font-size:18px;\">(dengue shock syndrome</span><span style=\"font-size:18px;\">，</span><span style=\"font-size:18px;\">DSS)</span><span style=\"font-size:18px;\">。潜伏期通常为</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">8</span><span style=\"font-size:18px;\">天。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">典型</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">普通型</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">登革热</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(1)</span><span style=\"font-size:18px;\">发热：通常起病迅速，患者有畏寒或寒战症状，高热，</span><span style=\"font-size:18px;\">24</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">36h</span><span style=\"font-size:18px;\">内体温升高达</span><span style=\"font-size:18px;\">39</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">40</span><span style=\"font-size:18px;\">℃，多数患者表现为稽留热或弛张热。大部分患者经治疗</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">天后，体温逐渐恢复至正常水平。少数患者于发热</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">天后体温降至正常，</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">天后再度上升，称为双峰热或马鞍热</span><span style=\"font-size:18px;\">(saddle fever)</span><span style=\"font-size:18px;\">。发热时常伴较剧烈眼眶痛，肌肉、头痛、骨及关节疼痛，极度乏力，可有恶心、呕吐、腹痛、腹泻或便秘等胃肠道症状。患者于发热期的呼吸、脉搏加快。早期体征有颜面、颈、胸皮肤潮红，眼结合膜充血及浅表淋巴结肿大。恢复期常因显著衰弱而需数周后才能完全恢复正常。儿童病例起病可较缓慢，毒血症状较轻，恢复亦常较快。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(2)</span><span style=\"font-size:18px;\">出血：出血多发生于病程的第</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">8</span><span style=\"font-size:18px;\">天。</span><span style=\"font-size:18px;\">25%</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">50%</span><span style=\"font-size:18px;\">的患者有不同程度的出血现象，如牙龈出血、皮下出血、鼻出血等。皮肤、黏膜下出血等，出血范围的大小与疾病的严重程度成正相关关系。皮肤或黏膜下出血范围的直径不超过</span><span style=\"font-size:18px;\">2mm</span><span style=\"font-size:18px;\">者称为出血点，直径为</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">5mm</span><span style=\"font-size:18px;\">者称为紫癜，直径为</span><span style=\"font-size:18px;\">5mm</span><span style=\"font-size:18px;\">以上者称为瘀斑。当出血灶的皮肤明显隆起时称为血肿，可见于严重出血的病例。束臂试验亦称毛细血管脆性试验可用于疑似本病病例检查。检查方法是在前臂屈侧面肘弯下</span><span style=\"font-size:18px;\">4cm</span><span style=\"font-size:18px;\">处画一直径为</span><span style=\"font-size:18px;\">5cm</span><span style=\"font-size:18px;\">的圆圈，仔细观察圆圈皮肤有无出血点，如果发现出血点则用墨水笔标出。然后用血压计的袖带束于该侧上臂，先测定血压，再使其保持于收缩压与舒张压之间维持</span><span style=\"font-size:18px;\">8min</span><span style=\"font-size:18px;\">后解除压力。待皮肤颜色恢复正常</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">约</span><span style=\"font-size:18px;\">2min)</span><span style=\"font-size:18px;\">后，计算圆圈内皮肤出血点的数目，减去原有出血点的数目。若两者之差大于</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">则为阳性。登革热患者的束臂试验常呈阳性。由于束臂试验有可能使存在严重出血倾向患者的试验前臂出现瘀斑的可能性。因此，本试验不宜过多施行。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(3)</span><span style=\"font-size:18px;\">皮疹：常于病程的第</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">天出现，多为斑丘疹，可呈麻疹样皮疹，也有猩红热样皮疹、红斑疹及出血性皮疹</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">瘀点</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">等。在同一患者身上可同时出现两种或多种皮疹。皮疹多先见于躯干，然后逐渐向四肢、头面部蔓延，最后分布于全身皮肤。皮疹多有痒感，大部分不脱屑，持续</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">天后逐渐消退。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(4)</span><span style=\"font-size:18px;\">其他：约</span><span style=\"font-size:18px;\">1/4</span><span style=\"font-size:18px;\">病例有轻度肝大，个别病例可有黄疸，但脾大少见。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">轻型登革热 症状和体征较典型登革热轻，表现为发热较低，全身疼痛较轻，皮疹稀少或不出疹，无出血倾向，但浅表淋巴结亦常肿大，病程常短于</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">天。流行期间轻型病例较多，由于其临床表现类似流行性感冒与急性上呼吸道炎，症状较轻，故较易被忽视而漏诊。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3.</span><span style=\"font-size:18px;\">重型登革热 早期临床表现类似典型登革热，但发热</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">天后病情突然加重。表现为脑膜脑炎，出现剧烈头痛、呕吐、谵妄、狂躁、昏迷、抽搐、大量出汗、血压骤降、颈强直、瞳孔缩小等。此型病情凶险，进展迅速，可于</span><span style=\"font-size:18px;\">24h</span><span style=\"font-size:18px;\">内死于中枢性呼吸衰竭。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4.</span><span style=\"font-size:18px;\">登革出血热</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 分为两型即较轻的登革出血热和较重的登革休克综合征</span> <span style=\"font-size:18px;\">。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1)</span><span style=\"font-size:18px;\">登革出血热</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 开始表现为典型登革热。但其肌痛、腰痛、发热、但骨、关节痛不明显，而鼻衄、呕血、咯血、尿血、便血等症状严重。常有两个以上器官大量出血，出血量大于</span><span style=\"font-size:18px;\">100ml</span><span style=\"font-size:18px;\">、血浓缩，红细胞压积增加</span><span style=\"font-size:18px;\">20%</span><span style=\"font-size:18px;\">以上，血小板计数</span><span style=\"font-size:18px;\">&lt;100</span><span style=\"font-size:18px;\">×</span><span style=\"font-size:18px;\">109/L</span><span style=\"font-size:18px;\">。有的病例出血量虽小，但出血部位位于脑、心脏、肾上腺等重要脏器而危及生命。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2)</span><span style=\"font-size:18px;\">登革休克综合征</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 具有典型登革热症状</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">在病程中或退热后，病情会突然加重，有明显出血倾向伴周围循环衰竭。表现皮肤湿冷，脉快而弱，脉压差进行性缩小，血压下降甚至测不到，烦燥、昏睡、昏迷等。病情凶险，如不及时抢险，可于</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">小时内死亡。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>登革热预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 传染源主要是病人，以发病前</span><span style=\"font-size:18px;\">1d</span><span style=\"font-size:18px;\">至发病后</span><span style=\"font-size:18px;\">5d</span><span style=\"font-size:18px;\">传染性最强，轻型和隐性感染者可能是重要的传染源。主要传播媒介为埃及伊蚊，其次为白纹伊蚊。蚊虫吸血受染后</span><span style=\"font-size:18px;\">8-14d</span><span style=\"font-size:18px;\">才有传染性，再次叮人即可传播疾病。伊蚊受染后终身具传染性，登革热病毒在白纹伊蚊的唾液腺及神经细胞中可大量复制。病毒在蚊体内可经卵传代，故伊蚊又是本病毒的储存宿主。感染后对同型病毒有巩固的免疫力，并可维持多年，对异型病毒也有</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">年以上的免疫力。流行季节与各地气候、蚊虫繁殖情况有关，广东省为</span><span style=\"font-size:18px;\">5-10</span><span style=\"font-size:18px;\">月，海南省为</span><span style=\"font-size:18px;\">3-10</span><span style=\"font-size:18px;\">月。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">预防：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">一、预防登革热要处理孳生地、消灭蚊蚴：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">、疏通沟渠、下水道，防止积水，填平洼地、翻盆倒罐清除积水。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">、尽量避免用清水养植植物，对于花瓶等容器，每星期至少清洗、换水一次，勿让花盆底盘留有积水。把所有用过的罐子及瓶子放进有盖的垃圾桶内，摧毁白纹伊蚊孳生地。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">二、预防登革热要杀灭成蚊：在住宅、办公室、医院等及其他公共场所可采用药物杀灭成蚊。目前可采用下列几种：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">、敌敌畏：可采用药物稀释喷洒及烟薰，特点是速效，迅速杀死成蚊。对人畜有毒性。使用时须小心，注意安全。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">、三氯杀虫酯</span><span style=\"font-size:18px;\">(7504)</span><span style=\"font-size:18px;\">：对人畜毒性低，可采用药物稀释喷洒及烟薰，特点是见效慢，但保持时间长，</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">天仍有效。可与敌敌畏混合使用，比例为</span><span style=\"font-size:18px;\">4:1</span><span style=\"font-size:18px;\">或</span><span style=\"font-size:18px;\">3:1,</span><span style=\"font-size:18px;\">其优点是速效且持续时间长。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">、人工合成除虫菊酯类：其制剂有二氯苯醚菊酯，胺菊酯等，可采用药物稀释喷洒，特点是杀虫效果强，对人畜毒性低。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">、溴氰菊酯：属于触杀药</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">即接触中毒</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">可采用药物稀释喷洒，杀虫作用强，对人畜毒性低。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">三、预防登革热要认真做好个人防护</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">、养成睡觉时放蚊帐的习惯。亦可用防蚊油涂搽暴露皮肤，室内可点燃蚊香。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">、到登革热流行区旅游或生活，应穿着长袖衣服及长裤，并在外露的皮肤及衣服上涂蚊虫驱避药物</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">、避免在“花斑蚊”出没频繁时段在树荫、草丛、凉亭等户外阴暗处逗留</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 四、预防登革热要隔断人为传染：一旦有病人发生，要早就诊，早报告，早诊断，早隔离，早治疗。避免向周围健康人群传播，病人在起病日算起应隔离防蚊叮咬</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">天。</span> \r\n</p>', 'hsg', 'upload/1650937971394.jpg', 2, '', '2022-04-26 09:52:53');
INSERT INTO `xinwentongzhi` VALUES (115, '病毒性肝炎的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>病毒性肝炎症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">典型症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">肝被膜紧张 ，大块肝细胞坏死， 蛋白尿， 大三阳， 恶心。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">肝被膜紧张</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">大块肝细胞坏死</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">蛋白尿</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">大三阳</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">恶心</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">症状</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">病毒性肝炎按病程和病情演变情况可分为：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(1)</span><span style=\"font-size:18px;\">急性肝炎</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">①急性黄疽性肝炎：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">病程可分为</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">个阶段：黄疸前期、 黄疸期 、恢复期。黄疸前期起病较急，有畏寒、发热、乏力、厌食、厌油、恶心、呕吐等症状，黄疸期就是约</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">周后尿色深黄，继而巩膜及皮肤出现黄疽，肝脾均可肿大，肝区触叩痈明显，恢复期是约经</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">—</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">周后黄疽逐渐消退，精神、食欲好转，肝功能恢复正常，病程约</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">—</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">个月。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">②急性无黄疽性肝炎：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">起病销缓，一般症状较轻，大多不发热，整个病程中始终无黄疽出现，其他症状和体征与急性黄疽性肝炎相似，但发病率高，约占急性肝炎总人数的</span><span style=\"font-size:18px;\">70%</span><span style=\"font-size:18px;\">——</span><span style=\"font-size:18px;\">90%</span><span style=\"font-size:18px;\">。肝功能改变主要是</span><span style=\"font-size:18px;\">ALT</span><span style=\"font-size:18px;\">升高。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(2)</span><span style=\"font-size:18px;\">慢性肝炎</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">①慢性迁延性肝炎：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">由急性肝炎迁延而至，病程达半年以上而病情未明显好转，仍有食欲减退、胁痛、乏力、肝肿大、肝区痛等。肝功检查主要是</span><span style=\"font-size:18px;\">ALT</span><span style=\"font-size:18px;\">单项增高。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">②慢性活动性肝炎：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">病程超过</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">年，症状和体征及肝功能检查均有明显异常，主要症状为乏力、纳差、腹胀、肝区痛等，且有肝病面容、肝掌、蜘蛛痣、黄疽、肝质较硬、脾肿大等体征。部分病例出现肝外器官损害，如慢性多发性关节炎，慢性肾小球炎慢性溃疡性结肠炎，结节性多动脉炎，桥本氏甲状腺炎等。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(3)</span><span style=\"font-size:18px;\">重症肝炎</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">凡急性、慢性肝炎或肝硬变患者出现高热、极度乏力、严重的消化道症状，黄疸进行加深，出血倾向、神经精神症状，肝脏进行性缩小，肝细胞明显损害，凝血酶原时间明显延长者，均应考虑为重型肝炎。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">①急性重症：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">骤起高热，来势凶险，黄疽出现后迅速加深，肝脏缩小，伴有明显肝臭，肝功能显著减退。常有出血或出血倾向、腹水、下肢浮肿、蛋白尿、管型尿等，并可出现烦躁不安、谵妄、狂躁等精神症状，随后进入肝昏迷状态，抢救不及时可导致死亡。尿常规可查见蛋白及管型，尿胆红素强阳性。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">②亚急性重症：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">发病初期类似肝炎，经</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">—</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">周后病情不见减轻，反而逐渐加重，常有乏力、厌食、严重的腹胀、尿少、重度黄疽、明显的出血倾向和腹水，血清胆红素升达</span><span style=\"font-size:18px;\">&gt;171.0</span><span style=\"font-size:18px;\">μ</span><span style=\"font-size:18px;\">mol/L(10mg/dl)</span><span style=\"font-size:18px;\">，常有肝臭</span><span style=\"font-size:18px;\">.</span><span style=\"font-size:18px;\">晚期可出现晚期可出现肝肾综合征，死前多发生消化道出血，亦可发生昏迷。肝功能严重损害，血清胆红素声速升高，</span><span style=\"font-size:18px;\">ALT</span><span style=\"font-size:18px;\">明显升高，或</span><span style=\"font-size:18px;\">ALT</span><span style=\"font-size:18px;\">下降与胆红素升高呈“酶肝分离”。多于发病后</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">—</span><span style=\"font-size:18px;\">12</span><span style=\"font-size:18px;\">周死亡，一部分患者可发展为坏死后肝硬化。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(4).</span><span style=\"font-size:18px;\">瘀胆型肝炎</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">起病及临床表现类似急性黄胆型肝炎，但自觉症状常较轻。常有明显肝肿大，皮肤搔痒。肝功能检查血胆红素明显升高，以直接胆红素为主，表现为梗阻性黄疸。大便色浅转肽酶、碱性磷酸酶以及</span><span style=\"font-size:18px;\">5-</span><span style=\"font-size:18px;\">核苷酸酶等梗阻指标升高。尿中胆红素强阳性而尿胆原阴性。梗阻性黄疸持续三周以上，并除外其他肝内外梗阻性黄疸</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">包括药原性等</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">者，可诊断为本病。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>病毒性肝炎预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span> <span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">控制传染源</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">应采取以切断传播途径为重点的综合性预防措施，例如重点抓好水源保护、饮水消毒、食品卫生、粪便管理等对切断甲型肝炎的传播有重要意义。防止通过血液和体液的传播，各种医疗及预防注射</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">包括皮肤试验、卡介苗接种等</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">应实行一人一针一管，对带血清的污染物应严格消毒处理。透析病房应加强卫生管理。血液制品应予严格检测，如</span><span style=\"font-size:18px;\">HBV</span><span style=\"font-size:18px;\">标志阳性者，不得出售和使用。</span><span style=\"font-size:18px;\">HBeAg</span><span style=\"font-size:18px;\">阳性者不可从事饮食行业，饮用水卫生管理及托幼工作。</span><span style=\"font-size:18px;\">HBsAg</span><span style=\"font-size:18px;\">阳性的婴幼儿在托幼机构中应与</span><span style=\"font-size:18px;\">HBsAg</span><span style=\"font-size:18px;\">阴性者适当隔离，</span><span style=\"font-size:18px;\">HBeAg</span><span style=\"font-size:18px;\">阳性婴幼儿不应入托。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">保护易感从群</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 对与急性起病的甲型肝炎病人接触的易感人群，应用人血丙种球蛋白，有相当保护作用，注射时间越早越好</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">一般应在接触后</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">天内注射</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">。剂量为</span><span style=\"font-size:18px;\">0.02</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">0.05ml/kg</span><span style=\"font-size:18px;\">，肌注。甲型肝炎疫苗</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">甲肝疫苗</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">已在不少国家研制成功，并取得了一定效果。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 乙型肝炎血源疫苗系由纯化</span><span style=\"font-size:18px;\">HBsAg</span><span style=\"font-size:18px;\">制成，制品内要求不含</span><span style=\"font-size:18px;\">Dane</span><span style=\"font-size:18px;\">颗粒，检不出</span><span style=\"font-size:18px;\">HBcAg</span><span style=\"font-size:18px;\">、</span><span style=\"font-size:18px;\">HBeAg</span><span style=\"font-size:18px;\">和</span><span style=\"font-size:18px;\">DNA</span><span style=\"font-size:18px;\">多聚酶以及其他各种病毒，我国已正式生产。乙肝疫苗与乙型肝炎免疫球蛋白</span><span style=\"font-size:18px;\">(HBIg)</span><span style=\"font-size:18px;\">联合应用对婴儿的保护率可提高至</span><span style=\"font-size:18px;\">95%</span><span style=\"font-size:18px;\">以上。保护婴儿切断母婴传播是预防重点，对</span><span style=\"font-size:18px;\">HBsAg</span><span style=\"font-size:18px;\">阳性尤以</span><span style=\"font-size:18px;\">HBeAg</span><span style=\"font-size:18px;\">亦呈阳性的产妇所产婴儿，出生后须迅即注射乙型肝炎特异免疫球蛋白及</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">或</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">乙型肝炎疫苗。乙肝疫苗使用方法：新生儿出生当天、生后</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">个月与</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">个月各肌注</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">30</span><span style=\"font-size:18px;\">μ</span><span style=\"font-size:18px;\">g</span><span style=\"font-size:18px;\">。成人剂量为第一次</span><span style=\"font-size:18px;\">20</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">30</span><span style=\"font-size:18px;\">μ</span><span style=\"font-size:18px;\">g</span><span style=\"font-size:18px;\">，以后</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">、</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">、</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">月各肌注</span><span style=\"font-size:18px;\">20</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">30</span><span style=\"font-size:18px;\">μ</span><span style=\"font-size:18px;\">g</span><span style=\"font-size:18px;\">，首次注射</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">年后加强接种</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">次，剂量为</span><span style=\"font-size:18px;\">20</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">30</span><span style=\"font-size:18px;\">μ</span><span style=\"font-size:18px;\">g</span><span style=\"font-size:18px;\">。</span><span style=\"font-size:18px;\">HBIg</span><span style=\"font-size:18px;\">用法为新生儿出生</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">12</span><span style=\"font-size:18px;\">小时内肌注高滴度</span><span style=\"font-size:18px;\">(&gt;1</span><span style=\"font-size:18px;\">∶</span><span style=\"font-size:18px;\">100</span><span style=\"font-size:18px;\">，</span><span style=\"font-size:18px;\">000)HBIg1ml</span><span style=\"font-size:18px;\">，以后在</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">个月和</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">个月时各注射</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">次，剂量同上。</span><span style=\"font-size:18px;\"> </span> \r\n</p>', 'hsg', 'upload/1650937928207.jpg', 2, '', '2022-04-26 09:52:10');
INSERT INTO `xinwentongzhi` VALUES (116, '炭疽病的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>炭疽病症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">早期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">皮肤炭疽最为多见，可分炭疽痈和恶性水肿两型。炭疽多见于面、颈、肩、手和脚等裸露部位皮肤，初为丘疹或斑疹。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">晚期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">水疱坏死破裂成浅小溃疡，血样分泌物结成黑色似炭块的干痂，黑痂坏死区的直径大小不等，自</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">2cm</span><span style=\"font-size:18px;\">至</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">6cm</span><span style=\"font-size:18px;\">，水肿区直径可达</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">20cm</span><span style=\"font-size:18px;\">，坚实、疼痛不著、溃疡不化脓等为其特点</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">寒战</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">高热</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">胸痛</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">呼吸困难</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">腹胀</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">斑疹</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">一、症状</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 潜伏期</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">日，最短仅</span><span style=\"font-size:18px;\">12</span><span style=\"font-size:18px;\">小时，最长</span><span style=\"font-size:18px;\">12</span><span style=\"font-size:18px;\">日。临床可分以下五型。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">、皮肤炭疽：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">最为多见，约占</span><span style=\"font-size:18px;\">95%</span><span style=\"font-size:18px;\">，可分炭疽痈和恶性水肿两型。主要发生于牧民及与皮毛、肉食、畜产等职业有关的职工，初起为坏死性痈样损害及化脓性淋巴腺炎，进而发生转移性脓疡，伴有严重的全身症状。开始表现为类似蚊虫叮咬的小疱，但是一到两天之后则呈疱疹状，里面含有淡黄色液体，周围组织硬而肿，第</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">日疱疹状中心区呈现出血性坏死，稍微凹凹下陷，周围有成群小水疱，水肿区继续扩大。第</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">日水疱坏死破裂成浅小溃疡，血样分泌物结成黑色似炭块的干痂，痂下有肉芽组织形成为炭疽痈。周围组织有非凹陷性水肿。黑痂坏死区的直径大小不等，自</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">2cm</span><span style=\"font-size:18px;\">至</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">6cm</span><span style=\"font-size:18px;\">，水肿区直径可达</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">20cm</span><span style=\"font-size:18px;\">，坚实、疼痛不著、溃疡不化脓等为其特点。继之水肿渐退，黑痂在</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">周内脱落，再过</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">周愈合成疤。发病</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">日后出现发热、头痛、局部淋巴结肿大及脾肿大等。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 炭疽杆菌为</span><span style=\"font-size:18px;\">Gram</span><span style=\"font-size:18px;\">阳性有荚膜的粗大杆菌，属芽胞杆菌属。对炭疽杆菌最敏感的是有蹄类动物，因此，受到污染的牧场、牧草和积水如不经过适当的消毒，将成为炭疽的传染源。人类由于受伤的皮肤接触病畜、死畜或含有芽胞的皮毛、土壤等机会较多，所以皮肤炭疽在三型炭疽中发病率最高。吸入带有芽胞的尘埃可能患肺炭疽。喝了污染的水和病畜的奶或吃了病畜或死畜的肉类，就可能患肠炭疽。人与人之间直接传染的机会极少。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">、肺炭疽：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">死亡率高，发病急骤，有寒战高热等中毒症状。咳嗽胸痛、呼吸困难、咳血，可因呼吸循环衰竭在</span><span style=\"font-size:18px;\">24</span><span style=\"font-size:18px;\">小时内死亡，极少数发生炭疽性脑膜炎。人因接触病畜及其产品或食用病畜的肉类而被感染。潜伏期一般为</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">天。多为原发性，也可继发于皮肤炭疽。初期为轻微的上呼吸道感染，低热、干咳、肌痛。数天后病情急骤加重，表现为高热、寒战、呼吸急促、喘鸣、发绀、咯血样痰、大汗和心率增速，颈、胸部可有皮下水肿。危重，常并发败血症和感染性休克，也可继发脑膜炎，表现为剧烈头痛、呕吐、抽搐、昏迷，有明显脑膜刺激症。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">、肠炭疽：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">主要是由于进食带菌肉类所致，以急性肠道感染为特征。其主要症状为恶心、厌食、呕吐和发热，重者腹痛、吐血并有严重的水样便。肠炭疽导致的死亡病例占患者</span><span style=\"font-size:18px;\">25%</span><span style=\"font-size:18px;\">—</span><span style=\"font-size:18px;\">60%</span><span style=\"font-size:18px;\">。在炭疽相对易发生并且动物的预防接种水平较低的地区，人们应该尽量避免与牲畜和动物产品接触，也要少吃处理不当或烹饪不够火候的肉类。人们也可以接种人类用的炭疽疫苗，据称这种疫苗抵抗各种炭疽感染的有效性可达到</span><span style=\"font-size:18px;\">93%</span><span style=\"font-size:18px;\">。肠炭疽临床症状不一，可表现为急性胃肠炎型和急腹症型。前者潜伏期</span><span style=\"font-size:18px;\">12</span><span style=\"font-size:18px;\">一</span><span style=\"font-size:18px;\">18</span><span style=\"font-size:18px;\">小时，同食者可同时或相继出现严重呕吐、腹痛、水样腹泻，多于数日内迅速康复。后者起病急骤，有严重毒血症症状，持续性呕吐、腹泻、血水样便、腹胀、腹痛等，腹部有压痛或呈腹膜炎征象，若不及时治疗，帝并发败血症和感染性休克而于起病后</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">一</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">日内死亡。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">、脑膜型炭疽：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">常继发于皮肤、肺及肠三型炭疽之后，临床虽少见，但发展极为迅速凶险，病死辜极高。病初多只轻度发热、不适、食欲不振，伴不同程度头痛及呕吐，以后病情迅速恶化，突然高热、惊厥、频繁呕吐、昏迷或休克，伴明显脑膜刺激征，脑脊液呈血性，细胞数增多，有明显颅内压增高症，常伴发脑疵，最终因呼吸及循环衰竭而死亡。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">、败血型炭疽：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">多继发于肺炭疽或肠炭疽，由皮肤炭疽引起者较少。可伴高热、头痛、出血、呕吐、毒血症、感染性休克、</span><span style=\"font-size:18px;\">DIC</span><span style=\"font-size:18px;\">等。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>炭疽病预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">怎样预防皮肤炭疽？</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">含炭疽的邮件会引起皮肤的接触，并有可能导致皮肤炭疽，或在小范围内引起呼吸道吸入。因此，对于境外无寄件人地址等可疑信件，应谨拆。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">怎样预防家畜炭疽？</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 本病是人与畜共患的疾病，家畜以猪、牛、马、羊、骆驼为主。对于病畜，不得剥皮或解剖，要予以焚烧。对于畜舍，应采用火烧，或用</span><span style=\"font-size:18px;\">20%</span><span style=\"font-size:18px;\">漂白粉喷撒。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">如何防止工业炭疽？</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 对可疑的畜毛可用</span><span style=\"font-size:18px;\">30</span><span style=\"font-size:18px;\">℃的</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">、</span><span style=\"font-size:18px;\">5%</span><span style=\"font-size:18px;\">盐酸和</span><span style=\"font-size:18px;\">15%</span><span style=\"font-size:18px;\">食盐溶液浸泡</span><span style=\"font-size:18px;\">40</span><span style=\"font-size:18px;\">小时，然后在</span><span style=\"font-size:18px;\">2%</span><span style=\"font-size:18px;\">苏打中浸泡</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">小时，畜毛用</span><span style=\"font-size:18px;\">110</span><span style=\"font-size:18px;\">℃蒸汽或福尔马林蒸汽消毒。运输工具用</span><span style=\"font-size:18px;\">20%</span><span style=\"font-size:18px;\">漂白粉喷洗。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">药物预防有哪些方法？</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 目前提倡用环丙沙星，每次</span><span style=\"font-size:18px;\">0</span><span style=\"font-size:18px;\">、</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">克，一日</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">次，连续用一周</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">也可用多西环素，首次用</span><span style=\"font-size:18px;\">200</span><span style=\"font-size:18px;\">毫克，以后用</span><span style=\"font-size:18px;\">100</span><span style=\"font-size:18px;\">毫克，每日</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">次，连续用一周。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">皮肤炭疽如何处理？</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 注意切勿摸弄挤压，以免细菌入血。也不能切开引流，以防败血症。保持创面清洁，局部用</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">∶</span><span style=\"font-size:18px;\">2000</span><span style=\"font-size:18px;\">高锰酸钾清洗，并用</span><span style=\"font-size:18px;\">2%</span><span style=\"font-size:18px;\">硼酸软膏局部敷。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">、管理传染源病人应隔离至创口愈合，</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">痂皮脱落或症状消失，分泌物或排泄物培养</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">次阴性</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">相隔</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">日</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">为止。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 严格隔离病畜，不用其乳类。死畜严禁剥皮或煮食，应焚毁或加大量生石灰深埋在地面</span><span style=\"font-size:18px;\">2m</span><span style=\"font-size:18px;\">以下。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">、排泄物等分别采取煮沸、</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">漂白粉、环氧乙烷、过氧乙酸、高压蒸气等消毒灭菌措施。用</span><span style=\"font-size:18px;\">Ascoli</span><span style=\"font-size:18px;\">沉淀试验检验皮毛、骨粉等样品，对染菌及可疑染菌者应予严格消毒。畜产品加工厂须改善劳动条件，加强防护设施，工作时要穿工作服、戴口罩和手套。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">、保护易感者</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">）、加强卫生宣教养成良好卫生习惯，防止皮肤受伤，如有皮肤破损，立即涂搽</span><span style=\"font-size:18px;\">3%</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">5%</span><span style=\"font-size:18px;\">碘酒，以免感染。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">）、健畜和病畜宜分开放牧，对接触病畜的畜群进行减毒活疫苗接种。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">）、对从事畜牧业、畜产品收购、加工、屠宰业等工作人员和疫区人群，每年接种炭疽杆菌减毒活菌苗</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">次。目前采用皮上划痕法，每次用菌苗</span><span style=\"font-size:18px;\">0</span><span style=\"font-size:18px;\">、</span><span style=\"font-size:18px;\">1ml</span><span style=\"font-size:18px;\">，滴于上臂外侧皮肤，划一“井”字即可。四联菌苗</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">炭疽杆菌、土拉杆菌、鼠疫杆菌和布氏杆菌</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">也已证明有效。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 国外采用保护性抗原作预防接种，第一年肌注</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">次，各相隔</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">周，</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">个月后接种第</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">次，继每年加强注射</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">次，每次均为</span><span style=\"font-size:18px;\">0</span><span style=\"font-size:18px;\">、</span><span style=\"font-size:18px;\">5ml</span><span style=\"font-size:18px;\">。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 密切接触者应留验</span><span style=\"font-size:18px;\">8</span><span style=\"font-size:18px;\">日，必要时早期应用青霉素、四环素等，对疑似患者可采取同一措施。</span> \r\n</p>', 'hsg', 'upload/1650937904504.jpg', 1, '', '2022-04-26 09:51:46');
INSERT INTO `xinwentongzhi` VALUES (117, '新生儿破伤风的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>新生儿破伤风症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">早期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">早期症状为哭闹、口张不大、吸吮困难</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">晚期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">随后牙关紧闭，面肌紧张，口角上牵，呈“苦笑”面容，伴有阵发性双拳紧握。上肢过度屈曲，下肢伸直，呈角弓反张状，呼吸肌和喉肌痉挛可引起青紫窒息。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">呼吸困难</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">牙关紧闭</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">角弓反张</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">抽搐</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">阵挛</span><span style=\"font-size:18px;\">-</span><span style=\"font-size:18px;\">强直型</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">症状</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 本病潜伏期一般为风</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">12</span><span style=\"font-size:18px;\">天，平均</span><span style=\"font-size:18px;\">7</span><span style=\"font-size:18px;\">天，故民间有</span><span style=\"font-size:18px;\">\"</span><span style=\"font-size:18px;\">锁口风</span><span style=\"font-size:18px;\">\"</span><span style=\"font-size:18px;\">、</span><span style=\"font-size:18px;\">\"</span><span style=\"font-size:18px;\">脐带风</span><span style=\"font-size:18px;\">\"</span><span style=\"font-size:18px;\">、</span><span style=\"font-size:18px;\">\"</span><span style=\"font-size:18px;\">七日风</span><span style=\"font-size:18px;\">\"</span><span style=\"font-size:18px;\">、</span><span style=\"font-size:18px;\">\"</span><span style=\"font-size:18px;\">四六风</span><span style=\"font-size:18px;\">\"</span><span style=\"font-size:18px;\">之称，新生儿破伤风首发症状，常表现为不能吸乳</span><span style=\"font-size:18px;\">( </span><span style=\"font-size:18px;\">咀嚼肌痉挛所致 </span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 在病的早期，病婴表现为想吃而不能吃，故烦躁不安，啼哭不止</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">继而由于面肌痉挛致牙关紧闭、眉举额皱、口角上牵，而出现苦笑面容，再进一步发展则出现全身抽搐。此过程在新生儿发展甚快，多在</span><span style=\"font-size:18px;\">24</span><span style=\"font-size:18px;\">小时内完成。抽搐严重则呈角弓反张，甚至频频窒息，膀胱及直肠痉挛，可致尿潴留及便秘。一般神智清醒，无发热或只有低热，发热高者可能由于并发症或严重抽搐所致，预后均较严重。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>新生儿破伤风预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 破伤风病是可以预防的。解放后新生儿破伤风发病率明显下降，亦说明了预防的效果。最积极的预防措施是推行新法接生，严格消毒断脐，如遇紧急情况，可用</span><span style=\"font-size:18px;\">2.5%</span><span style=\"font-size:18px;\">碘酒涂抹剪刀待干后断脐，结扎脐带的线也用</span><span style=\"font-size:18px;\">2.5%</span><span style=\"font-size:18px;\">碘酒消毒，以及做好新生儿脐部的护理。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 如果遇上未经消毒断脐的新生儿，则应争取在</span><span style=\"font-size:18px;\">24</span><span style=\"font-size:18px;\">小时内用</span><span style=\"font-size:18px;\">3%</span><span style=\"font-size:18px;\">双氧水或</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">：</span><span style=\"font-size:18px;\">4 000</span><span style=\"font-size:18px;\">的高锰酸钾冲洗脐部，洗后搽以酒精，重新断脐，并肌注破伤风抗毒素</span><span style=\"font-size:18px;\">1500</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3000</span><span style=\"font-size:18px;\">单位或人体免疫球蛋白</span><span style=\"font-size:18px;\">75</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">250</span><span style=\"font-size:18px;\">单位。</span> \r\n</p>', 'hsg', 'upload/1650937883097.jpg', 1, '', '2022-04-26 09:51:25');
INSERT INTO `xinwentongzhi` VALUES (118, '细菌性痢疾的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>细菌性痢疾症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">早期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">腹痛、腹泻，大便每天</span><span style=\"font-size:18px;\">2-4</span><span style=\"font-size:18px;\">次，呈水样或糊状，无脓血，有时混和黏液，解便后腹痛缓解，多数不发热或只有低热。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">晚期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">有高热，每日大便次数可达</span><span style=\"font-size:18px;\">20-30</span><span style=\"font-size:18px;\">次，大便呈脓血样，量少，腹痛剧烈，下坠较重，甚至不想离开便器，四肢发凉，很快出现脱水现象，有的可发生意识障碍。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">黏液便</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">稀便</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">阵发性腹痛</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">大便酸臭</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">恶心</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">一、细菌性痢疾症状</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">因进入人体的痢疾杆菌菌型不同，数量多少及每个人的抵抗力不同，所以，症状也各不相同。因此临床上将痢疾分为急性慢性两种。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> （一）</span> <span style=\"font-size:18px;\">急性痢疾</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 急性痢疾根据症状又分为轻型、普通型、重型和中毒型</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">种。在中毒型中，根据病情又分为休克型和脑型。虽然家长不必对孩子的病况进行严格的分型，但应了解痢疾的基本症状和病情变化的结局。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1. </span><span style=\"font-size:18px;\">轻型痢疾</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 这是痢疾中最轻的一种，一般只有轻度腹痛、腹泻，大便每天</span><span style=\"font-size:18px;\">2-4</span><span style=\"font-size:18px;\">次，呈水样或糊状，无脓血，有时混和黏液，解便后腹痛缓解，多数不发热或只有低热。由于症状不典型，常常被误诊为一般的肠炎。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2. </span><span style=\"font-size:18px;\">普通型</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 此型具有较典的痢疾症状，有发热，体温可高达</span><span style=\"font-size:18px;\">39</span><span style=\"font-size:18px;\">℃左右，个别孩子可高达</span><span style=\"font-size:18px;\">40</span><span style=\"font-size:18px;\">℃以上。开始可无腹痛、腹泻，只有恶心、呕吐、头痛等症状。因此，开始时常被误诊为重感冒，数小时之后开始出现阵发性腹痛、腹泻。开始为稀便，继而出现脓血便，因为此时肠黏膜已出现溃疡和坏死，故有明显的下坠感。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3. </span><span style=\"font-size:18px;\">重型</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 重型痢疾起病急，有高热，每日大便次数可达</span><span style=\"font-size:18px;\">20-30</span><span style=\"font-size:18px;\">次，大便呈脓血样，量少，腹痛剧烈，下坠较重，甚至不想离开便器，四肢发凉，很快出现脱水现象，有的可发生意识障碍。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4. </span><span style=\"font-size:18px;\">中毒型</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 中毒型痢疾多见于</span><span style=\"font-size:18px;\">2-7</span><span style=\"font-size:18px;\">岁的儿童，常突然发病，开始时只有高热，体温可达</span><span style=\"font-size:18px;\">40</span><span style=\"font-size:18px;\">℃，精神萎靡，面色青灰，口唇指甲青紫，皮肤常出现花纹，呼吸浅而弱，可反复出现惊厥。多数孩子没有腹痛、腹泻和呕吐，少数孩子只有轻度腹痛、腹泻，大便无脓血。除上述症状外，若出现休克症状的叫休克型，表现为脉膊细弱，血压下降或测不出，少尿或无尿，有呼吸困难、咯血现象，可因发生心力衰竭而死亡。若出现脑部症状者叫脑型。脑型的主要表现是烦燥、嗜睡、血压正常或增高，有剧烈头痛，频繁呕吐，呼吸增快，有时出现呼吸暂停、叹息样呼吸或双吸气，很快进入昏迷状态，而侧瞳孔大小不等或忽大忽小，常因呼吸衰竭而死亡。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> （二）</span> <span style=\"font-size:18px;\">慢性痢疾</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 凡病程超过</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">个月者，称为慢性痢疾、多数是因轻型痢疾治疗不彻底或孩子患有营养不良、佝偻病、贫血、寄生虫等病体质较弱所致。这种类型的病儿多无高热，有时可出现腹痛、腹泻、呕吐和低热，大便每日</span><span style=\"font-size:18px;\">3-5</span><span style=\"font-size:18px;\">次，可有正常便与黏液便和脓血便交替出现。患慢性痢疾的病儿，因长期营养不良，抵抗力差，易合并其他细菌感染，如肺炎、结核等。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">痢疾虽然有多种类型，对孩子生命有威胁的只有重型和中毒型。如果是在夏秋季节，孩子突然发烧、反复呕吐、面色苍白、四肢发凉，不管有无腹痛、腹泻，都要想到是中毒型痢疾的可能，应急送医院进行抢救治疗。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>细菌性痢疾预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">应从控制传染源，切断传播途径和增进人体抵抗力三方面着手：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">一</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">早期发现病人和带菌者</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">及时隔离和彻底治疗</span> <span style=\"font-size:18px;\">是控制菌病的重要措施，从事饮食业，保育及水厂工作的人员，更需作较长期的追查，必要时暂调离工作岗位。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">二</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">切断传播途径</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">搞好</span><span style=\"font-size:18px;\">“三管一灭”即管好水，粪和饮食以及消灭苍蝇</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">，养成饭前便后洗后的习惯，对饮食业，儿童机构工作人员定期检查带菌状态，一发现带菌者，应立即予以治疗并调离工作。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">三</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">保护易感人群可口服依莲菌株活菌苗</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">该菌无致病力，但有保护效果，保护率达</span><span style=\"font-size:18px;\">85%</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">100%</span><span style=\"font-size:18px;\">，国内已生产多价痢疾活菌苗。</span> \r\n</p>', 'hsg', 'upload/1650937859185.jpg', 1, '', '2022-04-26 09:51:00');
INSERT INTO `xinwentongzhi` VALUES (119, '艾滋病的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>艾滋病症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">典型症状：</span><span style=\"font-size:18px;\"> HIV</span><span style=\"font-size:18px;\">大量复制而</span><span style=\"font-size:18px;\">CD4</span><span style=\"font-size:18px;\">细胞急剧下降，使得大约</span><span style=\"font-size:18px;\">50</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">70%</span><span style=\"font-size:18px;\">的感染者出现类似感冒的症状如发热、咽痛、头痛、皮疹、肌肉关节痛、淋巴结肿大等。多数症状轻微，持续</span><span style=\"font-size:18px;\">1-3</span><span style=\"font-size:18px;\">周可缓解。此外，</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">在进入体内</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">天左右即可于外周血中检测出病毒成分，如</span><span style=\"font-size:18px;\">HIV-RNA</span><span style=\"font-size:18px;\">和</span><span style=\"font-size:18px;\">P24</span><span style=\"font-size:18px;\">抗原，而抗体则在感染后数周才出现。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">不明原因发热</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">免疫力降低</span><span style=\"font-size:18px;\"> HIV</span><span style=\"font-size:18px;\">感染</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">反复感染</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">一、</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">感染的临床分期</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">临床上主要将其分为三期，分别是急性期、无症状期和艾滋病期。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">1. </span><span style=\"font-size:18px;\">急性期</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">这种急性感染通常发生在初次接触</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">后</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">周左右。在此期间，</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">大量复制而</span><span style=\"font-size:18px;\">CD4</span><span style=\"font-size:18px;\">细胞急剧下降，使得大约</span><span style=\"font-size:18px;\">50</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">70%</span><span style=\"font-size:18px;\">的感染者出现类似感冒的症状如发热、咽痛、头痛、皮疹、肌肉关节痛、淋巴结肿大等。多数症状轻微，持续</span><span style=\"font-size:18px;\">1-3</span><span style=\"font-size:18px;\">周可缓解。此外，</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">在进入体内</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">天左右即可于外周血中检测出病毒成分，如</span><span style=\"font-size:18px;\">HIV-RNA</span><span style=\"font-size:18px;\">和</span><span style=\"font-size:18px;\">P24</span><span style=\"font-size:18px;\">抗原，而抗体则在感染后数周才出现。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">另外，约</span><span style=\"font-size:18px;\">9%</span><span style=\"font-size:18px;\">的病人可出现急性</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">脑膜炎，临床表现为发热、头痛、呕吐及脑膜刺激征，脑脊液检查中单核细胞增多、蛋白含量增高。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">2. </span><span style=\"font-size:18px;\">无症状期</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">本期可由原发</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">感染或急性感染症状消失后延伸而来。临床上没有任何症状，但血清中能检出</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">以及</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">核心蛋白和包膜蛋白的抗体，具有传染性。此期的长短与感染病毒的数量、型别，感染途径，机体免疫状况等多种因素有关，每个个体的病程进展有所不同，其持续时间一般为</span><span style=\"font-size:18px;\">6-8</span><span style=\"font-size:18px;\">年。一般认为经血途径感染者此期较短</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">数个月至</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">年，平均</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">年</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">，而性途径感染者较长</span><span style=\"font-size:18px;\">(6</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">12</span><span style=\"font-size:18px;\">年，平均</span><span style=\"font-size:18px;\">8</span><span style=\"font-size:18px;\">年</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">在无症状期，</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">在人体内一直维持着高度复制平衡状态，也就是病毒每天大量的产生，但同时也大量的被清除，不断的感染和杀伤</span><span style=\"font-size:18px;\">T</span><span style=\"font-size:18px;\">淋巴细胞，也不断的突变来逃避免疫系统的追击。结果在</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">感染者身上会整体表现出：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">⑴</span><span style=\"font-size:18px;\">T</span><span style=\"font-size:18px;\">淋巴细胞逐渐缓慢下降，有报道平均每年的下降数目为</span><span style=\"font-size:18px;\">40</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">60/mm3</span><span style=\"font-size:18px;\">；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">⑵血中病毒量基本维持此低水平不变，为相对动态稳定平衡状态，但这并不是一个绝对的静止的状态，而是</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">不断产生即不断被清除所得的动态平衡；这种快节奏高速率的动态过程，在大量的研究中已多次证实；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">⑶遗传基因快速的突变，从发病机制的研究来看，这个阶段是一个缓慢的</span><span style=\"font-size:18px;\">CD4</span><span style=\"font-size:18px;\">细胞的缺陷期， 这缓慢的免疫功能损伤使病人有着发生致命性感染的潜在风险。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">3. </span><span style=\"font-size:18px;\">艾滋病期</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">为感染</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">后的最终阶段。病人</span><span style=\"font-size:18px;\">CD4+ T</span><span style=\"font-size:18px;\">淋巴细胞计数明显下降，多</span><span style=\"font-size:18px;\">&lt;200/mm3</span><span style=\"font-size:18px;\">，</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">血浆病毒载量明显升高。此期的主要临床表现为</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">相关症状、各种机会性感染及肿瘤。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">在长时间的无症状期后，病人可以出现不明原因的发热、乏力、渐进性消瘦等和神经系统症状如头痛、癫痫、进行性痴呆、下肢瘫痪等，继而发生</span><span style=\"font-size:18px;\">“机会性”感染，大多数表现为卡氏肺囊肿性肺炎或中枢神经系统的感染，这是大多数</span><span style=\"font-size:18px;\">AIDS</span><span style=\"font-size:18px;\">病人死亡的直接原因。未经治疗者在进入此期后的平均生存期为</span><span style=\"font-size:18px;\">12</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">18</span><span style=\"font-size:18px;\">个月。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">此期还可出现持续性全身性淋巴结肿大</span><span style=\"font-size:18px;\">(PGL)</span><span style=\"font-size:18px;\">，其特点为①</span><span style=\"font-size:18px;\">.</span><span style=\"font-size:18px;\">除腹股沟以外有两个或两个以上部位的淋巴结肿大；②</span><span style=\"font-size:18px;\">.</span><span style=\"font-size:18px;\">淋巴结直径≥</span><span style=\"font-size:18px;\">1cm</span><span style=\"font-size:18px;\">，无压痛，无粘连；③持续时间</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">个月以上。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">此外，大约有</span><span style=\"font-size:18px;\">35%</span><span style=\"font-size:18px;\">的</span><span style=\"font-size:18px;\">AIDS</span><span style=\"font-size:18px;\">或</span><span style=\"font-size:18px;\">HIV-1</span><span style=\"font-size:18px;\">感染者会出现卡波西氏肉瘤，它是一种紫色的斑块，可出现于全身皮肤，但常见于四肢，亦可出现于口腔粘膜内，皮损附近的淋巴结亦可累及，局部皮肤损伤可发展为不易愈合的溃疡或感染霉菌，偶尔这种斑块亦可出现于内脏并扩散开来，但卡波西氏肉瘤并不构成</span><span style=\"font-size:18px;\">HIV-1</span><span style=\"font-size:18px;\">感染的死因，这类病人最后的直接死因仍是感染。还有少数的</span><span style=\"font-size:18px;\">HIV-1</span><span style=\"font-size:18px;\">感染者表现为淋巴肉瘤、黑色素瘤等其他恶性肿瘤。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">二、</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">感染的三种临床类型</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">(1) </span><span style=\"font-size:18px;\">典型进展者</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">在感染早期，其免疫功能未受损害，但在</span><span style=\"font-size:18px;\">8</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">10</span><span style=\"font-size:18px;\">年内免疫能力逐渐下降，最后发展成为艾滋病。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">(2) </span><span style=\"font-size:18px;\">快速进展者</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">这个群体的</span><span style=\"font-size:18px;\">CD4</span><span style=\"font-size:18px;\">细胞计数在</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">年内迅速下降，抗</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">的抗体水平很低，而且该抗体中和</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">的能力也较差，也可能有增强抗体。快速进展者中最显著的特征是</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">感染后一直维持较高的病毒载量。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">(3) </span><span style=\"font-size:18px;\">长期存活者</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">又称长期不进展者</span><span style=\"font-size:18px;\">)</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">这些感染者能维持健康状态达</span><span style=\"font-size:18px;\">12</span><span style=\"font-size:18px;\">年以上，目前最长已达</span><span style=\"font-size:18px;\">17</span><span style=\"font-size:18px;\">年，并且</span><span style=\"font-size:18px;\">CD4</span><span style=\"font-size:18px;\">细胞计数维持正常。此群体在所有感染者中的比例一般为</span><span style=\"font-size:18px;\">8</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">10%</span><span style=\"font-size:18px;\">，多为血友病患者、静脉吸毒者、异性接触者和新生儿。长期存活者常常具有如下特征：病毒载量低</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">血浆和</span><span style=\"font-size:18px;\">PBMC)</span><span style=\"font-size:18px;\">、</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">毒株是相对非致病性毒株、针对个体中现存</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">毒株的抗体不会加重感染、</span><span style=\"font-size:18px;\">PBMC</span><span style=\"font-size:18px;\">产生Ⅰ型细胞因子、</span><span style=\"font-size:18px;\">CD8</span><span style=\"font-size:18px;\">细胞抗病毒反应很强。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">长期存活的相关因素包括：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">⑴感染了复制能力较弱的减毒株</span><span style=\"font-size:18px;\">(nef</span><span style=\"font-size:18px;\">缺失</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">⑵</span><span style=\"font-size:18px;\">CD8</span><span style=\"font-size:18px;\">细胞抗病毒反应很强；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">⑶产生</span><span style=\"font-size:18px;\">Th1</span><span style=\"font-size:18px;\">型细胞因子</span><span style=\"font-size:18px;\">(IL-2</span><span style=\"font-size:18px;\">、</span><span style=\"font-size:18px;\">IFN-</span><span style=\"font-size:18px;\">γ、</span><span style=\"font-size:18px;\">IL-12)</span><span style=\"font-size:18px;\">；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">⑷可检测到自身毒株的中和抗体；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">⑸人体强壮；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">⑹缺失一个</span><span style=\"font-size:18px;\">CCR5</span><span style=\"font-size:18px;\">的等位基因，</span><span style=\"font-size:18px;\">CD4</span><span style=\"font-size:18px;\">细胞上</span><span style=\"font-size:18px;\">CCR5</span><span style=\"font-size:18px;\">受体的表达降低，影响</span><span style=\"font-size:18px;\">NSI</span><span style=\"font-size:18px;\">毒株的播散。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>艾滋病预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">加强自我保护意识</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">了解艾滋病毒；不要进行无保护措施的性行为，可使用安全套；不与别人共用针头，使用已消毒注射器；小心使用血制品。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">特异性预防</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">A)</span><span style=\"font-size:18px;\">随着</span><span style=\"font-size:18px;\">1993</span><span style=\"font-size:18px;\">年美国</span><span style=\"font-size:18px;\">CDC</span><span style=\"font-size:18px;\">分类诊断标准，扩大了</span><span style=\"font-size:18px;\">AIDS</span><span style=\"font-size:18px;\">的诊断范围，有利于</span><span style=\"font-size:18px;\">AIDS</span><span style=\"font-size:18px;\">的预防及治疗，依据</span><span style=\"font-size:18px;\">CD4T</span><span style=\"font-size:18px;\">淋巴细胞减少，给予一定的投药；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">B)</span><span style=\"font-size:18px;\">艾滋病疫苗：美国对含有</span><span style=\"font-size:18px;\">gp120</span><span style=\"font-size:18px;\">成份的两种艾滋病疫苗进行了第二期</span><span style=\"font-size:18px;\">296</span><span style=\"font-size:18px;\">人的试验，由于已有</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">人发生了感染，而暂时终止。泰国正进行</span><span style=\"font-size:18px;\">UBI</span><span style=\"font-size:18px;\">合成疫苗试验；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">C)</span><span style=\"font-size:18px;\">阻断母婴传播：用</span><span style=\"font-size:18px;\">AIT</span><span style=\"font-size:18px;\">于产前，产程内及婴儿治疗，有一定的保护效果。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">3.</span><span style=\"font-size:18px;\">综合预防</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">A)</span><span style=\"font-size:18px;\">普及宣传艾滋病的预防知识，了解传播途径和临床表现及预防方法</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">B)</span><span style=\"font-size:18px;\">加强道德教育，禁止滥交，尤其与外籍人员性乱行为，取缔暗娼</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">C)</span><span style=\"font-size:18px;\">避免与</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">感染者、艾滋病病人及高危人群发生性接触</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">D)</span><span style=\"font-size:18px;\">禁止与静脉药隐者共用注射器、针头</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">E)</span><span style=\"font-size:18px;\">使用进口血液，血液成份及血液制品时，必须进行</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">检测</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">F)</span><span style=\"font-size:18px;\">国内供血者严格排选，应逐步做到检测</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">阴性方能供血，严防</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">传播</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">G)</span><span style=\"font-size:18px;\">献血、献器官、组织及精液者应做</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">检测</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">H)</span><span style=\"font-size:18px;\">建立艾滋病检测中心</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">I)</span><span style=\"font-size:18px;\">提倡使用避孕套和避免肛交</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">J)</span><span style=\"font-size:18px;\">艾滋病或</span><span style=\"font-size:18px;\">HIV</span><span style=\"font-size:18px;\">感染者应避免妊娠，出生婴儿应避免母乳喂养。</span> \r\n</p>', 'hsg', 'upload/1650937835916.jpg', 1, '', '2022-04-26 09:50:38');
INSERT INTO `xinwentongzhi` VALUES (121, '新型冠状病毒肺炎的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>新型冠状病毒肺炎症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">早期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">以发热、乏力、干咳为主要表现。少数患者伴有鼻塞、 流涕、腹泻等症状。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">晚期症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">重型病例多在一周后出现呼吸困难，严 重者快速进展为急性呼吸窘迫综合征、脓毒症休克、难以纠 正的代谢性酸中毒和出凝血功能障碍。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">发烧</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">咳嗽</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">干咳</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">乏力</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">鼻塞</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">诊断标准</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">根据</span><span style=\"font-size:18px;\">2020</span><span style=\"font-size:18px;\">年</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">月</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">日，国家卫生健康委在官网发布的《新型冠状病毒肺炎诊疗方案（试行第七版）》。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">（一）疑似病例</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">结合下述流行病学史和临床表现综合分析：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">、流行病学史</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">(1) </span><span style=\"font-size:18px;\">发病前</span><span style=\"font-size:18px;\">14</span><span style=\"font-size:18px;\">天内有武汉市及周边地区，或其他有病例报告社区的旅行史或居住史；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">(2) </span><span style=\"font-size:18px;\">发病前</span><span style=\"font-size:18px;\">14</span><span style=\"font-size:18px;\">天内与新型冠状病毒感染者</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">核酸检测阳 性者</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">有接触史；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">(3) </span><span style=\"font-size:18px;\">发病前</span><span style=\"font-size:18px;\">14</span><span style=\"font-size:18px;\">天内曾接触过来自武汉市及周边地区，或来自有病例报告社区的发热或有呼吸道症状的患者；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">(4) </span><span style=\"font-size:18px;\">聚集性发病</span><span style=\"font-size:18px;\">(2</span><span style=\"font-size:18px;\">周内在小范围如家庭、办公室、学校班级等场所，出现</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">例及以上发热和</span><span style=\"font-size:18px;\">/</span><span style=\"font-size:18px;\">或呼吸道症状的病例</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">、临床表现</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">(1) </span><span style=\"font-size:18px;\">发热和</span><span style=\"font-size:18px;\">/</span><span style=\"font-size:18px;\">或呼吸道症状；</span><span style=\"font-size:18px;\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">(2) </span><span style=\"font-size:18px;\">具有上述新型冠状病毒肺炎影像学特征；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">(3) </span><span style=\"font-size:18px;\">发病早期白细胞总数正常或降低，淋巴细胞计数正常或减少。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">有流行病学史中的任何一条，且符合临床表现中的任意</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">条。无明确流行病学史的，符合临床表现中的</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">条。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">（二）确诊病例</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">疑似病例，具备以下病原学证据之一者：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">1. </span><span style=\"font-size:18px;\">实时荧光</span><span style=\"font-size:18px;\">RT-PCR</span><span style=\"font-size:18px;\">检测新型冠状病毒核酸阳性；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">2. </span><span style=\"font-size:18px;\">病毒基因测序，与已知的新型冠状病毒高度同源；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">3. </span><span style=\"font-size:18px;\">血清新型冠状病毒特异性</span><span style=\"font-size:18px;\">IgM</span><span style=\"font-size:18px;\">抗体和</span><span style=\"font-size:18px;\">IgG</span><span style=\"font-size:18px;\">抗体阳性；血清新型冠状病毒特异性</span><span style=\"font-size:18px;\">IgG</span><span style=\"font-size:18px;\">抗体由阴性转为阳性或恢复期较急性期</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">倍及以上升高。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">（三）临床分型</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">(1)</span><span style=\"font-size:18px;\">轻型。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">临床症状轻微，影像学未见肺炎表现。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">(2)</span><span style=\"font-size:18px;\">普通型。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">具有发热、呼吸道等症状，影像学可见肺炎表现。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">(3)</span><span style=\"font-size:18px;\">重型。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">成人符合下列任何一条：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">1. </span><span style=\"font-size:18px;\">出现气促，</span><span style=\"font-size:18px;\">RR330</span><span style=\"font-size:18px;\">次</span><span style=\"font-size:18px;\">/</span><span style=\"font-size:18px;\">分；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">2. </span><span style=\"font-size:18px;\">静息状态下，指氧饱和度≤</span><span style=\"font-size:18px;\">93%</span><span style=\"font-size:18px;\">；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">3. </span><span style=\"font-size:18px;\">动脉血氧分压（</span><span style=\"font-size:18px;\">PaO2</span><span style=\"font-size:18px;\">）</span><span style=\"font-size:18px;\">/</span><span style=\"font-size:18px;\">吸氧浓度（</span><span style=\"font-size:18px;\">Fi02</span><span style=\"font-size:18px;\">）≤</span><span style=\"font-size:18px;\">300mmHg </span><span style=\"font-size:18px;\">（</span><span style=\"font-size:18px;\">1mmHg=O. 133kPa</span><span style=\"font-size:18px;\">）。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">高海拔（海拔超过</span><span style=\"font-size:18px;\">1000</span><span style=\"font-size:18px;\">米）地区应根据以下公式对</span><span style=\"font-size:18px;\">Pa02/Fi02 </span><span style=\"font-size:18px;\">进行校正：</span><span style=\"font-size:18px;\">PaO2/FiO2 x [</span><span style=\"font-size:18px;\">大气压（</span><span style=\"font-size:18px;\">mmHg</span><span style=\"font-size:18px;\">）</span><span style=\"font-size:18px;\">/760] </span><span style=\"font-size:18px;\">。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">肺部影像学显示</span><span style=\"font-size:18px;\">24-48</span><span style=\"font-size:18px;\">小时内病灶明显进展＞</span><span style=\"font-size:18px;\">50%</span><span style=\"font-size:18px;\">者按重型管理。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">儿童符合下列任何一条：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">出现气促（</span><span style=\"font-size:18px;\">&lt;2</span><span style=\"font-size:18px;\">月龄，</span><span style=\"font-size:18px;\">RR</span><span style=\"font-size:18px;\">≥</span><span style=\"font-size:18px;\">60</span><span style=\"font-size:18px;\">次</span><span style=\"font-size:18px;\">/</span><span style=\"font-size:18px;\">分；</span><span style=\"font-size:18px;\">2?12</span><span style=\"font-size:18px;\">月龄，</span><span style=\"font-size:18px;\">RRN</span><span style=\"font-size:18px;\">≥</span><span style=\"font-size:18px;\">50</span><span style=\"font-size:18px;\">次</span><span style=\"font-size:18px;\">/</span><span style=\"font-size:18px;\">分；</span><span style=\"font-size:18px;\">1?5</span><span style=\"font-size:18px;\">岁，</span><span style=\"font-size:18px;\">RRN</span><span style=\"font-size:18px;\">≥</span><span style=\"font-size:18px;\">40</span><span style=\"font-size:18px;\">次</span><span style=\"font-size:18px;\">/</span><span style=\"font-size:18px;\">分；＞</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">岁，</span><span style=\"font-size:18px;\">RRN</span><span style=\"font-size:18px;\">≥</span><span style=\"font-size:18px;\">30</span><span style=\"font-size:18px;\">次</span><span style=\"font-size:18px;\">/</span><span style=\"font-size:18px;\">分），除外发热和哭闹的影响；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">2. </span><span style=\"font-size:18px;\">静息状态下，指氧饱和度≤</span><span style=\"font-size:18px;\">92%</span><span style=\"font-size:18px;\">；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">3. </span><span style=\"font-size:18px;\">辅助呼吸（呻吟、鼻翼扇动、三凹征），发绀，间歇性呼吸暂停；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">4. </span><span style=\"font-size:18px;\">出现嗜睡、惊厥；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">5. </span><span style=\"font-size:18px;\">拒食或喂养困难，有脱水征。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">(4)</span><span style=\"font-size:18px;\">危重型。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">符合以下情况之一者</span><span style=\"font-size:18px;\">:</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">出现呼吸衰竭，且需要机械通气</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">出现休克</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">3.</span><span style=\"font-size:18px;\">合并其他器官功能衰竭需</span><span style=\"font-size:18px;\">ICU</span><span style=\"font-size:18px;\">监护治疗。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">（四）重型、危重型临床预警指标</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">（一）成人。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">1. </span><span style=\"font-size:18px;\">外周血淋巴细胞进行性下降；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">2. </span><span style=\"font-size:18px;\">外周血炎症因子如“</span><span style=\"font-size:18px;\">IL-6</span><span style=\"font-size:18px;\">、</span><span style=\"font-size:18px;\">C</span><span style=\"font-size:18px;\">反应蛋白进行性上升；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">3. </span><span style=\"font-size:18px;\">乳酸进行性升高；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">4. </span><span style=\"font-size:18px;\">肺内病变在短期内迅速进展。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">（二）儿童。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">1. </span><span style=\"font-size:18px;\">呼吸频率增快；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">2. </span><span style=\"font-size:18px;\">精神反应差、嗜睡；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">3. </span><span style=\"font-size:18px;\">乳酸进行性升高；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">4. </span><span style=\"font-size:18px;\">影像学显示双侧或多肺叶浸润、胸腔积液或短期内病变快速进展；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">5. 3</span><span style=\"font-size:18px;\">月龄以下的婴儿或有基础疾病（先天性心脏病、支气管肺发育不良、呼吸道畸形、异常血红蛋白、重度营养不良等）， 有免疫缺陷或低下（长期使用免疫抑制剂）。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>新型冠状病毒肺炎预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">目前正处于冬春季传染病高发季节，市民要增强卫生健康意识，加强锻炼，规律作息，提高自身免疫力。注意保持室内空气流通，避免到封闭、空气不流通的公众场合和人多集中地方，必</span><span style=\"font-size:18px;\">.</span><span style=\"font-size:18px;\">要时可佩戴口罩。外出回家后，及时洗手、洗鼻。如有发热和其他呼吸道感染症状，特别是持续发热不退，及时到医疗机构就诊。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">目前对密切接触者采取较为严格的医学观察等预防性公共卫生措施十分必要，这是一种对公众健康安全负责任的态度，也是国际社会通行的做法。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">参考其他冠状病毒所致疾病潜伏期、此次新型冠状病毒病例相关信息和当前防控实际，将密切接触者医学观察期定为</span><span style=\"font-size:18px;\">14</span><span style=\"font-size:18px;\">天，并对密切接触者进行居家医学观察。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">按照要求进行居家医学观察，不用恐慌，不要上班，不要随便外出，做好自我身体状况观察，定期接受社区医生的随访，如果出现发热、咳嗽等异常临床表现，及时向当地疾病预防控制机构报告，在其指导下到指定医疗机构进行排查、诊治等。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">戴口罩</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">戴口罩是阻断呼吸道分泌物传播的有效手段。选择医用外科口罩能很好地预防呼吸道疾病。一次性医用口罩佩戴时，要将折面完全展开，将嘴、鼻、下颌完全包住，然后压紧鼻夹</span><span style=\"font-size:18px;\">,</span><span style=\"font-size:18px;\">使口罩与面部完全贴合。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">戴口罩前应洗手，或者在戴口罩过程中避免手接触到口罩内侧面，减少口罩被污染的可能。分清楚口罩的内外、上下，浅色面为内，应该贴着嘴鼻，深色面朝外</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">金属条</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">鼻夹</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">一端是口罩的上方。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">定期更换、不可戴反，更不能两面轮流戴。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">勤洗手</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">在餐前便后、外出回家、接触垃圾、抚摸动物后，要记得洗手。洗手时，要注意用流动的水和使用肥皂</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">皂液</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">洗手，揉搓的时间不少于</span><span style=\"font-size:18px;\">15</span><span style=\"font-size:18px;\">秒。为了方便记忆，操搓步骤简单归纳为七字口诀</span><span style=\"font-size:18px;\">:</span><span style=\"font-size:18px;\">“内——外——夹——弓——大——立——腕”。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">返程的火车上：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">戴好口罩，尽量不要吃东西。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">目前多地已经停止公路客运，如果你工作的地方在外地，除了开私家车，火车是重要的交通工具。虽然火车上会按照要求做好消毒工作，但因为火车属于密闭空间，而且人员密集复杂，所以一定要做好防护。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">浙江大学医学院附属第一院传染病科主任医师肖永红提醒：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1. </span><span style=\"font-size:18px;\">一定要佩戴防护效果好的口罩，如</span><span style=\"font-size:18px;\">N95</span><span style=\"font-size:18px;\">口罩或医用防护口罩，避免病毒的呼吸道传染途径。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">尽量不要在火车上进食，如果一定饿了要吃饭，建议在进食之前做好手部清洁，避免接触传播的可能。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">到达工作地后：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">连续</span><span style=\"font-size:18px;\">14</span><span style=\"font-size:18px;\">天做自我检测，确保没有感染。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 中国工程院院士、国家卫健委高级别专家组成员李兰娟提醒，如果你从来没有去过疫区，没接触过感染的人，身体健康，体温正常，回程后也是健康的，那没必要隔离</span><span style=\"font-size:18px;\">; </span><span style=\"font-size:18px;\">如果你曾经接触过疫区，就要坚持医学观察，确保没有感染</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 中国疾控中心副主任冯子健提醒：从离开疫情流行地区时间开始，连续</span><span style=\"font-size:18px;\">14</span><span style=\"font-size:18px;\">天进行自我健康状况的检测（如测体温），每天做</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">次。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">除了自我检测，还要做好以下事情。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">减少外出，居家保持房间的通风</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">勤洗手，做好垃圾桶、马桶、厨房、桌子等家庭环境的消毒</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">减少与其他人的聚餐等密切接触，避免到商场等人员密集的公共场所</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">如果出现发烧、咳嗽等症状，要及时去定点医院就诊。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">开始工作后：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">上班路上尽量不乘坐公共交通工具，工作中保持距离。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">上下班路上：尽量不乘坐公共交通工具，建议步行、骑行或乘坐私家车、班车上班。如必须乘坐公共交通工具时，务必全程佩戴口罩。途中尽量避免用手触摸车上物品。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">公司乘坐电梯时：戴好口罩。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 工作时：用酒精消毒电话、电脑、鼠标等办公用品；同事交流时保持</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">米以上距离，多人办公时戴口罩；如果外来人员，也要提醒对方佩戴口罩；尽量避免多人集中开会；勤洗手，多喝水，多通风。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">吃工作餐：采用分餐进食；避免人员密集场所扎堆聚餐；饭前最后一刻再摘掉口罩；饭前要洗手；吃饭时尽量不说话。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">无论是居家还是工作环境，或者说在某些交通工具上，想要预防冠状病毒感染，讲卫生，勤洗手，戴口罩，消毒等基本的防护方法都是适用的。</span> \r\n</p>', 'hsg', 'upload/1650937815300.jpg', 5, '', '2022-05-14 21:37:05');
INSERT INTO `xinwentongzhi` VALUES (123, '鼠疫的症状及预防', '症状及预防', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>鼠疫症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">典型症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">起病急，以畏寒或者寒战发热等开始，体温迅速上升至</span><span style=\"font-size:18px;\">39</span><span style=\"font-size:18px;\">度至</span><span style=\"font-size:18px;\">40</span><span style=\"font-size:18px;\">度，头痛及四肢疼痛剧烈，有时有恶心、呕吐等，病人意识迅速模糊，表情惊惶，言语含糊，颜面和眼结膜极度充血，步态蹒跚如酒醉状。此时病人极度衰竭，脉博与呼吸加速，脉律不规则，血压下降，肝脾肿大。有时皮肤、粘膜出现瘀血或皮下出血，鼻出血、尿血、胃肠道出血等</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">相关症状：</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">高热寒战</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">头痛</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">肢体疼痛</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">意识模糊</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">淋巴结肿大</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">高热</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">毒血症</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">出血倾向</span><span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\">全身感染中毒症状</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 鼠疫病的潜伏期一般为</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">至</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">天，曾接受预防注射者，则潜伏期延长至</span><span style=\"font-size:18px;\">9</span><span style=\"font-size:18px;\">至</span><span style=\"font-size:18px;\">12</span><span style=\"font-size:18px;\">天</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">机体抵抗力弱，而病菌毒力特强者，潜伏期可缩短至数小时。临床上主要有轻型、腺型、肺型及败血型等类型，除轻型外，初期的全身中毒症状大致相似。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 全身中毒症状：起病急，以畏寒或者寒战发热等开始，体温迅速上升至</span><span style=\"font-size:18px;\">39</span><span style=\"font-size:18px;\">度至</span><span style=\"font-size:18px;\">40</span><span style=\"font-size:18px;\">度，头痛及四肢疼痛剧烈，有时有恶心、呕吐等，病人意识迅速模糊，表情惊惶，言语含糊，颜面和眼结膜极度充血，步态蹒跚如酒醉状。此时病人极度衰竭，脉博与呼吸加速，脉律不规则，血压下降，肝脾肿大。有时皮肤、粘膜出现瘀血或皮下出血，鼻出血、尿血、胃肠道出血等。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 肺鼠疫多由腺鼠疫血行播散引起，少数为原发性吸入性肺鼠疫。咳痰为脓血痰、胸痛、咯血，呼吸困难、发绀。肺部体征少，可有少量湿啰音及胸膜摩擦音。体征与病情严重程度不一致为本病之特征，如抢救不及时，可出现意识障碍，多死于休克及呼吸衰竭。因病死后全身皮肤呈黑紫色，故有</span><span style=\"font-size:18px;\">“黑死病”之称。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">鼠疫的基本类型和症状表现</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">肺鼠疫</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 是最严重的一型，病死率极高。该型起病急骤，发展迅速，除严重中毒症状外，在起病</span><span style=\"font-size:18px;\">24</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">36</span><span style=\"font-size:18px;\">小时内出现剧烈胸痛、咳嗽、咯大量泡沫血痰或鲜红色痰</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">呼吸急促，并迅速呈现呼吸困难和紫绀</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">肺部可闻及少量散在湿罗音、可出现胸膜摩擦音</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">胸部</span><span style=\"font-size:18px;\">X</span><span style=\"font-size:18px;\">线呈支气管炎表现，与病情严重程度极不一致。如抢救不及时，多于</span><span style=\"font-size:18px;\">2-3</span><span style=\"font-size:18px;\">日内，因心力衰竭，出血而死亡。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">腺鼠疫</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 占</span><span style=\"font-size:18px;\">85</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">90%</span><span style=\"font-size:18px;\">。除全身中毒症状外，以急性淋巴结炎为特征。因下肢被蚤咬机会较多，故腹股沟淋巴结炎最多见，约占</span><span style=\"font-size:18px;\">70%;</span><span style=\"font-size:18px;\">其次为腋下，颈及颌下。也可几个部位淋巴结同时受累。局部淋巴结起病即肿痛，病后第</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">天症状迅速加剧，红、肿、热、痛并与周围组织粘连成块，剧烈触痛，病人处于强迫体位。</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">日后淋巴结化脓溃破，随之病情缓解。部分可发展成败血症、严重毒血症及心力衰竭或肺鼠疫而死</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">用抗生素治疗后，病死率可降至</span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">10%</span><span style=\"font-size:18px;\">。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">败血型鼠疫</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\"> 又称暴发型鼠疫。可原发或继发。原发型鼠疫因免疫功能差，菌量多，毒力强，所以发展极速。常突然高热或体温不升，神志不清，谵妄或昏迷。无淋巴结肿。皮肤粘膜出血、鼻衄、呕吐、便血或血尿、</span><span style=\"font-size:18px;\">DIC</span><span style=\"font-size:18px;\">和心力衰竭，多在发病后</span><span style=\"font-size:18px;\">24</span><span style=\"font-size:18px;\">小时内死亡，很少超过</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">天。病死率高达</span><span style=\"font-size:18px;\">100%</span><span style=\"font-size:18px;\">。因皮肤广泛出血、瘀斑、紫绀、坏死，故死后尸体呈紫黑色，俗称“黑死病”。继发性败血型鼠疫，可由肺鼠疫、腺鼠疫发展而来，症状轻重不一。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">轻型鼠疫</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">又称小鼠疫，发热轻，患者可照常工作，局部淋巴结肿大，轻度压痛，偶见化脓。血培养可阳性。多见于流行初、末期或预防接种者。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">其他少见类型</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">、皮肤鼠疫病菌侵入局部皮肤出现痛疼性红斑点，数小时后发展成水泡，形成脓疱，表面复有黑色痂皮，周围有暗红色浸润，基底为坚硬溃疡，颇似皮肤炭疽。偶见全身性脓疱，类似天花，有天花样鼠疫之称。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">、脑膜脑炎型多继发于腺型或其它型鼠疫。在出现脑膜脑炎症状、体征时、脑脊液为脓性，涂片或培养可检出鼠疫杆菌。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">、眼型病菌侵入眼结膜，致化脓性结膜炎。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">、肠炎型除全身中毒症状外，有腹泻及粘液血样便，并有呕吐、腹痛、里急后重，粪便可检出病菌。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">5</span><span style=\"font-size:18px;\">、咽喉型为隐性感染。无症状，但从鼻咽部可分离出鼠疫杆菌。见于预防接种者。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:24px;\"><strong>鼠疫预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">[</span><span style=\"font-size:18px;\">预防</span><span style=\"font-size:18px;\">]</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">一</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">严格控制传染源</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">管理患者 发现疑似或确诊患者，应立即按紧急疫情上报，同时将患者严密隔离，禁止探视及病人互相往来。病人排泄物应彻底消毒，病人死亡应火葬或深埋。接触者应检疫</span><span style=\"font-size:18px;\">9</span><span style=\"font-size:18px;\">天，对曾接受预防接种者，检疫期应延至</span><span style=\"font-size:18px;\">12</span><span style=\"font-size:18px;\">天。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">消灭动物传染源 对自然疫源地进行疫情监测，控制鼠间鼠疫。广泛开展灭鼠爱国卫生运动。旱獭在某些地区是重要传染源，也应大力捕杀。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">3.</span><span style=\"font-size:18px;\">切断传播途径</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">灭蚤 灭蚤必须彻底，对猫、狗，家畜等也要喷药。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">加强交通及国镜检疫 对来自疫源地的外国船只、车辆、飞机等均应进行严格的国境卫生检疫，实施灭鼠、灭蚤消毒，对乘客进行隔离留检。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">三</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">保护易感者</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">1.</span><span style=\"font-size:18px;\">预防接种 自鼠间开始流行时，对疫区及其周围的居民、进入疫区的工作人员，均应进行预防接种。常用为</span><span style=\"font-size:18px;\">EV</span><span style=\"font-size:18px;\">无毒株干燥活菌苗，皮肤划痕法接种，即</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">滴菌液，相距</span><span style=\"font-size:18px;\">3-4cm</span><span style=\"font-size:18px;\">。</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">周后可获免疫。一般每年接种一次，必要时</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">个月后再接种一次。我国新研制的</span><span style=\"font-size:18px;\">06173</span><span style=\"font-size:18px;\">菌苗免疫动物后产生</span><span style=\"font-size:18px;\">F1</span><span style=\"font-size:18px;\">抗体较</span><span style=\"font-size:18px;\">EV</span><span style=\"font-size:18px;\">株效果高</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">倍。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">2.</span><span style=\"font-size:18px;\">个人防护 进入疫区的医务人员，必须接种菌苗，两周后方能进入疫区。工作时必须着防护服，戴口罩、帽子、手套、眼镜、穿胶鞋及隔离衣。接触患者后可服下列一种药物预防，四环素每日</span><span style=\"font-size:18px;\">2g</span><span style=\"font-size:18px;\">，分</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">次服</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">磺胺嘧啶每日</span><span style=\"font-size:18px;\">2g</span><span style=\"font-size:18px;\">，分</span><span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">次服</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">或链霉素每日</span><span style=\"font-size:18px;\">1g</span><span style=\"font-size:18px;\">，分</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">次肌注，连续</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">天。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">避免去鼠类滋生地。如去鼠疫流行地区，采取对啮齿类动物和跳蚤的防护措施。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">避免接触在路边或林中发现的有病或死去的动物。当大量啮齿类动物死于鼠疫，蚤类叮咬的危险就很高。携带病菌饥饿的蚤类必急于寻找新的宿主。在森林或野外有许多啮齿类动物生活地方，要仔细照看儿童和宠物。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">到鼠疫流行地区的国际旅行者被感染鼠疫的危险性不大。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">如要去鼠疫流行地区，可向医生或健康部门咨询有关情况。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">向医生或健康部门及时报告所有疑似病例。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span> </span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">二</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">预后</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-size:18px;\">肺型、败血型鼠疫患者若不及时抢救，预后极差。年龄愈小或愈老者预后愈差，关键在于早期诊断，及时治疗，每可转危为安。</span> \r\n</p>', 'hsg', 'upload/1650937748696.jpg', 1, '', '2022-04-26 09:49:10');
INSERT INTO `xinwentongzhi` VALUES (141, '变幻图5', '变幻图', '', 'hsg', 'upload/1652534855472.jpg', 1, '', '2022-05-14 21:27:55');
INSERT INTO `xinwentongzhi` VALUES (143, '霍乱的症状及预防', '症状及预防', '<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\"><strong>霍乱症状</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">典型症状：</span> <span style=\"font-size:18px;\">(1)</span><span style=\"font-size:18px;\">剧烈吐泻。突起剧烈腹泻，继而呕吐 </span><span style=\"font-size:18px;\">2)</span><span style=\"font-size:18px;\">不同程度脱水 </span><span style=\"font-size:18px;\">(3)</span><span style=\"font-size:18px;\">腓肠肌和腹直肌痉挛性疼痛 </span><span style=\"font-size:18px;\">(4)</span><span style=\"font-size:18px;\">神志改变。烦躁不安 </span><span style=\"font-size:18px;\">5)</span><span style=\"font-size:18px;\">循环衰竭。脉搏微弱</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">相关症状：</span> <span style=\"font-size:18px;\">水样便</span> <span style=\"font-size:18px;\">皮肤弹性差</span> <span style=\"font-size:18px;\">乏力</span> <span style=\"font-size:18px;\">分泌性腹泻</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">【临床表现】</span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">霍乱的临床表现可分为四个阶段</span><span style=\"font-size:18px;\">——潜伏期、呕泻期、脱水期和反应期：</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">、潜伏期</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">霍乱潜伏期短的仅数小时，长的</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">天，一般为</span><span style=\"font-size:18px;\">1</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">天。多数患者起病急骤，有头昏、乏力或轻度腹泻等症状，一般不太会引起注意。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">、呕泻期</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">以剧烈的腹泻开始，继而出现呕吐，大多数情况下，霍乱病人在此病程中，会因为剧烈的腹泻和呕吐而意识到问题的严重性，此时，应尽早去医院寻求治疗。霍乱最明显的症状为腹泻、呕吐和脱水，不同于一般痢疾和肠炎等</span><span style=\"font-size:18px;\">,</span><span style=\"font-size:18px;\">这期间具体表现须注意如下几点</span><span style=\"font-size:18px;\">:</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">①无明显发烧症状</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">②腹泻为霍乱的最主要症状，但无里急后重感，也不伴有腹痛</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">③若出现疼痛症状，为痉挛性疼痛，腿部腓肠肌也会出现疼痛感</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">④排出粪便次数多，且多为水样便</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">⑤腹泻后若出现呕吐，多为喷射性呕吐，严重者呕吐出类似排泄物的“米泔水”。</span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">、脱水期</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">由于剧烈的呕吐与腹泻，病人体内会有大量水分和电解质丧失，出现脱水、电解质紊乱等状况，严重者还会出现循环衰竭。本期病程长短，主要决定于治疗及时和正确与否，所以持续时间为几小时或</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">天不等</span><span style=\"font-size:18px;\">,</span><span style=\"font-size:18px;\">具体表征有</span><span style=\"font-size:18px;\">:</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">①皮肤没有光泽，干皱，弹性变差</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">②眼眶深陷、两颊深凹</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">③体内丧失大量水分和电解质。</span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">4</span><span style=\"font-size:18px;\">、恢复期</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">经过治疗补液及辅助用药后，腹泻、呕吐停止，脱水症状也会逐渐消失，有的病人可能还会伴有轻微发热，若还有其它并发症状存在，辅以对应治疗。</span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\"><strong>霍乱预防</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">霍乱经口感染，被污染的水和食物是常见的传播媒介。因此，搞好饮食卫生，把住病从口入关，是预防霍乱的关键措施：</span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">只饮用安全卫生的水或煮沸、加氯消毒过的水</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">食物要彻底煮熟，剩余食品要彻底再加热，并趁热吃</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">不吃未煮熟的食物</span><span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">可削皮、剥壳者例外</span><span style=\"font-size:18px;\">);</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">生、熟食物要分开</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">接触排泄物后、加工食物或吃饭前均应洗手</span><span style=\"font-size:18px;\">;</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">保持厨房清洁和搞好室内外环境卫生。</span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">一</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">控制传染源及时检出病人，尽早予以隔离治疗。对密切接触者应严密检疫，进行粪便检查和药物治疗，粪便培养应每日一次，连续</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">天，每一次粪检后给予服药可减少带菌者，一般应用多西环素</span><span style=\"font-size:18px;\">200mg</span><span style=\"font-size:18px;\">顿服，次日口服</span><span style=\"font-size:18px;\">100mg</span><span style=\"font-size:18px;\">。儿童每日</span><span style=\"font-size:18px;\">6mg/kg</span><span style=\"font-size:18px;\">，连服</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">日。亦可应用诺佛沙星，每次</span><span style=\"font-size:18px;\">200mg</span><span style=\"font-size:18px;\">，每日</span><span style=\"font-size:18px;\">3</span><span style=\"font-size:18px;\">次，连服</span><span style=\"font-size:18px;\">2</span><span style=\"font-size:18px;\">日。同时应作好国境卫生检疫和国内交通检疫，一旦发现病人或疑似患者，应立即进行隔离治疗，并对交通工具进行彻底消毒。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">二</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">切断传播途径加强饮水消毒和食品管理，对病人和带菌者的排泄物进行彻底消毒。此外应消灭苍蝇等传播媒介。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">(</span><span style=\"font-size:18px;\">三</span><span style=\"font-size:18px;\">)</span><span style=\"font-size:18px;\">提高人群免疫力以往应用全菌死菌苗或并用霍乱肠毒素的类毒素疫苗免疫人群，由于保护率低，保护时间短，且不能防止隐性感染和带菌者，因而已不提倡应用。目前国外应用基因工程技术制成并试用的有多种菌苗，现仍在扩大试用，其中包括：</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">(1)B</span><span style=\"font-size:18px;\">亚单位</span><span style=\"font-size:18px;\">-</span><span style=\"font-size:18px;\">全菌体菌苗</span><span style=\"font-size:18px;\">(BS-WC)</span><span style=\"font-size:18px;\">：这是由灭活的霍乱弧菌全菌体细胞</span><span style=\"font-size:18px;\">(WC)</span><span style=\"font-size:18px;\">和纯化的霍乱肠毒素</span><span style=\"font-size:18px;\">B</span><span style=\"font-size:18px;\">亚单位</span><span style=\"font-size:18px;\">(BS)</span><span style=\"font-size:18px;\">组成的菌苗。其中</span><span style=\"font-size:18px;\">WC</span><span style=\"font-size:18px;\">细胞壁含有脂多糖</span><span style=\"font-size:18px;\">(LPS)</span><span style=\"font-size:18px;\">和霍乱毒素协同菌毛</span><span style=\"font-size:18px;\">(TCP)</span><span style=\"font-size:18px;\">等抗原，能诱导机体产生抗菌抗体，从而抑制霍乱弧菌在肠道定居</span><span style=\"font-size:18px;\">;</span><span style=\"font-size:18px;\">而</span><span style=\"font-size:18px;\">BS</span><span style=\"font-size:18px;\">产生的抗毒抗体，则能中和</span><span style=\"font-size:18px;\">CT</span><span style=\"font-size:18px;\">的</span><span style=\"font-size:18px;\">B</span><span style=\"font-size:18px;\">亚单位，使霍乱肠毒素不能与肠黏膜受体结合，而无从发挥肠毒素作用。此菌苗保护率为</span><span style=\"font-size:18px;\">65%</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">85%</span><span style=\"font-size:18px;\">，对古典生物型霍乱弧菌的预防作用优于埃尔托生物型霍乱弧菌。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;\">(2)</span><span style=\"font-size:18px;\">减毒口服活菌苗</span><span style=\"font-size:18px;\">(CVDl03-HgR)</span><span style=\"font-size:18px;\">：这是应用</span><span style=\"font-size:18px;\">DNA</span><span style=\"font-size:18px;\">重组技术将除去</span><span style=\"font-size:18px;\">94%</span><span style=\"font-size:18px;\">的</span><span style=\"font-size:18px;\">CTXA</span><span style=\"font-size:18px;\">基因，重组于古典生物型霍乱弧菌的</span><span style=\"font-size:18px;\">569B</span><span style=\"font-size:18px;\">株中，获得减毒株</span><span style=\"font-size:18px;\">CVD103</span><span style=\"font-size:18px;\">，然后导入一个抗汞</span><span style=\"font-size:18px;\">(Hg)</span><span style=\"font-size:18px;\">的编码基因进入</span><span style=\"font-size:18px;\">hgla</span><span style=\"font-size:18px;\">的染色体位点，成为</span><span style=\"font-size:18px;\">CVD103-HgR</span><span style=\"font-size:18px;\">减毒株。此菌苗能明显对抗</span><span style=\"font-size:18px;\">O1</span><span style=\"font-size:18px;\">群古典生物型和埃尔托生物型霍乱弧菌的感染。</span><span style=\"font-size:18px;\">Taket</span><span style=\"font-size:18px;\">等报告，口服</span><span style=\"font-size:18px;\">(3</span><span style=\"font-size:18px;\">～</span><span style=\"font-size:18px;\">5)</span><span style=\"font-size:18px;\">×</span><span style=\"font-size:18px;\">108</span><span style=\"font-size:18px;\">。单一剂量</span><span style=\"font-size:18px;\">CVD103-HgR</span><span style=\"font-size:18px;\">菌后，志愿者中获得</span><span style=\"font-size:18px;\">100%</span><span style=\"font-size:18px;\">的保护作用。一般认为保护作用至少持续</span><span style=\"font-size:18px;\">6</span><span style=\"font-size:18px;\">个月。但动物实验表明此菌苗对</span><span style=\"font-size:18px;\">O139</span><span style=\"font-size:18px;\">型霍乱弧菌无保护作用。</span><span style=\"font-size:18px;\">Woldol-</span><span style=\"font-size:18px;\">等曾构建出缺失细胞毒素</span><span style=\"font-size:18px;\">(CT)</span><span style=\"font-size:18px;\">和腺苷环化酶</span><span style=\"font-size:18px;\">(ACE)zot</span><span style=\"font-size:18px;\">等毒素的</span><span style=\"font-size:18px;\">VCO139</span><span style=\"font-size:18px;\">型减毒株</span><span style=\"font-size:18px;\">Bengal3</span><span style=\"font-size:18px;\">，但其安全性和保护力等还待进一步研究。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<!--[if !supportLists]--><span style=\"font-size:18px;\">(3)&nbsp;</span><!--[endif]--><span style=\"font-size:18px;\">O139</span><span style=\"font-size:18px;\">疫苗的研究 </span><span style=\"font-size:18px;\">O139</span><span style=\"font-size:18px;\">的荚膜脂多糖不仅是重要的毒力因子，也是重要的保护性抗原，以血清蛋白为载体蛋白制作</span><span style=\"font-size:18px;\">O139</span><span style=\"font-size:18px;\">荚膜脂多糖疫苗，应用</span><span style=\"font-size:18px;\">EDC</span><span style=\"font-size:18px;\">或</span><span style=\"font-size:18px;\">CDAP</span><span style=\"font-size:18px;\">为激活因子注射于小鼠，可使小鼠产生杀弧菌抗体。还有将</span><span style=\"font-size:18px;\">O139</span><span style=\"font-size:18px;\">荚膜脂多糖与白喉毒素共价结合可使小鼠产生针对荚膜多糖的</span><span style=\"font-size:18px;\">IgG</span><span style=\"font-size:18px;\">，具有杀弧菌作用，同时也可产生针对白喉毒素的抗体，此研究还处于动物实验阶段。</span> \r\n</p>', 'hsg', 'upload/1652536209521.jpg', 1, '', '2022-05-14 21:50:32');

-- ----------------------------
-- Table structure for yonghuxinxi
-- ----------------------------
DROP TABLE IF EXISTS `yonghuxinxi`;
CREATE TABLE `yonghuxinxi`  (
  `ID` int(0) NOT NULL AUTO_INCREMENT,
  `zhanghao` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `mima` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `xingming` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `xingbie` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `nianling` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `diqu` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `shoujihao` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `touxiang` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `addtime` timestamp(0) NOT NULL DEFAULT CURRENT_TIMESTAMP(0) ON UPDATE CURRENT_TIMESTAMP(0),
  PRIMARY KEY (`ID`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 19 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

